      id: number;
      userId: string;
      fullName: string;
      email: string;
      role: string;
      isVerified: boolean;
      profilePicture?: string;
    };
    lastActivity?: number;
    ipAddress?: string;
    userAgent?: string;
    mfaVerified?: boolean;
    mfaVerifiedAt?: number;
  }
}

// Extend the Request interface to support Passport.js-like methods
declare global {
  namespace Express {
    interface Request {
      isAuthenticated(): boolean;
      user?: {
        id: number;
        userId: string;
        fullName: string;
        email: string;
        role: string;
        isVerified: boolean;
        profilePicture?: string;
      };
    }
  }
}

// JWT Secret from environment
const JWT_SECRET = process.env.JWT_SECRET || process.env.JWT_SECRET_KEY || 'default-development-secret-key';
if (process.env.NODE_ENV === 'production' && (JWT_SECRET === 'default-development-secret-key' || !JWT_SECRET)) {
  throw new Error('JWT_SECRET must be set in environment variables for production');
}

// Session timeout (30 minutes)
const SESSION_TIMEOUT = 30 * 60 * 1000;

// Authentication middleware that adds Passport.js-like methods to req
export function setupAuth() {
  return (req: Request, res: Response, next: NextFunction) => {
    // Check for session timeout
    if (req.session?.lastActivity) {
      const timeSinceLastActivity = Date.now() - req.session.lastActivity;
      if (timeSinceLastActivity > SESSION_TIMEOUT) {
        req.session.destroy((err) => {
          if (err) console.error('Session destruction error:', err);
        });
        return res.status(401).json({ 
          success: false,
          message: "Session expired",
          code: "SESSION_EXPIRED"
        });
      }
    }

    // Update last activity
    if (req.session?.userId) {
      req.session.lastActivity = Date.now();

      // Verify IP and User Agent for security
      const currentIP = req.ip || req.connection.remoteAddress;
      const currentUA = req.headers['user-agent'];

      if (req.session.ipAddress && req.session.ipAddress !== currentIP) {
        console.warn(`IP address mismatch for user ${req.session.userId}: ${req.session.ipAddress} vs ${currentIP}`);
      }

      if (req.session.userAgent && req.session.userAgent !== currentUA) {
        console.warn(`User agent mismatch for user ${req.session.userId}`);
      }
    }

    // Add isAuthenticated method
    req.isAuthenticated = () => {
      return !!(req.session?.userId && req.session?.user);
    };

    // Add user property from session
    if (req.session?.user) {
      req.user = req.session.user;
    }

    next();
  };
}

// Token verification utility with proper error handling
export function verifyToken(token: string): Promise<any> {
  return new Promise((resolve, reject) => {
    jwt.verify(token, JWT_SECRET as string, (err, decoded) => {
      if (err) {
        if (err.name === 'TokenExpiredError') {
          reject(new Error('TOKEN_EXPIRED'));
        } else if (err.name === 'JsonWebTokenError') {
          reject(new Error('INVALID_TOKEN'));
        } else {
          reject(new Error('TOKEN_VERIFICATION_FAILED'));
        }
      } else {
        resolve(decoded);
      }
    });
  });
}

// Generate secure JWT token
export function generateToken(payload: object, expiresIn: string = '1h'): string {
  return jwt.sign(payload, JWT_SECRET as string, { 
    expiresIn: expiresIn,
    issuer: 'brillprime-api',
    audience: 'brillprime-app'
  } as jwt.SignOptions);
}

// Middleware to require authentication with enhanced security
export function requireAuth(req: Request, res: Response, next: NextFunction) {
  // Check for session-based auth first
  if (req.isAuthenticated()) {
    return next();
  }

  // Check for JWT token in headers
  const authHeader = req.headers.authorization;
  if (authHeader && authHeader.startsWith('Bearer ')) {
    const token = authHeader.substring(7);

    verifyToken(token)
      .then(async (decoded: any) => {
        // Verify user still exists and is active
        const user = await db
          .select()
          .from(users)
          .where(eq(users.id, decoded.userId))
          .limit(1);

        if (!user.length || !user[0].isVerified) {
          return res.status(401).json({ 
            success: false,
            message: "User not found or not verified",
            code: "USER_INVALID"
          });
        }

        // Set user in request  
        req.user = {
          id: user[0].id,
          userId: user[0].id.toString(), // Convert id to string for compatibility
          fullName: user[0].fullName,
          email: user[0].email,
          role: user[0].role || 'CONSUMER',
          isVerified: user[0].isVerified || false,
          profilePicture: user[0].profilePicture || undefined
        };

        next();
      })
      .catch((error) => {
        return res.status(401).json({ 
          success: false,
          message: "Invalid or expired token",
          code: error.message
        });
      });
  } else {
    return res.status(401).json({ 
      success: false,
      message: "Authentication required",
      code: "AUTH_REQUIRED"
    });
  }
}

// Middleware to require specific role with proper validation
export function requireRole(allowedRoles: string | string[]) {
  const roles = Array.isArray(allowedRoles) ? allowedRoles : [allowedRoles];

  return (req: Request, res: Response, next: NextFunction) => {
    if (!req.isAuthenticated() && !req.user) {
      return res.status(401).json({ 
        success: false,
        message: "Authentication required",
        code: "AUTH_REQUIRED"
      });
    }

    const userRole = req.user?.role;
    if (!userRole || !roles.includes(userRole)) {
      return res.status(403).json({ 
        success: false,
        message: `Access denied. Required role(s): ${roles.join(', ')}`,
        code: "INSUFFICIENT_PERMISSIONS"
      });
    }

    next();
  };
}

// Password hashing utilities
export async function hashPassword(password: string): Promise<string> {
  const saltRounds = 12;
  return bcrypt.hash(password, saltRounds);
}

export async function comparePassword(password: string, hash: string): Promise<boolean> {
  return bcrypt.compare(password, hash);
}

// Middleware to require verified user
export function requireVerified(req: Request, res: Response, next: NextFunction) {
  if (!req.user?.isVerified) {
    return res.status(403).json({
      success: false,
      message: "Account verification required",
      code: "VERIFICATION_REQUIRED"
    });
  }
  next();
}

// Aliases for consistency
export const auth = requireAuth;
export const authenticateToken = requireAuth;

server/middleware/cacheMiddleware.ts.content:
import { Request, Response, NextFunction } from 'express';
import { cacheService } from '../services/cache';

interface CacheOptions {
  ttl?: number;
  keyGenerator?: (req: Request) => string;
  skipCache?: (req: Request, res: Response) => boolean;
  varyByUser?: boolean;
}

export function cacheMiddleware(options: CacheOptions = {}) {
  const {
    ttl = 300, // 5 minutes default
    keyGenerator = (req) => `api:${req.method}:${req.originalUrl}`,
    skipCache = () => false,
    varyByUser = false
  } = options;

  return async (req: Request, res: Response, next: NextFunction) => {
    // Skip caching for non-GET requests or when skipCache returns true
    if (req.method !== 'GET' || skipCache(req, res)) {
      return next();
    }

    try {
      // Generate cache key
      let cacheKey = keyGenerator(req);
      if (varyByUser && req.session?.userId) {
        cacheKey = `${cacheKey}:user:${req.session.userId}`;
      }

      // Try to get cached response
      const cachedResponse = await cacheService.get(cacheKey);
      if (cachedResponse) {
        res.set('X-Cache', 'HIT');
        return res.json(cachedResponse);
      }

      // Override res.json to cache the response
      const originalJson = res.json.bind(res);
      res.json = function(data: any) {
        // Only cache successful responses
        if (res.statusCode >= 200 && res.statusCode < 300) {
          cacheService.set(cacheKey, data, ttl).catch(console.error);
        }
        res.set('X-Cache', 'MISS');
        return originalJson(data);
      };

      next();
    } catch (error) {
      console.error('Cache middleware error:', error);
      next();
    }
  };
}

// Specific cache configurations for different endpoints
export const dashboardCache = cacheMiddleware({
  ttl: 600, // 10 minutes
  keyGenerator: (req) => `dashboard:${req.originalUrl}`,
  varyByUser: true
});

export const productsCache = cacheMiddleware({
  ttl: 900, // 15 minutes
  keyGenerator: (req) => `products:${req.originalUrl}`,
  skipCache: (req) => req.query.live === 'true'
});

export const analyticsCache = cacheMiddleware({
  ttl: 1800, // 30 minutes
  keyGenerator: (req) => `analytics:${req.originalUrl}`,
  varyByUser: true
});

export const locationCache = cacheMiddleware({
  ttl: 300, // 5 minutes
  keyGenerator: (req) => `location:${req.originalUrl}`,
  skipCache: (req) => req.query.realtime === 'true'
});

// Cache invalidation helper
export function invalidateCache(patterns: string[]) {
  return (req: Request, res: Response, next: NextFunction) => {
    res.on('finish', () => {
      if (res.statusCode >= 200 && res.statusCode < 300) {
        patterns.forEach(pattern => {
          cacheService.invalidatePattern(pattern).catch(console.error);
        });
      }
    });
    next();
  };
}

server/middleware/loadBalancer.ts.content:
import { Request, Response, NextFunction } from 'express';
import os from 'os';

interface ServerStats {
  cpuUsage: number;
  memoryUsage: number;
  activeConnections: number;
  requestsPerSecond: number;
  uptime: number;
}

class LoadBalancerMiddleware {
  private requestCount: number = 0;
  private startTime: number = Date.now();
  private activeConnections: number = 0;
  private lastCpuUsage: NodeJS.CpuUsage = process.cpuUsage();
  private lastStatsTime: number = Date.now();

  constructor() {
    // Update stats every 10 seconds
    setInterval(() => {
      this.updateStats();
    }, 10000);
  }

  private updateStats() {
    this.lastCpuUsage = process.cpuUsage(this.lastCpuUsage);
    this.lastStatsTime = Date.now();
  }

  // Health check endpoint
  healthCheck = (req: Request, res: Response) => {
    const stats = this.getServerStats();
    const isHealthy = this.isServerHealthy(stats);

    res.status(isHealthy ? 200 : 503).json({
      status: isHealthy ? 'healthy' : 'unhealthy',
      timestamp: new Date().toISOString(),
      stats,
      version: process.env.npm_package_version || '1.0.0',
      nodeVersion: process.version,
      instanceId: process.env.INSTANCE_ID || os.hostname()
    });
  };

  // Get server statistics
  getServerStats(): ServerStats {
    const memUsage = process.memoryUsage();
    const uptime = process.uptime();
    const currentTime = Date.now();
    const elapsedSeconds = (currentTime - this.startTime) / 1000;

    // Calculate CPU usage percentage
    const cpuUsage = this.lastCpuUsage;
    const totalCpuTime = (cpuUsage.user + cpuUsage.system) / 1000; // Convert to milliseconds
    const cpuPercent = Math.min((totalCpuTime / (currentTime - this.lastStatsTime)) * 100, 100);

    return {
      cpuUsage: Math.round(cpuPercent * 100) / 100,
      memoryUsage: Math.round((memUsage.heapUsed / memUsage.heapTotal) * 100 * 100) / 100,
      activeConnections: this.activeConnections,
      requestsPerSecond: Math.round((this.requestCount / elapsedSeconds) * 100) / 100,
      uptime: Math.round(uptime)
    };
  }

  // Determine if server is healthy
  private isServerHealthy(stats: ServerStats): boolean {
    // More relaxed thresholds for development environment
    const isDev = process.env.NODE_ENV !== 'production';
    return (
      stats.cpuUsage < (isDev ? 95 : 90) && // CPU usage threshold
      stats.memoryUsage < (isDev ? 98 : 90) && // Memory usage threshold  
      stats.activeConnections < 1000 // Active connections under 1000
    );
  }

  // Request tracking middleware
  requestTracker = (req: Request, res: Response, next: NextFunction) => {
    this.requestCount++;
    this.activeConnections++;

    // Add request metadata
    req.requestStartTime = Date.now();
    req.requestId = `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    // Set request tracking headers
    res.setHeader('X-Request-ID', req.requestId);
    res.setHeader('X-Instance-ID', process.env.INSTANCE_ID || os.hostname());

    // Cleanup on response finish
    res.on('finish', () => {
      this.activeConnections--;
      const duration = Date.now() - req.requestStartTime!;

      // Log slow requests
      if (duration > 2000) {
        console.warn(`Slow request: ${req.method} ${req.url} took ${duration}ms`);
      }
    });

    next();
  };

  // Circuit breaker middleware
  circuitBreaker = (req: Request, res: Response, next: NextFunction) => {
    const stats = this.getServerStats();

    // If server is unhealthy, reject new requests
    if (!this.isServerHealthy(stats)) {
      return res.status(503).json({
        error: 'Service temporarily unavailable',
        message: 'Server is overloaded, please try again later',
        retryAfter: 30
      });
    }

    next();
  };

  // Rate limiting based on server load
  adaptiveRateLimit = (req: Request, res: Response, next: NextFunction) => {
    const stats = this.getServerStats();
    let maxRequestsPerMinute = 1000; // Base rate limit

    // Reduce rate limit based on server load
    if (stats.cpuUsage > 70) {
      maxRequestsPerMinute = 500;
    } else if (stats.cpuUsage > 50) {
      maxRequestsPerMinute = 750;
    }

    if (stats.memoryUsage > 70) {
      maxRequestsPerMinute = Math.min(maxRequestsPerMinute, 400);
    }

    // Store rate limit info (would typically use Redis for distributed systems)
    res.setHeader('X-RateLimit-Limit', maxRequestsPerMinute.toString());
    res.setHeader('X-RateLimit-Remaining', (maxRequestsPerMinute - (this.requestCount % maxRequestsPerMinute)).toString());

    next();
  };

  // Load balancer headers
  loadBalancerHeaders = (req: Request, res: Response, next: NextFunction) => {
    // Add load balancer information
    res.setHeader('X-Served-By', process.env.INSTANCE_ID || os.hostname());
    res.setHeader('X-Load-Balancer', 'BrillPrime-LB');

    // Add caching headers for load balancer
    if (req.method === 'GET' && !req.url.includes('/api/')) {
      res.setHeader('Cache-Control', 'public, max-age=300'); // 5 minutes
    }

    next();
  };

  // Graceful shutdown handling
  gracefulShutdown = () => {
    return new Promise<void>((resolve) => {
      console.log('Starting graceful shutdown...');

      // Stop accepting new connections
      const checkConnections = setInterval(() => {
        if (this.activeConnections === 0) {
          clearInterval(checkConnections);
          console.log('All connections closed, shutting down');
          resolve();
        } else {
          console.log(`Waiting for ${this.activeConnections} connections to close...`);
        }
      }, 1000);

      // Force shutdown after 30 seconds
      setTimeout(() => {
        clearInterval(checkConnections);
        console.log('Forced shutdown after 30 seconds');
        resolve();
      }, 30000);
    });
  };
}

// Singleton instance
export const loadBalancer = new LoadBalancerMiddleware();

// Express middleware exports
export const healthCheck = loadBalancer.healthCheck;
export const requestTracker = loadBalancer.requestTracker;
export const circuitBreaker = loadBalancer.circuitBreaker;
export const adaptiveRateLimit = loadBalancer.adaptiveRateLimit;
export const loadBalancerHeaders = loadBalancer.loadBalancerHeaders;
export const gracefulShutdown = loadBalancer.gracefulShutdown;

declare global {
  namespace Express {
    interface Request {
      requestStartTime?: number;
      requestId?: string;
    }
  }
}

server/middleware/pci-compliance.ts.content:
import { Request, Response, NextFunction } from 'express';
import crypto from 'crypto';
import { db } from '../db';
import { auditLogs, securityLogs } from '../../shared/schema';

// PCI DSS Requirement 1 & 2: Network Security and System Configuration
export const pciSecurityHeaders = (req: Request, res: Response, next: NextFunction) => {
  // Set security headers for PCI compliance
  res.setHeader('Strict-Transport-Security', 'max-age=31536000; includeSubDomains; preload');
  res.setHeader('X-Content-Type-Options', 'nosniff');
  res.setHeader('X-Frame-Options', 'DENY');
  res.setHeader('X-XSS-Protection', '1; mode=block');
  res.setHeader('Referrer-Policy', 'strict-origin-when-cross-origin');
  res.setHeader('Content-Security-Policy', "default-src 'self' 'unsafe-inline' 'unsafe-eval'; script-src 'self' 'unsafe-inline' 'unsafe-eval' https://cdn.tailwindcss.com; style-src 'self' 'unsafe-inline' https://cdn.tailwindcss.com; img-src 'self' data: blob: https:; connect-src 'self' wss: ws:;");

  next();
};

// PCI DSS Requirement 3: Protect Stored Cardholder Data
export const sanitizeCardData = (req: Request, res: Response, next: NextFunction) => {
  if (req.body) {
    // Remove any potential card data from request body
    const sensitiveFields = ['cardNumber', 'cvv', 'cvc', 'expiryDate', 'pin'];

    function sanitizeObject(obj: any): any {
      if (typeof obj !== 'object' || obj === null) return obj;

      const sanitized = Array.isArray(obj) ? [] : {};

      for (const [key, value] of Object.entries(obj)) {
        const lowerKey = key.toLowerCase();

        if (sensitiveFields.some(field => lowerKey.includes(field.toLowerCase()))) {
          // Log attempt to send sensitive data
          console.warn(`Attempt to send sensitive card data field: ${key} from IP: ${req.ip}`);

          // Replace with masked value
          if (typeof value === 'string' && value.length > 4) {
            (sanitized as any)[key] = '*'.repeat(value.length - 4) + value.slice(-4);
          } else {
            (sanitized as any)[key] = '***REDACTED***';
          }
        } else {
          (sanitized as any)[key] = typeof value === 'object' ? sanitizeObject(value) : value;
        }
      }

      return sanitized;
    }

    req.body = sanitizeObject(req.body);
  }

  next();
};

// PCI DSS Requirement 4: Encrypt Transmission of Cardholder Data
export const enforceHttps = (req: Request, res: Response, next: NextFunction) => {
  if (process.env.NODE_ENV === 'production') {
    const isHttps = req.headers['x-forwarded-proto'] === 'https' || req.secure;

    if (!isHttps) {
      return res.status(400).json({
        success: false,
        message: 'HTTPS required for payment operations',
        code: 'HTTPS_REQUIRED'
      });
    }
  }

  next();
};

// PCI DSS Requirement 8: Identify and Authenticate Access
export const enhancedPaymentAuth = async (req: Request, res: Response, next: NextFunction) => {
  try {
    if (!req.session?.userId) {
      return res.status(401).json({
        success: false,
        message: 'Authentication required for payment operations'
      });
    }

    const userId = req.session.userId;

    // Check for recent authentication
    const recentAuth = req.session.lastAuthTime;
    const fiveMinutesAgo = Date.now() - (5 * 60 * 1000);

    if (!recentAuth || recentAuth < fiveMinutesAgo) {
      return res.status(401).json({
        success: false,
        message: 'Recent authentication required for payment operations',
        code: 'REAUTH_REQUIRED'
      });
    }

    // Log payment access attempt
    await db.insert(securityLogs).values({
      userId,
      action: 'PAYMENT_ACCESS',
      details: JSON.stringify({
        endpoint: req.path,
        method: req.method,
        ipAddress: req.ip,
        userAgent: req.get('User-Agent')
      }),
      ipAddress: req.ip,
      userAgent: req.get('User-Agent') || '',
      severity: 'INFO'
    });

    next();

  } catch (error) {
    console.error('Payment authentication error:', error);
    res.status(500).json({
      success: false,
      message: 'Authentication verification failed'
    });
  }
};

// PCI DSS Requirement 10: Log and Monitor All Access
export const pciAuditLogger = async (req: Request, res: Response, next: NextFunction) => {
  const startTime = Date.now();

  // Log request
  const requestId = crypto.randomUUID();
  const logData = {
    requestId,
    userId: req.session?.userId || null,
    method: req.method,
    path: req.path,
    ipAddress: req.ip,
    userAgent: req.get('User-Agent') || '',
    timestamp: new Date()
  };

  // Capture response
  const originalSend = res.send;
  res.send = function(data) {
    const duration = Date.now() - startTime;

    // Log response for payment-related endpoints
    if (req.path.includes('/payment') || req.path.includes('/transaction')) {
      db.insert(auditLogs).values({
        userId: req.session?.userId || null,
        action: `${req.method}_${req.path}`,
        resource: 'PAYMENT_ENDPOINT',
        resourceId: requestId,
        newValues: JSON.stringify({
          ...logData,
          statusCode: res.statusCode,
          duration,
          success: res.statusCode < 400
        }),
        ipAddress: req.ip,
        userAgent: req.get('User-Agent') || '',
        success: res.statusCode < 400
      }).catch(err => console.error('Audit log error:', err));
    }

    return originalSend.call(this, data);
  };

  next();
};

// PCI DSS Requirement 6: Develop and Maintain Secure Systems
export const validatePaymentEndpoint = (req: Request, res: Response, next: NextFunction) => {
  // Validate critical payment parameters
  const criticalEndpoints = ['/api/payments/', '/api/transactions/', '/api/wallet/'];

  if (criticalEndpoints.some(endpoint => req.path.startsWith(endpoint))) {
    // Check for required security headers
    const requiredHeaders = ['user-agent', 'referer'];
    const missingHeaders = requiredHeaders.filter(header => !req.headers[header]);

    if (missingHeaders.length > 0) {
      return res.status(400).json({
        success: false,
        message: 'Required security headers missing',
        code: 'INVALID_REQUEST'
      });
    }

    // Validate request size
    const contentLength = parseInt(req.headers['content-length'] || '0');
    if (contentLength > 1024 * 1024) { // 1MB limit
      return res.status(413).json({
        success: false,
        message: 'Request too large',
        code: 'REQUEST_TOO_LARGE'
      });
    }
  }

  next();
};

// Token validation for payment operations
export const validatePaymentToken = (req: Request, res: Response, next: NextFunction) => {
  const paymentToken = req.headers['x-payment-token'] as string;

  if (!paymentToken) {
    return res.status(400).json({
      success: false,
      message: 'Payment security token required',
      code: 'PAYMENT_TOKEN_MISSING'
    });
  }

  // Validate token format and expiry
  try {
    const tokenData = JSON.parse(Buffer.from(paymentToken, 'base64').toString());
    const tokenAge = Date.now() - tokenData.timestamp;

    if (tokenAge > 300000) { // 5 minutes
      return res.status(400).json({
        success: false,
        message: 'Payment token expired',
        code: 'PAYMENT_TOKEN_EXPIRED'
      });
    }

    req.paymentTokenData = tokenData;
    next();

  } catch (error) {
    return res.status(400).json({
      success: false,
      message: 'Invalid payment token',
      code: 'PAYMENT_TOKEN_INVALID'
    });
  }
};

// Extend Request interface for payment token data
declare global {
  namespace Express {
    interface Request {
      paymentTokenData?: any;
    }
  }
}

server/middleware/rateLimiter.ts.content:
import rateLimit from 'express-rate-limit';
import { Request, Response } from 'express';
import { Redis } from 'ioredis';

// Redis configuration for rate limiting
const REDIS_URL = "redis://default:ob0XzfYSqIWm028JdW7JkBY8VWkhQp7A@redis-13241.c245.us-east-1-3.ec2.redns.redis-cloud.com:13241";
let redis: Redis | null = null;

if (!process.env.REDIS_DISABLED) {
  try {
    redis = new Redis(REDIS_URL, {
      maxRetriesPerRequest: 3,
      lazyConnect: true,
    });
    console.log('Rate limiter connected to Redis Cloud');
  } catch (error) {
    console.log('Rate limiter using memory store (Redis connection failed)');
    redis = null;
  }
} else {
  console.log('Rate limiter using memory store (Redis disabled)');
}

// Custom rate limiter store using Redis or Memory
class RateLimitStore {
  private memoryStore = new Map<string, { count: number; resetTime: number }>();

  constructor(private redis: Redis | null, private prefix: string = 'rl:') {}

  async incr(key: string): Promise<{ totalHits: number; resetTime?: Date }> {
    const fullKey = `${this.prefix}${key}`;

    if (this.redis) {
      // Use Redis if available
      const current = await this.redis.incr(fullKey);

      if (current === 1) {
        await this.redis.expire(fullKey, 60);
      }

      const ttl = await this.redis.ttl(fullKey);
      const resetTime = new Date(Date.now() + ttl * 1000);

      return { totalHits: current, resetTime };
    } else {
      // Use memory store fallback
      const now = Date.now();
      const resetTime = now + 60000; // 60 seconds
      const existing = this.memoryStore.get(fullKey);

      if (!existing || existing.resetTime < now) {
        this.memoryStore.set(fullKey, { count: 1, resetTime });
        return { totalHits: 1, resetTime: new Date(resetTime) };
      } else {
        existing.count++;
        this.memoryStore.set(fullKey, existing);
        return { totalHits: existing.count, resetTime: new Date(existing.resetTime) };
      }
    }
  }

  async decrement(key: string): Promise<void> {
    const fullKey = `${this.prefix}${key}`;

    if (this.redis) {
      await this.redis.decr(fullKey);
    } else {
      const existing = this.memoryStore.get(fullKey);
      if (existing && existing.count > 0) {
        existing.count--;
        this.memoryStore.set(fullKey, existing);
      }
    }
  }

  async resetKey(key: string): Promise<void> {
    const fullKey = `${this.prefix}${key}`;

    if (this.redis) {
      await this.redis.del(fullKey);
    } else {
      this.memoryStore.delete(fullKey);
    }
  }
}

const store = new RateLimitStore(redis);

// Role-based rate limits
const RATE_LIMITS = {
  ADMIN: { windowMs: 60 * 1000, max: 1000 }, // 1000 requests per minute
  MERCHANT: { windowMs: 60 * 1000, max: 200 }, // 200 requests per minute
  DRIVER: { windowMs: 60 * 1000, max: 300 }, // 300 requests per minute
  CONSUMER: { windowMs: 60 * 1000, max: 100 }, // 100 requests per minute
  GUEST: { windowMs: 60 * 1000, max: 20 } // 20 requests per minute for unauthenticated
};

// API-specific rate limits
const API_LIMITS = {
  '/api/auth/login': { windowMs: 15 * 60 * 1000, max: 5 }, // 5 login attempts per 15 minutes
  '/api/auth/register': { windowMs: 60 * 60 * 1000, max: 3 }, // 3 registrations per hour
  '/api/payments': { windowMs: 60 * 1000, max: 10 }, // 10 payment requests per minute
  '/api/wallet/fund': { windowMs: 60 * 1000, max: 5 }, // 5 funding attempts per minute
  '/api/verification': { windowMs: 60 * 60 * 1000, max: 5 } // 5 verification attempts per hour
};

// Create dynamic rate limiter based on user role and endpoint
export const createRateLimiter = (options: {
  keyGenerator?: (req: Request) => string;
  skipFailedRequests?: boolean;
  skipSuccessfulRequests?: boolean;
  onLimitReached?: (req: Request, res: Response) => void;
} = {}) => {
  return async (req: Request, res: Response, next: Function) => {
    try {
      // Determine user role
      const userRole = req.session?.user?.role || 'GUEST';
      const userId = req.session?.user?.id;

      // Get rate limit for user role
      let rateLimit = RATE_LIMITS[userRole as keyof typeof RATE_LIMITS] || RATE_LIMITS.GUEST;

      // Check for API-specific limits
      const endpoint = req.path;
      for (const [pattern, limit] of Object.entries(API_LIMITS)) {
        if (endpoint.startsWith(pattern)) {
          rateLimit = limit;
          break;
        }
      }

      // Generate key for rate limiting
      const key = options.keyGenerator 
        ? options.keyGenerator(req)
        : `${userId || req.ip}:${userRole}:${endpoint}`;

      // Check rate limit
      const result = await store.incr(key);

      // Add rate limit headers
      res.set({
        'X-RateLimit-Limit': rateLimit.max.toString(),
        'X-RateLimit-Remaining': Math.max(0, rateLimit.max - result.totalHits).toString(),
        'X-RateLimit-Reset': result.resetTime?.toISOString() || ''
      });

      if (result.totalHits > rateLimit.max) {
        // Rate limit exceeded
        if (options.onLimitReached) {
          options.onLimitReached(req, res);
        }

        // Log rate limit violation
        console.warn(`Rate limit exceeded for ${userRole} user ${userId || 'anonymous'} on ${endpoint}`);

        return res.status(429).json({
          success: false,
          message: 'Too many requests. Please try again later.',
          code: 'RATE_LIMIT_EXCEEDED',
          retryAfter: result.resetTime
        });
      }

      next();
    } catch (error) {
      console.error('Rate limiting error:', error);
      // Don't block requests if rate limiter fails
      next();
    }
  };
};

// Specific rate limiters
export const authLimiter = createRateLimiter({
  keyGenerator: (req) => `auth:${req.ip}`,
  onLimitReached: (req, res) => {
    console.warn(`Authentication rate limit exceeded from IP: ${req.ip}`);
  }
});

export const paymentLimiter = createRateLimiter({
  keyGenerator: (req) => `payment:${req.session?.user?.id || req.ip}`,
  skipSuccessfulRequests: true
});

export const generalLimiter = createRateLimiter();

// Cleanup function
export const cleanupRateLimitStore = async () => {
  const keys = await redis.keys('rl:*');
  if (keys.length > 0) {
    await redis.del(...keys);
  }
};

