import express from 'express';
import { db } from '../db';
import { transactions, users, orders, wallets } from '../../shared/schema';
import { eq, desc, and, gte, lte } from 'drizzle-orm';
import { requireAuth } from '../middleware/auth';

const router = express.Router();

// Process payment transaction
router.post('/process', requireAuth, async (req, res) => {
  try {
    const { orderId, amount, paymentMethod, currency = 'NGN' } = req.body;
    const userId = req.user.id;

    // Validate order exists and belongs to user
    const order = await db.select().from(orders)
      .where(and(eq(orders.id, orderId), eq(orders.customerId, userId)))
      .limit(1);

    if (order.length === 0) {
      return res.status(404).json({ success: false, message: 'Order not found' });
    }

    // Create transaction record
    const transaction = await db.insert(transactions).values({
      orderId,
      userId,
      amount: amount.toString(),
      currency,
      paymentMethod,
      paymentStatus: 'PENDING',
      transactionRef: `TX_${Date.now()}_${userId}`,
      metadata: {
        orderId,
        paymentMethod,
        initiatedAt: new Date().toISOString()
      }
    }).returning();

    // Update order payment status
    await db.update(orders).set({
      paymentStatus: 'PENDING'
    }).where(eq(orders.id, orderId));

    res.json({
      success: true,
      data: {
        transaction: transaction[0],
        paymentUrl: `${process.env.BASE_URL}/payment/confirm/${transaction[0].id}`
      }
    });
  } catch (error) {
    console.error('Transaction processing error:', error);
    res.status(500).json({ success: false, message: 'Failed to process transaction' });
  }
});

// Confirm payment
router.post('/confirm/:transactionId', requireAuth, async (req, res) => {
  try {
    const { transactionId } = req.params;
    const { gatewayResponse } = req.body;

    const transaction = await db.select().from(transactions)
      .where(eq(transactions.id, parseInt(transactionId)))
      .limit(1);

    if (transaction.length === 0) {
      return res.status(404).json({ success: false, message: 'Transaction not found' });
    }

    // Update transaction status
    await db.update(transactions).set({
      paymentStatus: 'COMPLETED',
      paymentGatewayRef: gatewayResponse?.reference,
      metadata: {
        ...transaction[0].metadata as any,
        completedAt: new Date().toISOString(),
        gatewayResponse
      }
    }).where(eq(transactions.id, parseInt(transactionId)));

    // Update order status
    if (transaction[0].orderId) {
      await db.update(orders).set({
        paymentStatus: 'COMPLETED',
        status: 'CONFIRMED'
      }).where(eq(orders.id, transaction[0].orderId));
    }

    // Update wallet balance if applicable
    if (transaction[0].userId) {
      const wallet = await db.select().from(wallets)
        .where(eq(wallets.userId, transaction[0].userId))
        .limit(1);

      if (wallet.length > 0) {
        await db.update(wallets).set({
          balance: (parseFloat(wallet[0].balance) + parseFloat(transaction[0].amount)).toString()
        }).where(eq(wallets.userId, transaction[0].userId));
      }
    }

    res.json({
      success: true,
      data: { transactionId, status: 'COMPLETED' }
    });
  } catch (error) {
    console.error('Payment confirmation error:', error);
    res.status(500).json({ success: false, message: 'Failed to confirm payment' });
  }
});

// Get transaction history
router.get('/history', requireAuth, async (req, res) => {
  try {
    const userId = req.user.id;
    const { page = 1, limit = 20, status, startDate, endDate } = req.query;
    const offset = (parseInt(page as string) - 1) * parseInt(limit as string);

    let whereConditions = [eq(transactions.userId, userId)];

    if (status) {
      whereConditions.push(eq(transactions.paymentStatus, status as any));
    }

    if (startDate) {
      whereConditions.push(gte(transactions.createdAt, new Date(startDate as string)));
    }

    if (endDate) {
      whereConditions.push(lte(transactions.createdAt, new Date(endDate as string)));
    }

    const userTransactions = await db.select({
      id: transactions.id,
      amount: transactions.amount,
      currency: transactions.currency,
      paymentMethod: transactions.paymentMethod,
      paymentStatus: transactions.paymentStatus,
      transactionRef: transactions.transactionRef,
      createdAt: transactions.createdAt,
      order: {
        orderNumber: orders.orderNumber,
        orderType: orders.orderType
      }
    })
    .from(transactions)
    .leftJoin(orders, eq(transactions.orderId, orders.id))
    .where(and(...whereConditions))
    .orderBy(desc(transactions.createdAt))
    .limit(parseInt(limit as string))
    .offset(offset);

    res.json({
      success: true,
      data: {
        transactions: userTransactions,
        pagination: {
          page: parseInt(page as string),
          limit: parseInt(limit as string),
          total: userTransactions.length
        }
      }
    });
  } catch (error) {
    console.error('Transaction history error:', error);
    res.status(500).json({ success: false, message: 'Failed to get transaction history' });
  }
});

export default router;

server/routes/simple-verification.ts.content:
import { Request, Response } from "express";

import { db } from "../db";
import { identityVerifications, users } from "../../shared/schema";
import { eq } from "drizzle-orm";
import { nanoid } from "nanoid";

export const submitIdentityVerification = async (req: Request, res: Response) => {
  try {
    const { userId, role, documentType, documentUrl, additionalData } = req.body;

    if (!userId || !role) {
      return res.status(400).json({
        status: 'Error',
        message: 'User ID and role are required'
      });
    }

    // Verify user exists
    const user = await db.select()
      .from(users)
      .where(eq(users.id, parseInt(userId)))
      .limit(1);

    if (user.length === 0) {
      return res.status(404).json({
        status: 'Error',
        message: 'User not found'
      });
    }

    // Create verification record
    const verificationId = nanoid();
    const verificationData = {
      userId: parseInt(userId),
      verificationType: role === 'DRIVER' ? 'DRIVER_LICENSE' : 'IDENTITY_CARD',
      documentUrl: documentUrl || null,
      status: 'PENDING',
      submittedAt: new Date(),
      additionalData: additionalData ? JSON.stringify(additionalData) : null
    };

    await db.insert(identityVerifications).values(verificationData);

    console.log(`Identity verification submitted for user ${userId} with role ${role}`);

    res.json({
      status: 'Success',
      message: 'Identity verification submitted successfully',
      data: { verificationId }
    });

  } catch (error) {
    console.error('Identity verification error:', error);
    res.status(500).json({
      status: 'Error',
      message: 'Failed to submit identity verification'
    });
  }
};

server/routes/social-auth.ts.content:
import express from 'express';
import { z } from 'zod';
import { db } from '../db';
import { users } from '../../shared/schema';
import { eq } from 'drizzle-orm';
import { sanitizeInput } from '../middleware/validation';

const router = express.Router();

const socialLoginSchema = z.object({
  provider: z.enum(['google', 'apple', 'facebook']),
  profile: z.object({
    id: z.string(),
    email: z.string().email(),
    name: z.string(),
    avatar: z.string().optional()
  }).optional()
});

// Social login endpoint
router.post('/social-login', 
  sanitizeInput(),
  async (req, res) => {
    try {
      const { provider, profile } = socialLoginSchema.parse(req.body);

      // For development, return mock data if no profile provided
      if (!profile) {
        const mockProfiles = {
          google: {
            id: 'google_dev_123',
            email: 'user@gmail.com',
            name: 'Google Dev User',
            avatar: 'https://via.placeholder.com/100'
          },
          apple: {
            id: 'apple_dev_123',
            email: 'user@privaterelay.appleid.com',
            name: 'Apple Dev User'
          },
          facebook: {
            id: 'facebook_dev_123',
            email: 'user@facebook.com',
            name: 'Facebook Dev User',
            avatar: 'https://via.placeholder.com/100'
          }
        };

        return res.json({
          success: true,
          profile: {
            ...mockProfiles[provider],
            provider
          },
          message: 'Development mode - mock authentication'
        });
      }

      // Check if user exists with this social ID
      const [existingUser] = await db
        .select()
        .from(users)
        .where(eq(users.email, profile.email))
        .limit(1);

      let user = existingUser;

      if (!user) {
        // Create new user from social profile
        const [newUser] = await db
          .insert(users)
          .values({
            email: profile.email,
            fullName: profile.name,
            passwordHash: '', // No password for social users
            role: 'CONSUMER',
            isVerified: true, // Social accounts are pre-verified
            socialProvider: provider,
            socialId: profile.id,
            avatar: profile.avatar,
            createdAt: new Date()
          })
          .returning();

        user = newUser;
      } else if (!user.socialProvider) {
        // Link social account to existing user
        await db
          .update(users)
          .set({
            socialProvider: provider,
            socialId: profile.id,
            avatar: profile.avatar || user.avatar
          })
          .where(eq(users.id, user.id));
      }

      // Create session
      req.session.userId = user.id;
      req.session.user = {
        id: user.id,
        email: user.email,
        fullName: user.fullName,
        role: user.role
      };

      res.json({
        success: true,
        profile: {
          ...profile,
          provider
        },
        user: {
          id: user.id,
          email: user.email,
          fullName: user.fullName,
          role: user.role,
          avatar: user.avatar
        }
      });

    } catch (error) {
      console.error('Social auth error:', error);
      res.status(500).json({
        success: false,
        message: 'Social authentication failed'
      });
    }
  }
);

export default router;

