      const userId = req.session!.userId!;

      let chatRoom;

      switch (type) {
        case 'CUSTOMER_DRIVER':
          chatRoom = await liveChatService.createCustomerDriverChat(
            orderId || "TEST_ORDER_123",
            userId,
            recipientId || 999
