          break;
        case 'CUSTOMER_MERCHANT':
          chatRoom = await liveChatService.createCustomerMerchantChat(
            userId,
            recipientId || 888,
            orderId
          );
          break;
        case 'SUPPORT':
          chatRoom = await liveChatService.createSupportChat(
            userId,
            "TEST_ISSUE",
            orderId
          );
          break;
        default:
