import { Router } from "express";
import { z } from "zod";
import { db } from "../db";
import { users, transactions, wallets } from "../../shared/schema";
import { eq, and } from "drizzle-orm";
import { transactionService } from "../services/transaction";
import { sanitizeInput, validateSchema } from "../middleware/validation";

const router = Router();

const tollPaymentSchema = z.object({
  tollGateId: z.string(),
  vehicleType: z.enum(['motorcycle', 'car', 'suv', 'truck']),
  amount: z.number().positive(),
  paymentMethod: z.enum(['wallet', 'card']).default('wallet'),
  latitude: z.number().optional(),
  longitude: z.number().optional()
});

const verifyQRSchema = z.object({
  qrCode: z.string().min(10).max(100).refine(
    (code) => code.startsWith('TOLL_'),
    { message: 'QR code must be a valid toll code' }
  )
});

// Get toll gates from database
async function getTollGateById(id: string) {
  try {
    // Query from database - you should create a toll_gates table
    const tollGates = await db.execute(`
      SELECT * FROM toll_gates WHERE id = ?
    `, [id]);

    return tollGates[0] || null;
  } catch (error) {
    console.error('Error fetching toll gate:', error);
    return null;
  }
}

async function getAllTollGates() {
  try {
    const tollGates = await db.execute(`
      SELECT * FROM toll_gates WHERE is_active = true
    `);

    return tollGates;
  } catch (error) {
    console.error('Error fetching toll gates:', error);
    return [];
  }
}

// Process toll payment
router.post("/payment", sanitizeInput(), validateSchema(tollPaymentSchema), async (req: any, res: any) => {
  try {
    const userId = req.session?.userId;
    if (!userId) {
      return res.status(401).json({ success: false, error: 'User not authenticated' });
    }

    const validatedData = req.body;

    // Check if toll gate exists in database
    const tollGateInfo = await getTollGateById(validatedData.tollGateId);
    if (!tollGateInfo) {
      return res.status(400).json({ success: false, error: 'Invalid toll gate' });
    }

    // Check wallet balance if using wallet payment
    if (validatedData.paymentMethod === 'wallet') {
      const wallet = await db
        .select()
        .from(wallets)
        .where(eq(wallets.userId, userId))
        .limit(1);

      if (!wallet.length || parseFloat(wallet[0].balance) < validatedData.amount) {
        return res.status(400).json({ 
          success: false, 
          error: 'Insufficient wallet balance' 
        });
      }

      // Deduct from wallet
      await transactionService.updateWalletBalance(userId, validatedData.amount, 'subtract');
    }

    // Create transaction record
    const transaction = await db.insert(transactions).values({
      userId,
      type: 'TOLL_PAYMENT',
      status: 'SUCCESS',
      amount: validatedData.amount.toString(),
      fee: "0.00",
      netAmount: validatedData.amount.toString(),
      description: `Toll payment at ${tollGateInfo.name}`,
      metadata: {
        tollGateId: validatedData.tollGateId,
        vehicleType: validatedData.vehicleType,
        tollGateName: tollGateInfo.name,
        location: tollGateInfo.location,
        paymentMethod: validatedData.paymentMethod
      },
      completedAt: new Date()
    }).returning();

    // Generate QR code for toll gate scanning
    const qrCode = `TOLL_${validatedData.tollGateId}_${transaction[0].id}_${Date.now()}`;

    // Real-time notifications
    if (global.io) {
      // Notify user
      global.io.to(`user_${userId}`).emit('toll_payment_success', {
        type: 'TOLL_PAYMENT_SUCCESS',
        transaction: transaction[0],
        tollGate: tollGateInfo,
        qrCode,
        message: `Toll payment successful at ${tollGateInfo.name}`,
        timestamp: Date.now()
      });

      // Update wallet balance in real-time
      const updatedWallet = await db
        .select()
        .from(wallets)
        .where(eq(wallets.userId, userId))
        .limit(1);

      global.io.to(`user_${userId}`).emit('wallet_balance_update', {
        balance: updatedWallet[0]?.balance || '0.00',
        currency: updatedWallet[0]?.currency || 'NGN',
        lastTransaction: transaction[0],
        timestamp: Date.now()
      });

      // Notify admin monitoring
      global.io.to('admin_monitoring').emit('toll_payment_activity', {
        type: 'TOLL_PAYMENT_PROCESSED',
        userId,
        tollGateId: validatedData.tollGateId,
        amount: validatedData.amount,
        vehicleType: validatedData.vehicleType,
        timestamp: Date.now()
      });
    }

    res.json({
      success: true,
      transaction: transaction[0],
      qrCode,
      tollGate: tollGateInfo,
      message: 'Toll payment processed successfully'
    });

  } catch (error: any) {
    console.error('Error processing toll payment:', error);
    if (error.name === 'ZodError') {
      return res.status(400).json({ 
        success: false, 
        error: 'Invalid request data', 
        details: error.errors 
      });
    }
    res.status(500).json({ success: false, error: 'Failed to process toll payment' });
  }
});

// Get toll payment history
router.get("/payments", async (req: any, res: any) => {
  try {
    const userId = req.session?.userId;
    if (!userId) {
      return res.status(401).json({ success: false, error: 'User not authenticated' });
    }

    const { page = 1, limit = 20 } = req.query;
    const offset = (parseInt(page) - 1) * parseInt(limit);

    const tollPayments = await db
      .select()
      .from(transactions)
      .where(and(
        eq(transactions.userId, userId),
        eq(transactions.type, 'TOLL_PAYMENT')
      ))
      .orderBy(transactions.createdAt)
      .limit(parseInt(limit))
      .offset(offset);

    res.json({
      success: true,
      payments: tollPayments,
      pagination: {
        page: parseInt(page),
        limit: parseInt(limit),
        total: tollPayments.length
      }
    });

  } catch (error) {
    console.error('Error fetching toll payments:', error);
    res.status(500).json({ success: false, error: 'Failed to fetch toll payments' });
  }
});

// Get toll gates
router.get("/gates", async (req: any, res: any) => {
  try {
    const { latitude, longitude, radius = 50 } = req.query;

    // Get toll gates from database
    const gates = await getAllTollGates();

    // Calculate distance if location provided
    const gatesWithDistance = gates.map(gate => {
      let distance = null;
      if (latitude && longitude && gate.latitude && gate.longitude) {
        const R = 6371; // Earth's radius in km
        const dLat = (gate.latitude - latitude) * Math.PI / 180;
        const dLon = (gate.longitude - longitude) * Math.PI / 180;
        const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
                  Math.cos(latitude * Math.PI / 180) * Math.cos(gate.latitude * Math.PI / 180) *
                  Math.sin(dLon/2) * Math.sin(dLon/2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
        distance = R * c;
      }

      return {
        ...gate,
        distance,
        paymentMethods: ['wallet', 'card', 'qr']
      };
    });

    // Filter by radius if location provided
    const filteredGates = latitude && longitude && radius
      ? gatesWithDistance.filter(gate => gate.distance === null || gate.distance <= radius)
      : gatesWithDistance;

    res.json({
      success: true,
      gates: filteredGates,
      metadata: {
        total: filteredGates.length,
        searchRadius: radius,
        userLocation: latitude && longitude ? { latitude, longitude } : null
      }
    });

  } catch (error) {
    console.error('Error fetching toll gates:', error);
    res.status(500).json({ success: false, error: 'Failed to fetch toll gates' });
  }
});

// Verify toll QR code
router.post("/verify-qr", 
  sanitizeInput(),
  validateSchema(verifyQRSchema),
  async (req: any, res: any) => {
    try {
      const { qrCode } = req.body;

      // Parse QR code to extract transaction info
      const qrParts = qrCode.split('_');
      if (qrParts.length < 4) {
        return res.status(400).json({ success: false, error: 'Invalid QR code format' });
      }

      const [, tollGateId, transactionId] = qrParts;

      // Verify transaction exists and is valid
      const transaction = await db
        .select()
        .from(transactions)
        .where(and(
          eq(transactions.id, parseInt(transactionId)),
          eq(transactions.type, 'TOLL_PAYMENT'),
          eq(transactions.status, 'SUCCESS')
        ))
        .limit(1);

      if (!transaction.length) {
        return res.status(404).json({ success: false, error: 'Transaction not found' });
      }

      const tollGateInfo = tollGatesData[tollGateId as keyof typeof tollGatesData];

      res.json({
        success: true,
        transaction: transaction[0],
        tollGate: tollGateInfo,
        isValid: true,
        message: 'QR code verified successfully'
      });

    } catch (error) {
      console.error('Error verifying QR code:', error);
      res.status(500).json({ success: false, error: 'Failed to verify QR code' });
    }
  }
);

export default router;

server/routes/vendor-feed.ts.content:
import type { Express } from "express";
import { storage } from "../storage";
import { requireAuth } from "../middleware/auth";
import { z } from "zod";
import { insertVendorPostSchema, vendorPosts, vendorPostLikes, vendorPostComments } from "../../shared/schema";

// Validation schemas
const createPostSchema = insertVendorPostSchema.extend({
  title: z.string().min(1, "Title is required").max(200, "Title too long"),
  content: z.string().min(1, "Content is required").max(2000, "Content too long"),
  postType: z.enum(["PRODUCT_UPDATE", "NEW_PRODUCT", "PROMOTION", "ANNOUNCEMENT", "RESTOCK"]),
  productId: z.string().uuid().optional(),
  images: z.array(z.string().url()).max(5, "Maximum 5 images allowed").optional(),
  tags: z.array(z.string()).max(10, "Maximum 10 tags allowed").optional(),
  originalPrice: z.number().positive().optional(),
  discountPrice: z.number().positive().optional(),
  discountPercentage: z.number().min(0).max(100).optional(),
  validUntil: z.string().datetime().optional()
});

const updatePostSchema = createPostSchema.partial().extend({
  id: z.string().uuid()
});

const likePostSchema = z.object({
  postId: z.string().uuid()
});

const commentSchema = z.object({
  postId: z.string().uuid(),
  content: z.string().min(1, "Comment cannot be empty").max(500, "Comment too long"),
  parentCommentId: z.number().optional()
});

export function registerVendorFeedRoutes(app: Express) {
  // Create a new vendor post
  app.post("/api/vendor/posts", requireAuth, async (req, res) => {
    try {
      const userId = req.session!.userId!;
      const user = await storage.getUserById(userId);

      if (!user || user.role !== 'MERCHANT') {
        return res.status(403).json({
          success: false,
          message: "Only merchants can create posts"
        });
      }

      const validatedData = createPostSchema.parse(req.body);

      // Validate pricing logic
      if (validatedData.originalPrice && validatedData.discountPrice) {
        if (validatedData.discountPrice >= validatedData.originalPrice) {
          return res.status(400).json({
            success: false,
            message: "Discount price must be less than original price"
          });
        }
      }

      // Calculate discount percentage if not provided
      if (validatedData.originalPrice && validatedData.discountPrice && !validatedData.discountPercentage) {
        validatedData.discountPercentage = Math.round(
          ((validatedData.originalPrice - validatedData.discountPrice) / validatedData.originalPrice) * 100
        );
      }

      const post = await storage.createVendorPost({
        ...validatedData,
        vendorId: userId,
        originalPrice: validatedData.originalPrice?.toString(),
        discountPrice: validatedData.discountPrice?.toString(),
        validUntil: validatedData.validUntil ? new Date(validatedData.validUntil) : undefined
      });

      // Real-time notification to followers
      if ((global as any).io) {
        (global as any).io.emit('new_vendor_post', {
          type: 'NEW_POST',
          vendorId: userId,
          postId: post.id,
          postType: post.postType,
          title: post.title,
          timestamp: Date.now()
        });
      }

      res.status(201).json({
        success: true,
        message: "Post created successfully",
        post
      });
    } catch (error: any) {
      console.error("Create post error:", error);
      if (error.name === 'ZodError') {
        return res.status(400).json({
          success: false,
          message: "Invalid post data",
          errors: error.errors
        });
      }
      res.status(500).json({
        success: false,
        message: "Failed to create post"
      });
    }
  });

  // Get vendor posts (with pagination and filtering)
  app.get("/api/vendor/posts", async (req, res) => {
    try {
      const {
        vendorId,
        postType,
        page = '1',
        limit = '20',
        sortBy = 'recent'
      } = req.query;

      const posts = await storage.getVendorPosts({
        vendorId: vendorId ? parseInt(vendorId as string) : undefined,
        postType: postType as string,
        page: parseInt(page as string),
        limit: parseInt(limit as string),
        sortBy: sortBy as string
      });

      res.json({
        success: true,
        posts: posts.posts,
        pagination: posts.pagination
      });
    } catch (error) {
      console.error("Get vendor posts error:", error);
      res.status(500).json({
        success: false,
        message: "Failed to fetch posts"
      });
    }
  });

  // Get single vendor post with details
  app.get("/api/vendor/posts/:postId", async (req, res) => {
    try {
      const { postId } = req.params;
      const post = await storage.getVendorPostById(postId);

      if (!post) {
        return res.status(404).json({
          success: false,
          message: "Post not found"
        });
      }

      // Increment view count
      await storage.incrementPostViewCount(postId);

      res.json({
        success: true,
        post
      });
    } catch (error) {
      console.error("Get vendor post error:", error);
      res.status(500).json({
        success: false,
        message: "Failed to fetch post"
      });
    }
  });

  // Update vendor post
  app.put("/api/vendor/posts/:postId", requireAuth, async (req, res) => {
    try {
      const userId = req.session!.userId!;
      const { postId } = req.params;
      const validatedData = updatePostSchema.parse({ ...req.body, id: postId });

      // Verify ownership
      const existingPost = await storage.getVendorPostById(postId);
      if (!existingPost || existingPost.vendorId !== userId) {
        return res.status(403).json({
          success: false,
          message: "You can only edit your own posts"
        });
      }

      const updatedPost = await storage.updateVendorPost(postId, validatedData);

      res.json({
        success: true,
        message: "Post updated successfully",
        post: updatedPost
      });
    } catch (error: any) {
      console.error("Update post error:", error);
      if (error.name === 'ZodError') {
        return res.status(400).json({
          success: false,
          message: "Invalid post data",
          errors: error.errors
        });
      }
      res.status(500).json({
        success: false,
        message: "Failed to update post"
      });
    }
  });

  // Delete vendor post
  app.delete("/api/vendor/posts/:postId", requireAuth, async (req, res) => {
    try {
      const userId = req.session!.userId!;
      const { postId } = req.params;

      // Verify ownership
      const post = await storage.getVendorPostById(postId);
      if (!post || post.vendorId !== userId) {
        return res.status(403).json({
          success: false,
          message: "You can only delete your own posts"
        });
      }

      await storage.deleteVendorPost(postId);

      res.json({
        success: true,
        message: "Post deleted successfully"
      });
    } catch (error) {
      console.error("Delete post error:", error);
      res.status(500).json({
        success: false,
        message: "Failed to delete post"
      });
    }
  });

  // Like/Unlike a vendor post
  app.post("/api/vendor/posts/like", requireAuth, async (req, res) => {
    try {
      const userId = req.session!.userId!;
      const { postId } = likePostSchema.parse(req.body);

      const result = await storage.togglePostLike(postId, userId);

      res.json({
        success: true,
        message: result.liked ? "Post liked" : "Post unliked",
        liked: result.liked,
        likeCount: result.likeCount
      });
    } catch (error: any) {
      console.error("Like post error:", error);
      if (error.name === 'ZodError') {
        return res.status(400).json({
          success: false,
          message: "Invalid request data",
          errors: error.errors
        });
      }
      res.status(500).json({
        success: false,
        message: "Failed to like post"
      });
    }
  });

  // Add comment to vendor post
  app.post("/api/vendor/posts/comment", requireAuth, async (req, res) => {
    try {
      const userId = req.session!.userId!;
      const validatedData = commentSchema.parse(req.body);

      const comment = await storage.addPostComment({
        ...validatedData,
        userId
      });

      // Real-time notification
      if ((global as any).io) {
        (global as any).io.emit('new_post_comment', {
          type: 'NEW_COMMENT',
          postId: validatedData.postId,
          commentId: comment.id,
          userId,
          timestamp: Date.now()
        });
      }

      res.status(201).json({
        success: true,
        message: "Comment added successfully",
        comment
      });
    } catch (error: any) {
      console.error("Add comment error:", error);
      if (error.name === 'ZodError') {
        return res.status(400).json({
          success: false,
          message: "Invalid comment data",
          errors: error.errors
        });
      }
      res.status(500).json({
        success: false,
        message: "Failed to add comment"
      });
    }
  });

  // Get comments for a post
  app.get("/api/vendor/posts/:postId/comments", async (req, res) => {
    try {
      const { postId } = req.params;
      const { page = '1', limit = '20' } = req.query;

      const comments = await storage.getPostComments(postId, {
        page: parseInt(page as string),
        limit: parseInt(limit as string)
      });

      res.json({
        success: true,
        comments: comments.comments,
        pagination: comments.pagination
      });
    } catch (error) {
      console.error("Get comments error:", error);
      res.status(500).json({
        success: false,
        message: "Failed to fetch comments"
      });
    }
  });

  // Get merchant analytics for posts
  app.get("/api/vendor/posts/analytics", requireAuth, async (req, res) => {
    try {
      const userId = req.session!.userId!;
      const user = await storage.getUserById(userId);

      if (!user || user.role !== 'MERCHANT') {
        return res.status(403).json({
          success: false,
          message: "Only merchants can view post analytics"
        });
      }

      const analytics = await storage.getVendorPostAnalytics(userId);

      res.json({
        success: true,
        analytics
      });
    } catch (error) {
      console.error("Get post analytics error:", error);
      res.status(500).json({
        success: false,
        message: "Failed to fetch analytics"
      });
    }
  });
}

