import express from 'express';
import { authenticateToken } from '../middleware/auth';
import { db } from '../db';
import { users, transactions, wallets } from '../../shared/schema';
import { eq, sum, and } from 'drizzle-orm';

const router = express.Router();

// Get wallet balance
router.get('/balance', authenticateToken, async (req, res) => {
  try {
    const userId = req.user?.id;

    if (!userId) {
      return res.status(401).json({ error: 'User not authenticated' });
    }

    // Get user's wallet
    const wallet = await db.select().from(wallets).where(eq(wallets.userId, userId)).limit(1);

    let balance = 0;
    if (wallet.length) {
      balance = wallet[0].balance || 0;
    } else {
      // Create wallet if it doesn't exist
      await db.insert(wallets).values({
        userId,
        balance: 0,
        createdAt: new Date(),
        updatedAt: new Date()
      });
    }

    res.json({
      success: true,
      data: { balance }
    });

  } catch (error) {
    console.error('Wallet balance fetch error:', error);
    res.status(500).json({ error: 'Failed to fetch wallet balance' });
  }
});

// Fund wallet - Initialize payment
router.post('/fund', authenticateToken, async (req, res) => {
  try {
    const userId = req.user?.id;
    const { amount, paymentMethod } = req.body;

    if (!userId) {
      return res.status(401).json({ error: 'User not authenticated' });
    }

    if (!amount || amount <= 0) {
      return res.status(400).json({ error: 'Invalid amount' });
    }

    // Get user details for payment
    const user = await db.select().from(users).where(eq(users.id, userId)).limit(1);

    if (!user.length) {
      return res.status(404).json({ error: 'User not found' });
    }

    // Initialize Paystack payment
    const paystackResponse = await fetch('https://api.paystack.co/transaction/initialize', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${process.env.PAYSTACK_SECRET_KEY}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email: user[0].email,
        amount: amount * 100, // Convert to kobo
        reference: `WALLET_FUND_${Date.now()}_${userId}`,
        callback_url: `${process.env.FRONTEND_URL}/wallet-fund/callback`,
        metadata: {
          userId,
          type: 'wallet_funding',
          paymentMethod
        }
      }),
    });

    const paystackData = await paystackResponse.json();

    if (paystackData.status) {
      // Store pending transaction
      await db.insert(transactions).values({
        userId,
        type: 'CREDIT',
        amount,
        status: 'PENDING',
        description: 'Wallet funding - pending payment',
        reference: paystackData.data.reference,
        createdAt: new Date(),
        updatedAt: new Date()
      });

      res.json({
        success: true,
        authorization_url: paystackData.data.authorization_url,
        access_code: paystackData.data.access_code,
        reference: paystackData.data.reference
      });
    } else {
      throw new Error(paystackData.message || 'Payment initialization failed');
    }

  } catch (error) {
    console.error('Wallet funding error:', error);
    res.status(500).json({ error: 'Failed to initialize payment' });
  }
});

// Verify wallet funding payment
router.post('/fund/verify', authenticateToken, async (req, res) => {
  try {
    const { reference } = req.body;
    const userId = req.user?.id;

    if (!reference) {
      return res.status(400).json({ error: 'Payment reference required' });
    }

    // Verify payment with Paystack
    const verifyResponse = await fetch(`https://api.paystack.co/transaction/verify/${reference}`, {
      headers: {
        'Authorization': `Bearer ${process.env.PAYSTACK_SECRET_KEY}`,
      },
    });

    const verifyData = await verifyResponse.json();

    if (verifyData.status && verifyData.data.status === 'success') {
      const amount = verifyData.data.amount / 100; // Convert from kobo

      // Get or create wallet
      let wallet = await db.select().from(wallets).where(eq(wallets.userId, userId)).limit(1);

      if (!wallet.length) {
        await db.insert(wallets).values({
          userId,
          balance: 0,
          createdAt: new Date(),
          updatedAt: new Date()
        });
        wallet = await db.select().from(wallets).where(eq(wallets.userId, userId)).limit(1);
      }

      const currentBalance = wallet[0].balance || 0;
      const newBalance = currentBalance + amount;

      // Update wallet balance
      await db.update(wallets)
        .set({ 
          balance: newBalance,
          updatedAt: new Date()
        })
        .where(eq(wallets.userId, userId));

      // Update transaction status
      await db.update(transactions)
        .set({ 
          status: 'SUCCESS',
          description: 'Wallet funding - completed',
          updatedAt: new Date()
        })
        .where(eq(transactions.reference, reference));

      res.json({
        success: true,
        data: { 
          balance: newBalance,
          amount,
          message: 'Wallet funded successfully' 
        }
      });
    } else {
      // Update transaction as failed
      await db.update(transactions)
        .set({ 
          status: 'FAILED',
          description: 'Wallet funding - payment failed',
          updatedAt: new Date()
        })
        .where(eq(transactions.reference, reference));

      res.status(400).json({ error: 'Payment verification failed' });
    }

  } catch (error) {
    console.error('Payment verification error:', error);
    res.status(500).json({ error: 'Failed to verify payment' });
  }
});

// Withdraw from wallet
router.post('/withdraw', authenticateToken, async (req, res) => {
  try {
    const userId = req.user?.id;
    const { amount, bankDetails } = req.body;

    if (!userId) {
      return res.status(401).json({ error: 'User not authenticated' });
    }

    if (!amount || amount <= 0) {
      return res.status(400).json({ error: 'Invalid amount' });
    }

    if (!bankDetails || !bankDetails.accountNumber || !bankDetails.bankCode) {
      return res.status(400).json({ error: 'Bank details required' });
    }

    // Get wallet
    const [wallet] = await db.select().from(wallets).where(eq(wallets.userId, userId)).limit(1);

    if (!wallet) {
      return res.status(404).json({ error: 'Wallet not found' });
    }

    const currentBalance = parseFloat(wallet.balance);

    if (currentBalance < amount) {
      return res.status(400).json({ error: 'Insufficient balance' });
    }

    // Validate bank account
    const accountValidation = await paystackService.resolveAccountNumber(
      bankDetails.accountNumber,
      bankDetails.bankCode
    );

    if (!accountValidation.success) {
      return res.status(400).json({ error: 'Invalid bank account details' });
    }

    // Create transfer recipient
    const recipientResult = await paystackService.createTransferRecipient({
      type: 'nuban',
      name: accountValidation.account_name,
      account_number: bankDetails.accountNumber,
      bank_code: bankDetails.bankCode,
      currency: 'NGN'
    });

    if (!recipientResult.success) {
      return res.status(400).json({ error: 'Failed to create transfer recipient' });
    }

    const newBalance = currentBalance - amount;
    const withdrawalRef = `WITHDRAW_${Date.now()}_${userId}`;

    // Update wallet balance
    await db.update(wallets)
      .set({ 
        balance: newBalance.toString(),
        lastUpdated: new Date()
      })
      .where(eq(wallets.userId, userId));

    // Record transaction
    const [transaction] = await db.insert(transactions).values({
      userId,
      type: 'WITHDRAWAL',
      status: 'PENDING',
      amount: amount.toString(),
      netAmount: amount.toString(),
      currency: 'NGN',
      description: `Wallet withdrawal to ${accountValidation.account_name}`,
      paystackReference: withdrawalRef,
      metadata: {
        bankDetails: {
          accountNumber: bankDetails.accountNumber,
          bankCode: bankDetails.bankCode,
          accountName: accountValidation.account_name
        },
        recipientCode: recipientResult.recipient_code
      },
      initiatedAt: new Date()
    }).returning();

    // Initiate transfer
    const transferResult = await paystackService.initiateTransfer({
      source: 'balance',
      amount: amount * 100, // Convert to kobo
      recipient: recipientResult.recipient_code,
      reason: 'Wallet withdrawal',
      reference: withdrawalRef
    });

    if (transferResult.success) {
      await db.update(transactions)
        .set({
          status: 'PROCESSING',
          paystackTransactionId: transferResult.transfer_code,
          updatedAt: new Date()
        })
        .where(eq(transactions.id, transaction.id));
    }

    res.json({
      success: true,
      data: { 
        balance: newBalance,
        transactionId: transaction.id,
        reference: withdrawalRef,
        message: 'Withdrawal request submitted successfully' 
      }
    });

  } catch (error) {
    console.error('Wallet withdrawal error:', error);
    res.status(500).json({ error: 'Failed to process withdrawal' });
  }
});

// Get available banks
router.get('/banks', authenticateToken, async (req, res) => {
  try {
    const banksResult = await paystackService.getBanks();

    if (!banksResult.success) {
      return res.status(500).json({ error: 'Failed to fetch banks' });
    }

    res.json({
      success: true,
      data: banksResult.data
    });

  } catch (error) {
    console.error('Get banks error:', error);
    res.status(500).json({ error: 'Failed to fetch banks' });
  }
});

// Validate bank account
router.post('/validate-account', authenticateToken, async (req, res) => {
  try {
    const { accountNumber, bankCode } = req.body;

    if (!accountNumber || !bankCode) {
      return res.status(400).json({ error: 'Account number and bank code required' });
    }

    const validationResult = await paystackService.resolveAccountNumber(accountNumber, bankCode);

    if (!validationResult.success) {
      return res.status(400).json({ error: 'Invalid account details' });
    }

    res.json({
      success: true,
      data: {
        accountName: validationResult.account_name,
        accountNumber: validationResult.account_number
      }
    });

  } catch (error) {
    console.error('Account validation error:', error);
    res.status(500).json({ error: 'Failed to validate account' });
  }
});

// Get transaction history
router.get('/transactions', authenticateToken, async (req, res) => {
  try {
    const userId = req.user?.id;

    if (!userId) {
      return res.status(401).json({ error: 'User not authenticated' });
    }

    const userTransactions = await db.select()
      .from(transactions)
      .where(eq(transactions.userId, userId))
      .orderBy(transactions.createdAt);

    res.json({
      success: true,
      data: userTransactions
    });

  } catch (error) {
    console.error('Transaction history fetch error:', error);
    res.status(500).json({ error: 'Failed to fetch transaction history' });
  }
});

export default router;

server/routes/websocket-test.ts.content:
import { Router } from 'express';
import { authenticateToken } from '../middleware/auth';

const router = Router();

// Test WebSocket connection endpoint
router.get('/test', authenticateToken, (req, res) => {
  const io = (global as any).io;

  if (!io) {
    return res.status(500).json({
      success: false,
      message: 'WebSocket server not initialized'
    });
  }

  // Send test message to user
  const userId = req.user?.id;
  if (userId) {
    io.to(`user_${userId}`).emit('test_notification', {
      type: 'TEST',
      title: 'WebSocket Test',
      message: 'WebSocket connection is working properly!',
      timestamp: Date.now()
    });
  }

  // Get connection stats
  const connectedSockets = io.sockets.sockets.size;
  const rooms = Array.from(io.sockets.adapter.rooms.keys());

  res.json({
    success: true,
    message: 'WebSocket test completed',
    stats: {
      connectedSockets,
      totalRooms: rooms.length,
      testSent: !!userId
    }
  });
});

// Get WebSocket status
router.get('/status', (req, res) => {
  const io = (global as any).io;

  if (!io) {
    return res.status(500).json({
      success: false,
      message: 'WebSocket server not initialized'
    });
  }

  const connectedSockets = io.sockets.sockets.size;
  const rooms = Array.from(io.sockets.adapter.rooms.keys());
  const adminRooms = rooms.filter(room => room.startsWith('admin_'));
  const userRooms = rooms.filter(room => room.startsWith('user_'));
  const orderRooms = rooms.filter(room => room.startsWith('order_'));

  res.json({
    success: true,
    status: 'active',
    stats: {
      connectedSockets,
      totalRooms: rooms.length,
      adminRooms: adminRooms.length,
      userRooms: userRooms.length,
      orderRooms: orderRooms.length
    },
    rooms: {
      admin: adminRooms.slice(0, 10), // Show first 10
      user: userRooms.slice(0, 10),
      order: orderRooms.slice(0, 10)
    }
  });
});

export default router;

server/routes/withdrawal-system.ts.content:
import express from 'express';
import { db } from '../db';
import { wallets, transactions, users } from '../../shared/schema';
import { eq, and, gte } from 'drizzle-orm';
import { authenticateToken } from '../middleware/auth';
import { paystack } from '../services/paystack';

const router = express.Router();

// Get user's bank accounts
router.get('/bank-accounts', authenticateToken, async (req, res) => {
  try {
    const userId = req.user?.id;

    const [user] = await db
      .select()
      .from(users)
      .where(eq(users.id, userId!));

    if (!user?.bankAccounts) {
      return res.json({ accounts: [] });
    }

    res.json({ accounts: user.bankAccounts });
  } catch (error) {
    console.error('Get bank accounts error:', error);
    res.status(500).json({ error: 'Failed to fetch bank accounts' });
  }
});

// Add/verify bank account
router.post('/verify-account', authenticateToken, async (req, res) => {
  try {
    const { accountNumber, bankCode } = req.body;
    const userId = req.user?.id;

    // Verify account with Paystack
    const verification = await paystack.verifyAccount(accountNumber, bankCode);

    if (!verification.status) {
      return res.status(400).json({ error: 'Account verification failed' });
    }

    const accountInfo = {
      accountNumber,
      bankCode,
      accountName: verification.data.account_name,
      bankName: verification.data.bank_name,
      verified: true,
      createdAt: new Date()
    };

    // Update user's bank accounts
    const [user] = await db
      .select()
      .from(users)
      .where(eq(users.id, userId!));

    const existingAccounts = user?.bankAccounts || [];
    const updatedAccounts = [...existingAccounts, accountInfo];

    await db
      .update(users)
      .set({ 
        bankAccounts: updatedAccounts,
        updatedAt: new Date()
      })
      .where(eq(users.id, userId!));

    res.json({ 
      success: true, 
      account: accountInfo,
      message: 'Bank account verified successfully'
    });
  } catch (error) {
    console.error('Account verification error:', error);
    res.status(500).json({ error: 'Failed to verify account' });
  }
});

// Initiate withdrawal
router.post('/initiate', authenticateToken, async (req, res) => {
  try {
    const { amount, accountNumber, bankCode, reason } = req.body;
    const userId = req.user?.id;

    // Validate amount
    if (amount < 100) {
      return res.status(400).json({ error: 'Minimum withdrawal amount is â‚¦100' });
    }

    // Check wallet balance
    const [wallet] = await db
      .select()
      .from(wallets)
      .where(eq(wallets.userId, userId!));

    if (!wallet || wallet.balance < amount) {
      return res.status(400).json({ error: 'Insufficient wallet balance' });
    }

    // Check daily withdrawal limit
    const todayStart = new Date();
    todayStart.setHours(0, 0, 0, 0);

    const todayWithdrawals = await db
      .select({ total: sql<number>`COALESCE(SUM(amount), 0)` })
      .from(transactions)
      .where(and(
        eq(transactions.userId, userId!),
        eq(transactions.type, 'WITHDRAWAL'),
        gte(transactions.createdAt, todayStart),
        eq(transactions.status, 'COMPLETED')
      ));

    const dailyLimit = 50000; // â‚¦50,000 daily limit
    if ((todayWithdrawals[0]?.total || 0) + amount > dailyLimit) {
      return res.status(400).json({ error: 'Daily withdrawal limit exceeded' });
    }

    // Create withdrawal transaction
    const reference = `withdrawal_${userId}_${Date.now()}`;

    const [transaction] = await db
      .insert(transactions)
      .values({
        userId: userId!,
        type: 'WITHDRAWAL',
        amount: -amount, // Negative for withdrawal
        status: 'PENDING',
        reference,
        description: reason || 'Wallet withdrawal',
        paystackData: { accountNumber, bankCode },
        createdAt: new Date()
      })
      .returning();

    // Deduct from wallet (will be restored if withdrawal fails)
    await db
      .update(wallets)
      .set({ 
        balance: sql`balance - ${amount}`,
        updatedAt: new Date()
      })
      .where(eq(wallets.userId, userId!));

    // Initiate transfer with Paystack
    try {
      const transferResult = await paystack.initiateTransfer({
        amount: amount * 100, // Convert to kobo
        recipient: accountNumber,
        bankCode,
        reference,
        reason: reason || 'Wallet withdrawal'
      });

      if (transferResult.status) {
        // Update transaction with Paystack reference
        await db
          .update(transactions)
          .set({ 
            paystackReference: transferResult.data.transfer_code,
            status: 'PROCESSING'
          })
          .where(eq(transactions.id, transaction.id));

        res.json({
          success: true,
          message: 'Withdrawal initiated successfully',
          reference,
          transferCode: transferResult.data.transfer_code
        });
      } else {
        throw new Error('Paystack transfer initiation failed');
      }
    } catch (transferError) {
      // Restore wallet balance if transfer fails
      await db
        .update(wallets)
        .set({ 
          balance: sql`balance + ${amount}`,
          updatedAt: new Date()
        })
        .where(eq(wallets.userId, userId!));

      // Update transaction status
      await db
        .update(transactions)
        .set({ status: 'FAILED' })
        .where(eq(transactions.id, transaction.id));

      throw transferError;
    }

  } catch (error) {
    console.error('Withdrawal initiation error:', error);
    res.status(500).json({ error: 'Failed to initiate withdrawal' });
  }
});

// Get withdrawal history
router.get('/history', authenticateToken, async (req, res) => {
  try {
    const userId = req.user?.id;
    const page = parseInt(req.query.page as string) || 1;
    const limit = parseInt(req.query.limit as string) || 20;
    const offset = (page - 1) * limit;

    const withdrawals = await db
      .select()
      .from(transactions)
      .where(and(
        eq(transactions.userId, userId!),
        eq(transactions.type, 'WITHDRAWAL')
      ))
      .orderBy(desc(transactions.createdAt))
      .limit(limit)
      .offset(offset);

    const total = await db
      .select({ count: sql<number>`count(*)` })
      .from(transactions)
      .where(and(
        eq(transactions.userId, userId!),
        eq(transactions.type, 'WITHDRAWAL')
      ));

    res.json({
      withdrawals,
      pagination: {
        page,
        limit,
        total: total[0]?.count || 0,
        pages: Math.ceil((total[0]?.count || 0) / limit)
      }
    });
  } catch (error) {
    console.error('Withdrawal history error:', error);
    res.status(500).json({ error: 'Failed to fetch withdrawal history' });
  }
});

export default router;

