import { db } from "../db";
import { users, adminUsers } from "../../shared/schema";
import { eq, and, desc } from "drizzle-orm";

export class RoleManagementService {
  // Apply for a new role
  static async applyForRole(applicationData: Omit<InsertRoleApplication, 'id' | 'createdAt' | 'updatedAt'>) {
    try {
      const [application] = await db.insert(roleApplications).values({
        ...applicationData,
        appliedAt: new Date(),
      }).returning();

      return { success: true, application };
    } catch (error) {
      console.error('Error creating role application:', error);
      return { success: false, error: 'Failed to submit role application' };
    }
  }

  // Get user's role applications
  static async getUserRoleApplications(userId: number) {
    try {
      const applications = await db
        .select()
        .from(roleApplications)
        .where(eq(roleApplications.userId, userId))
        .orderBy(desc(roleApplications.appliedAt));

      return { success: true, applications };
    } catch (error) {
      console.error('Error fetching role applications:', error);
      return { success: false, error: 'Failed to fetch applications' };
    }
  }

  // Admin: Review role application
  static async reviewRoleApplication(
    applicationId: string, 
    reviewerId: number, 
    status: 'APPROVED' | 'REJECTED' | 'REQUIRES_ADDITIONAL_INFO',
    reviewNotes?: string,
    rejectionReason?: string
  ) {
    try {
      const [updatedApplication] = await db
        .update(roleApplications)
        .set({
          applicationStatus: status,
          reviewedBy: reviewerId,
          reviewNotes,
          rejectionReason,
          reviewedAt: new Date(),
          ...(status === 'APPROVED' && { approvedAt: new Date() })
        })
        .where(eq(roleApplications.id, applicationId))
        .returning();

      // If approved, create new user role
      if (status === 'APPROVED' && updatedApplication) {
        await this.activateUserRole(updatedApplication.userId, updatedApplication.toRole);
      }

      return { success: true, application: updatedApplication };
    } catch (error) {
      console.error('Error reviewing role application:', error);
      return { success: false, error: 'Failed to review application' };
    }
  }

  // Activate a new role for user
  static async activateUserRole(userId: number, role: string, isPrimary: boolean = false) {
    try {
      // If setting as primary, deactivate current primary role
      if (isPrimary) {
        await db
          .update(userRoles)
          .set({ isPrimary: false })
          .where(and(eq(userRoles.userId, userId), eq(userRoles.isPrimary, true)));
      }

      const [newRole] = await db.insert(userRoles).values({
        userId,
        role: role as any,
        isActive: true,
        isPrimary,
        activatedAt: new Date(),
      }).returning();

      // Update primary role in users table if this is the new primary
      if (isPrimary) {
        await db
          .update(users)
          .set({ role: role as any })
          .where(eq(users.id, userId));
      }

      return { success: true, role: newRole };
    } catch (error) {
      console.error('Error activating user role:', error);
      return { success: false, error: 'Failed to activate role' };
    }
  }

  // Switch active role (for multi-role users)
  static async switchUserRole(userId: number, targetRole: string) {
    try {
      // Check if user has this role
      const existingRole = await db
        .select()
        .from(userRoles)
        .where(and(
          eq(userRoles.userId, userId),
          eq(userRoles.role, targetRole as any),
          eq(userRoles.isActive, true)
        ))
        .limit(1);

      if (existingRole.length === 0) {
        return { success: false, error: 'User does not have this role' };
      }

      // Deactivate current primary role
      await db
        .update(userRoles)
        .set({ isPrimary: false })
        .where(and(eq(userRoles.userId, userId), eq(userRoles.isPrimary, true)));

      // Set new primary role
      await db
        .update(userRoles)
        .set({ isPrimary: true })
        .where(and(
          eq(userRoles.userId, userId),
          eq(userRoles.role, targetRole as any)
        ));

      // Update users table
      await db
        .update(users)
        .set({ role: targetRole as any })
        .where(eq(users.id, userId));

      return { success: true, message: 'Role switched successfully' };
    } catch (error) {
      console.error('Error switching user role:', error);
      return { success: false, error: 'Failed to switch role' };
    }
  }

  // Get user's active roles
  static async getUserRoles(userId: number) {
    try {
      const roles = await db
        .select()
        .from(userRoles)
        .where(and(eq(userRoles.userId, userId), eq(userRoles.isActive, true)))
        .orderBy(desc(userRoles.isPrimary));

      return { success: true, roles };
    } catch (error) {
      console.error('Error fetching user roles:', error);
      return { success: false, error: 'Failed to fetch roles' };
    }
  }

  // Deactivate a role
  static async deactivateUserRole(userId: number, role: string) {
    try {
      await db
        .update(userRoles)
        .set({ 
          isActive: false,
          isPrimary: false,
          deactivatedAt: new Date()
        })
        .where(and(
          eq(userRoles.userId, userId),
          eq(userRoles.role, role as any)
        ));

      return { success: true, message: 'Role deactivated successfully' };
    } catch (error) {
      console.error('Error deactivating user role:', error);
      return { success: false, error: 'Failed to deactivate role' };
    }
  }

  // Get pending role applications for admin review
  static async getPendingApplications() {
    try {
      const applications = await db
        .select({
          id: roleApplications.id,
          userId: roleApplications.userId,
          fromRole: roleApplications.fromRole,
          toRole: roleApplications.toRole,
          applicationStatus: roleApplications.applicationStatus,
          applicationData: roleApplications.applicationData,
          documents: roleApplications.documents,
          appliedAt: roleApplications.appliedAt,
          userFullName: users.fullName,
          userEmail: users.email,
        })
        .from(roleApplications)
        .leftJoin(users, eq(roleApplications.userId, users.id))
        .where(eq(roleApplications.applicationStatus, 'PENDING'))
        .orderBy(desc(roleApplications.appliedAt));

      return { success: true, applications };
    } catch (error) {
      console.error('Error fetching pending applications:', error);
      return { success: false, error: 'Failed to fetch applications' };
    }
  }
}

server/services/transaction.ts.content:
import { storage } from '../storage';
import { paystackService } from './paystack';
import { db } from '../db';
import { transactions, wallets } from '../../shared/schema';
import { eq, and } from 'drizzle-orm';

export interface PaymentInitiationParams {
  userId: number;
  amount: number;
  email: string;
  description?: string;
  orderId?: string;
  channels?: string[];
  split?: any;
  metadata?: any;
}

export interface PaymentVerificationResult {
  success: boolean;
  transaction?: any;
  error?: string;
}

class TransactionService {
  /**
   * Initiate a payment transaction
   */
  async initiatePayment(params: PaymentInitiationParams) {
    try {
      // Create transaction record
      const transactionRef = `TXN_${Date.now()}_${params.userId}`;
      const amountInKobo = Math.round(params.amount * 100);

      const [transaction] = await db
        .insert(transactions)
        .values({
          userId: params.userId,
          type: 'PAYMENT',
          status: 'PENDING',
          amount: params.amount.toString(),
          netAmount: params.amount.toString(),
          currency: 'NGN',
          description: params.description || 'Payment transaction',
          paystackReference: transactionRef,
          orderId: params.orderId,
          metadata: params.metadata,
          initiatedAt: new Date()
        })
        .returning();

      // Initialize with Paystack
      const paystackResult = await paystackService.initializeTransaction({
        email: params.email,
        amount: amountInKobo,
        reference: transactionRef,
        metadata: {
          ...params.metadata,
          userId: params.userId,
          transactionId: transaction.id
        },
        channels: params.channels || ['card', 'bank', 'ussd', 'qr'],
        split_code: params.split?.split_code
      });

      if (!paystackResult.success) {
        // Update transaction status to failed
        await db
          .update(transactions)
          .set({
            status: 'FAILED',
            failedAt: new Date()
          })
          .where(eq(transactions.id, transaction.id));

        return {
          success: false,
          error: paystackResult.error,
          transactionId: transaction.id
        };
      }

      // Update transaction with Paystack details
      await db
        .update(transactions)
        .set({
          paystackAccessCode: paystackResult.access_code,
          paystackTransactionId: paystackResult.data?.id
        })
        .where(eq(transactions.id, transaction.id));

      return {
        success: true,
        transactionId: transaction.id,
        reference: transactionRef,
        authorization_url: paystackResult.authorization_url,
        access_code: paystackResult.access_code
      };

    } catch (error: any) {
      console.error('Payment initiation error:', error);
      return {
        success: false,
        error: error.message || 'Failed to initiate payment'
      };
    }
  }

  /**
   * Verify a payment transaction
   */
  async verifyPayment(reference: string): Promise<PaymentVerificationResult> {
    try {
      // Get transaction from database
      const [transaction] = await db
        .select()
        .from(transactions)
        .where(eq(transactions.paystackReference, reference));

      if (!transaction) {
        return {
          success: false,
          error: 'Transaction not found'
        };
      }

      // Verify with Paystack
      const paystackResult = await paystackService.verifyTransaction(reference);

      if (!paystackResult.success) {
        return {
          success: false,
          error: paystackResult.error,
          transaction
        };
      }

      // Update transaction status
      const status = paystackResult.status === 'success' ? 'SUCCESS' : 'FAILED';
      const updateData: any = {
        status,
        gatewayResponse: paystackResult.data
      };

      if (status === 'SUCCESS') {
        updateData.completedAt = new Date();
      } else {
        updateData.failedAt = new Date();
      }

      const [updatedTransaction] = await db
        .update(transactions)
        .set(updateData)
        .where(eq(transactions.id, transaction.id))
        .returning();

      // If successful and it's a wallet deposit, update wallet balance
      if (status === 'SUCCESS' && transaction.type === 'DEPOSIT') {
        await this.updateWalletBalance(transaction.userId, parseFloat(transaction.amount));
      }

      return {
        success: status === 'SUCCESS',
        transaction: updatedTransaction
      };

    } catch (error: any) {
      console.error('Payment verification error:', error);
      return {
        success: false,
        error: error.message || 'Failed to verify payment'
      };
    }
  }

  /**
   * Update wallet balance
   */
  private async updateWalletBalance(userId: number, amount: number) {
    try {
      const [wallet] = await db
        .select()
        .from(wallets)
        .where(eq(wallets.userId, userId));

      if (wallet) {
        const newBalance = parseFloat(wallet.balance) + amount;
        await db
          .update(wallets)
          .set({
            balance: newBalance.toString(),
            lastUpdated: new Date()
          })
          .where(eq(wallets.id, wallet.id));
      }
    } catch (error) {
      console.error('Wallet balance update error:', error);
    }
  }

  /**
   * Process wallet transfer
   */
  async processWalletTransfer(fromUserId: number, toUserId: number, amount: number, description?: string) {
    try {
      // Get sender wallet
      const [senderWallet] = await db
        .select()
        .from(wallets)
        .where(eq(wallets.userId, fromUserId));

      if (!senderWallet || parseFloat(senderWallet.balance) < amount) {
        return {
          success: false,
          error: 'Insufficient balance'
        };
      }

      // Create transfer transaction
      const transferRef = `TRANSFER_${Date.now()}_${fromUserId}_${toUserId}`;

      const [transaction] = await db
        .insert(transactions)
        .values({
          userId: fromUserId,
          recipientId: toUserId,
          type: 'TRANSFER',
          status: 'SUCCESS',
          amount: amount.toString(),
          netAmount: amount.toString(),
          currency: 'NGN',
          description: description || 'Wallet transfer',
          paystackReference: transferRef,
          completedAt: new Date()
        })
        .returning();

      // Update sender balance
      const newSenderBalance = parseFloat(senderWallet.balance) - amount;
      await db
        .update(wallets)
        .set({
          balance: newSenderBalance.toString(),
          lastUpdated: new Date()
        })
        .where(eq(wallets.id, senderWallet.id));

      // Update receiver balance
      await this.updateWalletBalance(toUserId, amount);

      return {
        success: true,
        transaction,
        transferRef
      };

    } catch (error: any) {
      console.error('Wallet transfer error:', error);
      return {
        success: false,
        error: error.message || 'Failed to process transfer'
      };
    }
  }

  /**
   * Process refund
   */
  async processRefund(transactionId: string, amount?: number, reason?: string) {
    try {
      const [originalTransaction] = await db
        .select()
        .from(transactions)
        .where(eq(transactions.id, transactionId));

      if (!originalTransaction) {
        return {
          success: false,
          error: 'Original transaction not found'
        };
      }

      const refundAmount = amount || parseFloat(originalTransaction.amount);
      const refundRef = `REFUND_${Date.now()}_${transactionId}`;

      // Create refund transaction
      const [refundTransaction] = await db
        .insert(transactions)
        .values({
          userId: originalTransaction.userId,
          type: 'REFUND',
          status: 'SUCCESS',
          amount: refundAmount.toString(),
          netAmount: refundAmount.toString(),
          currency: 'NGN',
          description: reason || 'Transaction refund',
          paystackReference: refundRef,
          orderId: originalTransaction.orderId,
          completedAt: new Date()
        })
        .returning();

      // Update wallet balance
      await this.updateWalletBalance(originalTransaction.userId, refundAmount);

      return {
        success: true,
        refundTransaction,
        refundRef
      };

    } catch (error: any) {
      console.error('Refund processing error:', error);
      return {
        success: false,
        error: error.message || 'Failed to process refund'
      };
    }
  }
}

export const transactionService = new TransactionService();

server/websocket/live-system-handler.ts.content:
import { Server as SocketIOServer, Socket } from "socket.io";
import { LiveSystemService } from "../services/live-system";
import { RoleManagementService } from "../services/role-management";
import { AnalyticsService } from "../services/analytics";
import jwt from "jsonwebtoken";

interface AuthenticatedSocket extends Socket {
  userId?: number;
  userRole?: string;
  userName?: string;
}

export class LiveSystemHandler {
  private io: SocketIOServer;
  private connectedUsers: Map<string, AuthenticatedSocket> = new Map();
  private userSockets: Map<number, Set<string>> = new Map();

  constructor(io: SocketIOServer) {
    this.io = io;
    this.setupEventHandlers();
  }

  private setupEventHandlers() {
    this.io.on('connection', (socket: AuthenticatedSocket) => {
      console.log(`WebSocket connection established: ${socket.id}`);

      // Authentication
      socket.on('authenticate', async (data) => {
        await this.handleAuthentication(socket, data);
      });

      // Role Management Events
      socket.on('role:apply', async (data) => {
        await this.handleRoleApplication(socket, data);
      });

      socket.on('role:switch', async (data) => {
        await this.handleRoleSwitch(socket, data);
      });

      // Live System Events
      socket.on('location:update', async (data) => {
        await this.handleLocationUpdate(socket, data);
      });

      socket.on('notification:send', async (data) => {
        await this.handleNotificationSend(socket, data);
      });

      socket.on('notification:read', async (data) => {
        await this.handleNotificationRead(socket, data);
      });

      // Order and Delivery Events
      socket.on('order:status_update', async (data) => {
        await this.handleOrderStatusUpdate(socket, data);
      });

      socket.on('delivery:status_update', async (data) => {
        await this.handleDeliveryStatusUpdate(socket, data);
      });

      socket.on('delivery:location_share', async (data) => {
        await this.handleDeliveryLocationShare(socket, data);
      });

      // Chat and Communication Events
      socket.on('chat:join_room', async (data) => {
        await this.handleJoinChatRoom(socket, data);
      });

      socket.on('chat:leave_room', async (data) => {
        await this.handleLeaveChatRoom(socket, data);
      });

      socket.on('chat:message', async (data) => {
        await this.handleChatMessage(socket, data);
      });

      socket.on('chat:typing', async (data) => {
        await this.handleTypingIndicator(socket, data);
      });

      // Analytics Events
      socket.on('analytics:track', async (data) => {
        await this.handleAnalyticsTracking(socket, data);
      });

      socket.on('analytics:interaction', async (data) => {
        await this.handleInteractionTracking(socket, data);
      });

      // System Events
      socket.on('system:heartbeat', async () => {
        await this.handleHeartbeat(socket);
      });

      socket.on('system:presence', async (data) => {
        await this.handlePresenceUpdate(socket, data);
      });

      // Disconnect handler
      socket.on('disconnect', async () => {
        await this.handleDisconnect(socket);
      });

      // Error handling
      socket.on('error', (error) => {
        console.error(`WebSocket error for ${socket.id}:`, error);
      });
    });
  }

  private async handleAuthentication(socket: AuthenticatedSocket, data: any) {
    try {
      const { token } = data;

      if (!token) {
        socket.emit('auth:error', { message: 'Authentication token required' });
        return;
      }

      // Verify JWT token
      const JWT_SECRET = process.env.JWT_SECRET || 'your-secret-key';
      const decoded = jwt.verify(token, JWT_SECRET) as any;

      socket.userId = decoded.userId;
      socket.userRole = decoded.role;
      socket.userName = decoded.fullName;

      // Register connection in database
      await LiveSystemService.registerConnection({
        userId: socket.userId!,
        socketId: socket.id,
        userRole: socket.userRole as any,
        connectionType: 'LIVE_CHAT',
        metadata: {
          userAgent: socket.handshake.headers['user-agent'],
          ip: socket.handshake.address,
        },
      });

      // Add to connected users tracking
      this.connectedUsers.set(socket.id, socket);

      if (!this.userSockets.has(socket.userId!)) {
        this.userSockets.set(socket.userId!, new Set());
      }
      this.userSockets.get(socket.userId!)!.add(socket.id);

      // Join role-based rooms
      socket.join(`role_${socket.userRole}`);
      socket.join(`user_${socket.userId}`);

      socket.emit('auth:success', {
        userId: socket.userId,
        role: socket.userRole,
        socketId: socket.id,
      });

      console.log(`User ${socket.userId} (${socket.userRole}) authenticated on socket ${socket.id}`);

      // Broadcast user online status
      this.broadcastPresenceUpdate(socket.userId!, 'online', socket.userRole);

    } catch (error) {
      console.error('Authentication error:', error);
      socket.emit('auth:error', { message: 'Invalid authentication token' });
    }
  }

  private async handleRoleApplication(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) {
      socket.emit('error', { message: 'Authentication required' });
      return;
    }

    try {
      const result = await RoleManagementService.applyForRole({
        userId: socket.userId!,
        ...data,
      });

      if (result.success) {
        socket.emit('role:application_submitted', result.application);

        // Notify admins
        this.io.to('role_ADMIN').emit('role:new_application', {
          application: result.application,
          applicant: { id: socket.userId, name: socket.userName },
        });
      } else {
        socket.emit('role:application_error', { error: result.error });
      }
    } catch (error) {
      console.error('Role application error:', error);
      socket.emit('role:application_error', { error: 'Failed to submit application' });
    }
  }

  private async handleRoleSwitch(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) {
      socket.emit('error', { message: 'Authentication required' });
      return;
    }

    try {
      const { targetRole } = data;
      const result = await RoleManagementService.switchUserRole(socket.userId!, targetRole);

      if (result.success) {
        // Leave old role room
        socket.leave(`role_${socket.userRole}`);

        // Update socket role
        socket.userRole = targetRole;

        // Join new role room
        socket.join(`role_${targetRole}`);

        socket.emit('role:switched', { newRole: targetRole });

        // Track the role switch interaction
        await AnalyticsService.trackCrossRoleInteraction({
          initiatorId: socket.userId,
          initiatorRole: socket.userRole as any,
          targetId: socket.userId,
          targetRole: targetRole as any,
          interactionType: 'ROLE_SWITCH' as any,
        });

      } else {
        socket.emit('role:switch_error', { error: result.error });
      }
    } catch (error) {
      console.error('Role switch error:', error);
      socket.emit('role:switch_error', { error: 'Failed to switch role' });
    }
  }

  private async handleLocationUpdate(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) {
      socket.emit('error', { message: 'Authentication required' });
      return;
    }

    try {
      const result = await LiveSystemService.updateLocation({
        userId: socket.userId,
        userRole: socket.userRole as any,
        ...data,
      });

      if (result.success) {
        socket.emit('location:updated', result.location);

        // Broadcast location to relevant parties based on sharing level
        this.broadcastLocationUpdate(result.location);
      } else {
        socket.emit('location:error', { error: result.error });
      }
    } catch (error) {
      console.error('Location update error:', error);
      socket.emit('location:error', { error: 'Failed to update location' });
    }
  }

  private async handleNotificationSend(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) {
      socket.emit('error', { message: 'Authentication required' });
      return;
    }

    try {
      const result = await LiveSystemService.createNotification({
        userId: data.targetUserId || socket.userId,
        ...data,
      });

      if (result.success) {
        socket.emit('notification:sent', result.notification);

        // Send notification to target user
        const targetSockets = this.userSockets.get(data.targetUserId);
        if (targetSockets) {
          targetSockets.forEach(socketId => {
            this.io.to(socketId).emit('notification:received', result.notification);
          });
        }
      } else {
        socket.emit('notification:error', { error: result.error });
      }
    } catch (error) {
      console.error('Notification send error:', error);
      socket.emit('notification:error', { error: 'Failed to send notification' });
    }
  }

  private async handleNotificationRead(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) {
      socket.emit('error', { message: 'Authentication required' });
      return;
    }

    try {
      const { notificationId } = data;
      const result = await LiveSystemService.markNotificationAsRead(notificationId, socket.userId);

      if (result.success) {
        socket.emit('notification:marked_read', { notificationId });
      } else {
        socket.emit('notification:error', { error: result.error });
      }
    } catch (error) {
      console.error('Notification read error:', error);
      socket.emit('notification:error', { error: 'Failed to mark notification as read' });
    }
  }

  private async handleOrderStatusUpdate(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) {
      socket.emit('error', { message: 'Authentication required' });
      return;
    }

    try {
      const { orderId, status, updates } = data;

      // Broadcast order update to all parties in the order room
      this.io.to(`order_${orderId}`).emit('order:status_changed', {
        orderId,
        status,
        updates,
        updatedBy: socket.userId,
        timestamp: new Date(),
      });

      // Track interaction
      await AnalyticsService.trackCrossRoleInteraction({
        initiatorId: socket.userId,
        initiatorRole: socket.userRole as any,
        targetId: updates.customerId || socket.userId,
        targetRole: 'CONSUMER',
        interactionType: 'ORDER_UPDATE' as any,
        relatedOrderId: orderId,
      });

      socket.emit('order:update_sent', { orderId, status });

    } catch (error) {
      console.error('Order status update error:', error);
      socket.emit('order:error', { error: 'Failed to update order status' });
    }
  }

  private async handleDeliveryStatusUpdate(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId || socket.userRole !== 'DRIVER') {
      socket.emit('error', { message: 'Driver authentication required' });
      return;
    }

    try {
      const { deliveryId, status, location, estimatedTime, proof } = data;

      // Broadcast delivery update
      this.io.to(`delivery_${deliveryId}`).emit('delivery:status_changed', {
        deliveryId,
        status,
        location,
        estimatedTime,
        proof,
        driverId: socket.userId,
        timestamp: new Date(),
      });

      socket.emit('delivery:update_sent', { deliveryId, status });

    } catch (error) {
      console.error('Delivery status update error:', error);
      socket.emit('delivery:error', { error: 'Failed to update delivery status' });
    }
  }

  private async handleDeliveryLocationShare(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId || socket.userRole !== 'DRIVER') {
      socket.emit('error', { message: 'Driver authentication required' });
      return;
    }

    try {
      const { deliveryId, location } = data;

      // Share location with customers and merchants for this delivery
      this.io.to(`delivery_${deliveryId}`).emit('delivery:location_update', {
        deliveryId,
        driverId: socket.userId,
        location,
        timestamp: new Date(),
      });

    } catch (error) {
      console.error('Delivery location share error:', error);
      socket.emit('delivery:error', { error: 'Failed to share location' });
    }
  }

  private async handleJoinChatRoom(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) {
      socket.emit('error', { message: 'Authentication required' });
      return;
    }

    try {
      const { roomId, roomType } = data;

      socket.join(`chat_${roomId}`);

      // Notify others in the room
      socket.to(`chat_${roomId}`).emit('chat:user_joined', {
        userId: socket.userId,
        userName: socket.userName,
        userRole: socket.userRole,
        timestamp: new Date(),
      });

      socket.emit('chat:joined', { roomId, roomType });

    } catch (error) {
      console.error('Join chat room error:', error);
      socket.emit('chat:error', { error: 'Failed to join chat room' });
    }
  }

  private async handleLeaveChatRoom(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) {
      socket.emit('error', { message: 'Authentication required' });
      return;
    }

    try {
      const { roomId } = data;

      socket.leave(`chat_${roomId}`);

      // Notify others in the room
      socket.to(`chat_${roomId}`).emit('chat:user_left', {
        userId: socket.userId,
        userName: socket.userName,
        timestamp: new Date(),
      });

      socket.emit('chat:left', { roomId });

    } catch (error) {
      console.error('Leave chat room error:', error);
      socket.emit('chat:error', { error: 'Failed to leave chat room' });
    }
  }

  private async handleChatMessage(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) {
      socket.emit('error', { message: 'Authentication required' });
      return;
    }

    try {
      const { roomId, message, messageType, attachments } = data;

      const messageData = {
        id: Date.now().toString(),
        roomId,
        senderId: socket.userId,
        senderName: socket.userName,
        senderRole: socket.userRole,
        message,
        messageType: messageType || 'TEXT',
        attachments: attachments || [],
        timestamp: new Date(),
      };

      // Broadcast message to room
      this.io.to(`chat_${roomId}`).emit('chat:message_received', messageData);

      // Track chat interaction
      await AnalyticsService.trackCrossRoleInteraction({
        initiatorId: socket.userId,
        initiatorRole: socket.userRole as any,
        targetId: 0, // Would be determined by room participants
        targetRole: 'CONSUMER', // Would be determined by room type
        interactionType: 'CHAT_MESSAGE' as any,
        relatedChatId: roomId,
      });

    } catch (error) {
      console.error('Chat message error:', error);
      socket.emit('chat:error', { error: 'Failed to send message' });
    }
  }

  private async handleTypingIndicator(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) return;

    try {
      const { roomId, isTyping } = data;

      socket.to(`chat_${roomId}`).emit('chat:typing_indicator', {
        userId: socket.userId,
        userName: socket.userName,
        isTyping,
        timestamp: new Date(),
      });

    } catch (error) {
      console.error('Typing indicator error:', error);
    }
  }

  private async handleAnalyticsTracking(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) return;

    try {
      await AnalyticsService.trackUserBehavior({
        userId: socket.userId,
        userRole: socket.userRole as any,
        sessionId: socket.id,
        ...data,
      });

    } catch (error) {
      console.error('Analytics tracking error:', error);
    }
  }

  private async handleInteractionTracking(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) return;

    try {
      await AnalyticsService.trackCrossRoleInteraction({
        initiatorId: socket.userId,
        initiatorRole: socket.userRole as any,
        ...data,
      });

    } catch (error) {
      console.error('Interaction tracking error:', error);
    }
  }

  private async handleHeartbeat(socket: AuthenticatedSocket) {
    if (!socket.userId) return;

    try {
      await LiveSystemService.updateConnectionActivity(socket.id);
      socket.emit('system:heartbeat_ack', { timestamp: new Date() });

    } catch (error) {
      console.error('Heartbeat error:', error);
    }
  }

  private async handlePresenceUpdate(socket: AuthenticatedSocket, data: any) {
    if (!socket.userId) return;

    try {
      const { status, activity } = data;

      this.broadcastPresenceUpdate(socket.userId, status, socket.userRole, activity);

    } catch (error) {
      console.error('Presence update error:', error);
    }
  }

  private async handleDisconnect(socket: AuthenticatedSocket) {
    console.log(`WebSocket disconnected: ${socket.id}`);

    if (socket.userId) {
      // Remove from tracking
      this.connectedUsers.delete(socket.id);

      const userSocketsSet = this.userSockets.get(socket.userId);
      if (userSocketsSet) {
        userSocketsSet.delete(socket.id);
        if (userSocketsSet.size === 0) {
          this.userSockets.delete(socket.userId);
          // User is completely offline
          this.broadcastPresenceUpdate(socket.userId, 'offline', socket.userRole);
        }
      }

      // Update database connection status
      await LiveSystemService.disconnectConnection(socket.id);
    }
  }

  private broadcastPresenceUpdate(userId: number, status: string, role?: string, activity?: string) {
    const presenceData = {
      userId,
      status,
      role,
      activity,
      timestamp: new Date(),
    };

    // Broadcast to role-specific rooms
    if (role) {
      this.io.to(`role_${role}`).emit('presence:update', presenceData);
    }

    // Broadcast to specific user connections (for multi-device scenarios)
    const userSockets = this.userSockets.get(userId);
    if (userSockets) {
      userSockets.forEach(socketId => {
        this.io.to(socketId).emit('presence:update', presenceData);
      });
    }
  }

  private broadcastLocationUpdate(location: any) {
    const locationData = {
      userId: location.userId,
      latitude: location.latitude,
      longitude: location.longitude,
      timestamp: location.timestamp,
      trackingType: location.trackingType,
    };

    // Broadcast based on sharing level
    switch (location.sharingLevel) {
      case 'PUBLIC':
        this.io.emit('location:update', locationData);
        break;

      case 'CUSTOMERS_ONLY':
        this.io.to('role_CONSUMER').emit('location:update', locationData);
        break;

      case 'MERCHANTS_ONLY':
        this.io.to('role_MERCHANT').emit('location:update', locationData);
        break;

      case 'PRIVATE':
        // Only send to user's own connections
        const userSockets = this.userSockets.get(location.userId);
        if (userSockets) {
          userSockets.forEach(socketId => {
            this.io.to(socketId).emit('location:update', locationData);
          });
        }
        break;
    }
  }

  // Public methods for external services to use
  public broadcastToUser(userId: number, event: string, data: any) {
    const userSockets = this.userSockets.get(userId);
    if (userSockets) {
      userSockets.forEach(socketId => {
        this.io.to(socketId).emit(event, data);
      });
    }
  }

  public broadcastToRole(role: string, event: string, data: any) {
    this.io.to(`role_${role}`).emit(event, data);
  }

  public getConnectedUsers(): number {
    return this.connectedUsers.size;
  }

  public getUsersByRole(role: string): number {
    let count = 0;
    this.connectedUsers.forEach(socket => {
      if (socket.userRole === role) count++;
    });
    return count;
  }
}

