interface KycDocument {
  id: string;
  userId: string;
  userName: string;
  userEmail: string;
  documentType: 'ID_CARD' | 'PASSPORT' | 'DRIVER_LICENSE' | 'UTILITY_BILL';
  documentUrl: string;
  status: 'PENDING' | 'APPROVED' | 'REJECTED';
  submittedAt: string;
  reviewedAt?: string;
  reviewedBy?: string;
  rejectionReason?: string;
}

interface KycReviewModalProps {
  document: KycDocument;
  isOpen: boolean;
  onClose: () => void;
  onReview: (id: string, action: 'approve' | 'reject', reason?: string) => Promise<void>;
}

export function KycReviewModal({ document, isOpen, onClose, onReview }: KycReviewModalProps) {
  const [reason, setReason] = useState('');
  const [processing, setProcessing] = useState(false);

  const handleReview = async (action: 'approve' | 'reject') => {
    setProcessing(true);
    try {
      await onReview(document.id, action, reason);
      onClose();
    } catch (error) {
      console.error('Review failed:', error);
    } finally {
      setProcessing(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <div className="p-6 max-w-2xl">
        <h2 className="text-xl font-bold mb-4">Review KYC Document</h2>

        <div className="mb-4">
          <p><strong>User:</strong> {document.userName} ({document.userEmail})</p>
          <p><strong>Document Type:</strong> {document.documentType}</p>
          <p><strong>Submitted:</strong> {new Date(document.submittedAt).toLocaleDateString()}</p>
        </div>

        <div className="mb-4">
          <img 
            src={document.documentUrl} 
            alt="KYC Document" 
            className="max-w-full h-auto border rounded"
          />
        </div>

        <div className="mb-4">
          <label className="block text-sm font-medium mb-2">
            Review Notes (required for rejection)
          </label>
          <Textarea
            value={reason}
            onChange={(e) => setReason(e.target.value)}
            placeholder="Enter review notes..."
            rows={4}
          />
        </div>

        <div className="flex gap-3">
          <Button
            onClick={() => handleReview('approve')}
            disabled={processing}
            className="bg-green-600 hover:bg-green-700"
          >
            Approve
          </Button>
          <Button
            onClick={() => handleReview('reject')}
            disabled={processing || !reason.trim()}
            variant="destructive"
          >
            Reject
          </Button>
          <Button variant="outline" onClick={onClose}>
            Cancel
          </Button>
        </div>
      </div>
    </Dialog>
  );
}

client/src/hooks/use-auth.tsx.content:
import { useState, useEffect, createContext, useContext } from 'react'

interface User {
  id: string
  email: string
  fullName: string
  role: 'CONSUMER' | 'MERCHANT' | 'DRIVER' | 'ADMIN'
  name?: string
  profileImageUrl?: string
}

interface AuthContextType {
  user: User | null
  setUser: (user: User | null) => void
  login: (email: string, password: string) => Promise<void>
  logout: () => void
  signup: (email: string, password: string, role: string) => Promise<void>
  isLoading: boolean
  refreshUser: () => Promise<void>;
  updateUser: (userData: Partial<User>) => Promise<void>;
  error: string | null;
  clearError: () => void;
  isAuthenticated: () => boolean;
}

const AuthContext = createContext<AuthContextType | null>(null)

// Simple fetch wrapper for API calls
const apiRequest = async (endpoint: string, options: RequestInit = {}) => {
  const response = await fetch(`/api${endpoint}`, {
    credentials: 'include',
    headers: {
      'Content-Type': 'application/json',
      ...options.headers,
    },
    ...options,
  });

  const data = await response.json();

  return {
    success: response.ok,
    data: response.ok ? data : null,
    error: response.ok ? null : data.message || 'Request failed',
    user: data.user,
  };
};


export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<User | null>(null)
  const [isLoading, setIsLoading] = useState(true)
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false); // Renamed from isLoading to loading for clarity if needed, but keeping original name for now

  useEffect(() => {
    // Check for existing session without blocking the UI
    const storedUser = localStorage.getItem('user')
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser))
      } catch (error) {
        console.error('Error parsing stored user:', error)
        localStorage.removeItem('user')
      }
    }
    // Always set loading to false immediately to allow app to render
    setIsLoading(false)
  }, [])

  const login = async (email: string, password: string) => {
    setLoading(true)
    setError(null);
    try {
      const response = await apiRequest('/auth/signin', {
        method: 'POST',
        body: JSON.stringify({ email, password })
      });

      if (!response.success) {
        throw new Error(response.error || 'Login failed');
      }

      const data = response;
      setUser(data.data?.user || data.user);
      localStorage.setItem('user', JSON.stringify(data.data?.user || data.user));
    } catch (err: any) {
      setError(err.message);
      console.error('Login error:', err);
    } finally {
      setLoading(false);
    }
  }

  const signup = async (email: string, password: string, role: string) => {
    setLoading(true);
    setError(null);
    try {
      const response = await apiRequest('/auth/signup', {
        method: 'POST',
        body: JSON.stringify({ email, password, role })
      });

      if (!response.success) {
        throw new Error(response.error || 'Signup failed');
      }

      const data = response;
      setUser(data.data?.user || data.user);
      localStorage.setItem('user', JSON.stringify(data.data?.user || data.user));
    } catch (err: any) {
      setError(err.message);
      console.error('Signup error:', err);
    } finally {
      setLoading(false);
    }
  }

  const logout = () => {
    setUser(null)
    localStorage.removeItem('user')
  }

  const refreshUser = async (): Promise<void> => {
    try {
      const response = await apiRequest("/auth/me");
      if (response.success) {
        setUser(response.data?.user || response.user);
      } else {
        // If /auth/me fails, it's likely due to an expired token or invalid session
        setUser(null);
        localStorage.removeItem('user');
        setError(response.error || 'Session expired. Please log in again.');
      }
    } catch (err: any) {
      console.error('Failed to refresh user:', err);
      setUser(null);
      localStorage.removeItem('user');
      setError(err.message || 'An error occurred while refreshing user data.');
    }
  };

  const updateUser = async (userData: Partial<User>): Promise<void> => {
    if (!user) return;
    setLoading(true);
    setError(null);
    try {
      const response = await apiRequest(`/users/${user.id}`, {
        method: 'PUT',
        body: JSON.stringify(userData),
      });

      if (!response.success) {
        throw new Error(response.error || 'Failed to update user');
      }

      const updatedUserData = response;
      setUser(updatedUserData.data?.user || updatedUserData.user);
      localStorage.setItem('user', JSON.stringify(updatedUserData.data?.user || updatedUserData.user));
    } catch (err: any) {
      setError(err.message);
      console.error('Update user error:', err);
    } finally {
      setLoading(false);
    }
  };

  const clearError = () => {
    setError(null);
  };

  const isAuthenticated = () => !!user;


  return (
    <AuthContext.Provider value={{ user, setUser, login, logout, signup, isLoading: isLoading || loading, refreshUser, updateUser, error, clearError, isAuthenticated }}>
      {children}
    </AuthContext.Provider>
  )
}

export function useAuth() {
  const context = useContext(AuthContext)
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider')
  }
  return context
}

