            <path d="M25.326 5.495c0 .907-.26 1.62-.778 2.139-.651.651-1.575 1.037-2.678 1.037-1.103 0-2.027-.386-2.678-1.037-.519-.519-.778-1.232-.778-2.139s.26-1.62.778-2.139c.651-.651 1.575-1.037 2.678-1.037 1.103 0 2.027.386 2.678 1.037.519.519.778 1.232.778 2.139z" fill="#EA4335"/>
            <path d="M46.045 5.495c0 .907-.26 1.62-.778 2.139-.651.651-1.575 1.037-2.678 1.037-1.103 0-2.027-.386-2.678-1.037-.519-.519-.778-1.232-.778-2.139s.26-1.62.778-2.139c.651-.651 1.575-1.037 2.678-1.037 1.103 0 2.027.386 2.678 1.037.519.519.778 1.232.778 2.139z" fill="#4285F4"/>
            <path d="M35.686 5.495c0 .907-.26 1.62-.778 2.139-.651.651-1.575 1.037-2.678 1.037-1.103 0-2.027-.386-2.678-1.037-.519-.519-.778-1.232-.778-2.139s.26-1.62.778-2.139c.651-.651 1.575-1.037 2.678-1.037 1.103 0 2.027.386 2.678 1.037.519.519.778 1.232.778 2.139z" fill="#34A853"/>
            <path d="M56.405 5.495c0 .907-.26 1.62-.778 2.139-.651.651-1.575 1.037-2.678 1.037-1.103 0-2.027-.386-2.678-1.037-.519-.519-.778-1.232-.778-2.139s.26-1.62.778-2.139c.651-.651 1.575-1.037 2.678-1.037 1.103 0 2.027.386 2.678 1.037.519.519.778 1.232.778 2.139z" fill="#FBBC04"/>
          </g>
        </svg>
      )
    },
    {
      id: "paypal",
      name: "PayPal",
      icon: (
        <svg width="20" height="24" viewBox="0 0 34 40" className="w-5 h-6">
          <path d="M28.2524 3.65072C26.4394 1.5924 23.1619 0.709961 18.9693 0.709961H6.80085C5.94305 0.709961 5.21413 1.33134 5.07983 2.17387L0.013153 34.1802C-0.087574 34.8113 0.403064 35.3831 1.04533 35.3831H8.55761L10.4443 23.4636L10.3859 23.8369C10.5202 22.9944 11.2437 22.373 12.1004 22.373H15.6702C22.6832 22.373 28.1745 19.5358 29.7785 11.3284C29.8262 11.0857 29.8673 10.8494 29.9031 10.6186C29.7005 10.5118 29.7005 10.5118 29.9031 10.6186C30.3807 7.58504 29.8998 5.52025 28.2524 3.65072Z" fill="#003087"/>
          <path d="M13.3189 9.52577C13.5192 9.43083 13.7423 9.37797 13.9763 9.37797H23.5161C24.6458 9.37797 25.6996 9.45133 26.6625 9.6056C26.9387 9.64983 27.2062 9.70053 27.4661 9.75878C27.7261 9.81596 27.9784 9.88069 28.2232 9.95189C28.3456 9.98749 28.4658 10.0242 28.5839 10.063C29.0572 10.2205 29.498 10.4039 29.9031 10.6186C30.3807 7.58396 29.8998 5.52025 28.2524 3.65072C26.4383 1.5924 23.1619 0.709961 18.9693 0.709961H6.79977C5.94305 0.709961 5.21413 1.33134 5.07983 2.17387L0.013153 34.1802C-0.087574 34.8113 0.403064 35.3831 1.04533 35.3831H8.55761L10.4443 23.4636L10.3859 23.8369C10.5202 22.9944 11.2437 22.373 12.1004 22.373H15.6702C22.6832 22.373 28.1745 19.5358 29.7785 11.3284C29.8262 11.0857 29.8673 10.8494 29.9031 10.6186C29.7005 10.5118 29.7005 10.5118 29.9031 10.6186C30.3807 7.58504 29.8998 5.52025 28.2524 3.65072Z" fill="#0070E0"/>
        </svg>
      )
    },
    {
      id: "bank_transfer",
      name: "Bank Transfer",
      icon: <Building2 className="w-5 h-5 text-[#4682b4]" />
    }
  ];

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Here you would typically save the payment method
    console.log("Adding payment method:", {
      type: selectedType,
      cardNumber,
      expiryDate,
      cvv,
      cardholderName,
      isDefault
    });
    // Navigate back to payment methods
    setLocation("/payment-methods");
  };

  const formatCardNumber = (value: string) => {
    // Remove all non-digit characters
    const cleaned = value.replace(/\D/g, '');
    // Add spaces every 4 digits
    const formatted = cleaned.replace(/(\d{4})(?=\d)/g, '$1 ');
    return formatted;
  };

  const formatExpiryDate = (value: string) => {
    // Remove all non-digit characters
    const cleaned = value.replace(/\D/g, '');
    // Add slash after 2 digits
    if (cleaned.length >= 2) {
      return cleaned.substring(0, 2) + '/' + cleaned.substring(2, 4);
    }
    return cleaned;
  };

  return (
    <div className="min-h-screen bg-gray-50 w-full max-w-sm sm:max-w-md md:max-w-lg lg:max-w-xl xl:max-w-2xl mx-auto px-2 sm:px-4">{/*Responsive container*/}
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="flex items-center justify-between p-4">
          <div className="flex items-center space-x-3">
            <Button 
              variant="ghost" 
              size="icon" 
              onClick={() => setLocation("/payment-methods")}
            >
              <ArrowLeft className="h-5 w-5" />
            </Button>
            <h1 className="text-xl font-semibold text-[#131313]">
              Add Payment Method
            </h1>
          </div>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="p-4 space-y-6">
        {/* Payment Type Selection */}
        <Card className="border-2 border-blue-100">
          <CardContent className="p-4">
            <Label className="text-base font-medium text-[#131313] mb-4 block">
              Select Payment Type
            </Label>
            <div className="grid grid-cols-2 gap-3">
              {paymentTypes.map((type) => (
                <div
                  key={type.id}
                  className={`p-3 rounded-xl border-2 cursor-pointer transition-all ${
                    selectedType === type.id
                      ? "border-[#4682b4] bg-blue-50"
                      : "border-gray-200 hover:border-blue-200"
                  }`}
                  onClick={() => setSelectedType(type.id)}
                >
                  <div className="flex items-center space-x-2">
                    {type.icon}
                    <span className="text-sm font-medium text-[#131313]">
                      {type.name}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Card Details Form */}
        {selectedType && selectedType !== "bank_transfer" && (
          <Card className="border-2 border-blue-100">
            <CardContent className="p-4 space-y-4">
              <Label className="text-base font-medium text-[#131313] block">
                Payment Details
              </Label>

              {selectedType !== "paypal" && selectedType !== "apple_pay" && selectedType !== "google_pay" && (
                <>
                  <div>
                    <Label htmlFor="cardNumber" className="text-sm text-gray-700">
                      Card Number
                    </Label>
                    <Input
                      id="cardNumber"
                      type="text"
                      placeholder="1234 5678 9012 3456"
                      value={formatCardNumber(cardNumber)}
                      onChange={(e) => setCardNumber(e.target.value.replace(/\s/g, ''))}
                      maxLength={19}
                      className="mt-1"
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="expiryDate" className="text-sm text-gray-700">
                        Expiry Date
                      </Label>
                      <Input
                        id="expiryDate"
                        type="text"
                        placeholder="MM/YY"
                        value={formatExpiryDate(expiryDate)}
                        onChange={(e) => setExpiryDate(e.target.value)}
                        maxLength={5}
                        className="mt-1"
                      />
                    </div>

                    <div>
                      <Label htmlFor="cvv" className="text-sm text-gray-700">
                        CVV
                      </Label>
                      <Input
                        id="cvv"
                        type="text"
                        placeholder="123"
                        value={cvv}
                        onChange={(e) => setCvv(e.target.value.replace(/\D/g, ''))}
                        maxLength={4}
                        className="mt-1"
                      />
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="cardholderName" className="text-sm text-gray-700">
                      Cardholder Name
                    </Label>
                    <Input
                      id="cardholderName"
                      type="text"
                      placeholder="John Doe"
                      value={cardholderName}
                      onChange={(e) => setCardholderName(e.target.value)}
                      className="mt-1"
                    />
                  </div>
                </>
              )}

              {(selectedType === "paypal" || selectedType === "apple_pay" || selectedType === "google_pay") && (
                <div className="text-center py-8">
                  <p className="text-gray-600">
                    You will be redirected to {paymentTypes.find(t => t.id === selectedType)?.name} to complete the setup.
                  </p>
                </div>
              )}
            </CardContent>
          </Card>
        )}

        {/* Bank Transfer Form */}
        {selectedType === "bank_transfer" && (
          <Card className="border-2 border-blue-100">
            <CardContent className="p-4 space-y-4">
              <Label className="text-base font-medium text-[#131313] block">
                Bank Details
              </Label>

              <div>
                <Label htmlFor="bankName" className="text-sm text-gray-700">
                  Bank Name
                </Label>
                <Input
                  id="bankName"
                  type="text"
                  placeholder="First Bank of Nigeria"
                  className="mt-1"
                />
              </div>

              <div>
                <Label htmlFor="accountNumber" className="text-sm text-gray-700">
                  Account Number
                </Label>
                <Input
                  id="accountNumber"
                  type="text"
                  placeholder="1234567890"
                  className="mt-1"
                />
              </div>

              <div>
                <Label htmlFor="accountName" className="text-sm text-gray-700">
                  Account Name
                </Label>
                <Input
                  id="accountName"
                  type="text"
                  placeholder="John Doe"
                  className="mt-1"
                />
              </div>
            </CardContent>
          </Card>
        )}

        {/* Default Payment Method Toggle */}
        {selectedType && (
          <Card className="border-2 border-blue-100">
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <Label className="text-base font-medium text-[#131313]">
                    Set as Default
                  </Label>
                  <p className="text-sm text-gray-600 mt-1">
                    Use this as your primary payment method
                  </p>
                </div>
                <Switch
                  checked={isDefault}
                  onCheckedChange={setIsDefault}
                />
              </div>
            </CardContent>
          </Card>
        )}

        {/* Submit Button */}
        <Button
          type="submit"
          disabled={!selectedType}
          className="w-full bg-[#4682b4] hover:bg-[#010e42] text-white rounded-2xl py-4 text-base font-medium transition-colors"
        >
          Add Payment Method
        </Button>
      </form>
    </div>
  );
}

client/src/pages/admin-control-center.ts.content:
import React, { useState, useEffect } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import {
  Shield, Users, DollarSign, AlertTriangle, TrendingUp, 
  Eye, CheckCircle, XCircle, Clock, Activity, FileText,
  Gavel, RefreshCw, Bell, BarChart3, Settings
} from 'lucide-react';

interface SystemMetrics {
  platform: {
    totalUsers: number;
    activeUsers: number;
    onlineDrivers: number;
    activeMerchants: number;
    systemUptime: number;
    serverHealth: string;
  };
  transactions: {
    totalTransactions: number;
    todayTransactions: number;
    pendingTransactions: number;
    disputedTransactions: number;
    totalVolume: number;
    escrowBalance: number;
  };
  security: {
    fraudAlerts: number;
    suspiciousActivities: number;
    blockedUsers: number;
    securityIncidents: number;
  };
}

interface EscrowOverview {
  totalBalance: number;
  pendingReleases: number;
  disputedAmount: number;
  releasedToday: number;
  transactions: {
    pending: number;
    disputed: number;
    readyForRelease: number;
  };
  analytics: {
    averageHoldTime: number;
    releaseRate: number;
    disputeRate: number;
  };
}

export default function AdminControlCenter() {
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedDispute, setSelectedDispute] = useState<any>(null);
  const [showDisputeModal, setShowDisputeModal] = useState(false);
  const { toast } = useToast();

  // Real-time system metrics
  const { data: systemMetrics, refetch: refetchMetrics } = useQuery({
    queryKey: ['/api/admin/system-metrics'],
    refetchInterval: 30000 // Refresh every 30 seconds
  });

  // Escrow overview
  const { data: escrowOverview } = useQuery({
    queryKey: ['/api/admin/escrow-overview'],
    refetchInterval: 60000
  });

  // Pending verifications
  const { data: pendingVerifications } = useQuery({
    queryKey: ['/api/admin/pending-verifications'],
    refetchInterval: 30000
  });

  // Active disputes
  const { data: disputes } = useQuery({
    queryKey: ['/api/admin/disputes'],
    refetchInterval: 30000
  });

  // Platform analytics
  const { data: analytics } = useQuery({
    queryKey: ['/api/admin/analytics', { timeframe: 'week' }],
    refetchInterval: 300000 // 5 minutes
  });

  // Resolve dispute mutation
  const resolveDisputeMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", `/api/admin/disputes/${data.disputeId}/resolve`, data);
    },
    onSuccess: () => {
      toast({ title: "Dispute resolved successfully" });
      setShowDisputeModal(false);
      setSelectedDispute(null);
    }
  });

  // Manual escrow action mutation
  const manualEscrowMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/admin/escrow/manual-action", data);
    },
    onSuccess: () => {
      toast({ title: "Manual action completed successfully" });
    }
  });

  // Format currency
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-NG', {
      style: 'currency',
      currency: 'NGN'
    }).format(amount);
  };

  const formatUptime = (seconds: number) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    return `${hours}h ${minutes}m`;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50 p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Admin Control Center</h1>
            <p className="text-gray-600">Platform oversight and management dashboard</p>
          </div>
          <div className="flex items-center space-x-4">
            <Badge variant="outline" className="px-3 py-1">
              <Activity className="w-4 h-4 mr-2" />
              System Online
            </Badge>
            <Button onClick={() => refetchMetrics()} variant="outline" size="sm">
              <RefreshCw className="w-4 h-4 mr-2" />
              Refresh
            </Button>
          </div>
        </div>

        {/* System Status Cards */}
        {systemMetrics && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Active Users</CardTitle>
                <Users className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{systemMetrics.platform.activeUsers.toLocaleString()}</div>
                <p className="text-xs text-muted-foreground">
                  {systemMetrics.platform.onlineDrivers} drivers online
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Escrow Balance</CardTitle>
                <DollarSign className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{formatCurrency(systemMetrics.transactions.escrowBalance)}</div>
                <p className="text-xs text-muted-foreground">
                  {systemMetrics.transactions.pendingTransactions} pending
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Security Alerts</CardTitle>
                <AlertTriangle className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{systemMetrics.security.fraudAlerts}</div>
                <p className="text-xs text-muted-foreground">
                  {systemMetrics.security.securityIncidents} incidents today
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">System Health</CardTitle>
                <Activity className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-green-600">{systemMetrics.platform.serverHealth}</div>
                <p className="text-xs text-muted-foreground">
                  Uptime: {formatUptime(systemMetrics.platform.systemUptime)}
                </p>
              </CardContent>
            </Card>
          </div>
        )}

        {/* Main Dashboard Tabs */}
        <Tabs value={activeTab} onValueChange={setActiveTab}>
          <TabsList className="grid w-full grid-cols-6">
            <TabsTrigger value="overview">Overview</TabsTrigger>
            <TabsTrigger value="escrow">Escrow</TabsTrigger>
            <TabsTrigger value="disputes">Disputes</TabsTrigger>
            <TabsTrigger value="verifications">Verifications</TabsTrigger>
            <TabsTrigger value="analytics">Analytics</TabsTrigger>
            <TabsTrigger value="content">Content</TabsTrigger>
          </TabsList>

          {/* Overview Tab */}
          <TabsContent value="overview" className="space-y-6">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Real-time Activity */}
              <Card>
                <CardHeader>
                  <CardTitle>Real-time Activity</CardTitle>
                  <CardDescription>Live platform monitoring</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex justify-between items-center">
                      <span>Today's Transactions</span>
                      <Badge>{systemMetrics?.transactions.todayTransactions}</Badge>
                    </div>
                    <div className="flex justify-between items-center">
                      <span>Active Merchants</span>
                      <Badge variant="secondary">{systemMetrics?.platform.activeMerchants}</Badge>
                    </div>
                    <div className="flex justify-between items-center">
                      <span>Online Drivers</span>
                      <Badge variant="outline">{systemMetrics?.platform.onlineDrivers}</Badge>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Quick Actions */}
              <Card>
                <CardHeader>
                  <CardTitle>Quick Actions</CardTitle>
                  <CardDescription>Administrative controls</CardDescription>
                </CardHeader>
                <CardContent className="space-y-3">
                  <Button className="w-full justify-start" variant="outline">
                    <Bell className="w-4 h-4 mr-2" />
                    Send Platform Announcement
                  </Button>
                  <Button className="w-full justify-start" variant="outline">
                    <Settings className="w-4 h-4 mr-2" />
                    System Maintenance Mode
                  </Button>
                  <Button className="w-full justify-start" variant="outline">
                    <FileText className="w-4 h-4 mr-2" />
                    Generate Reports
                  </Button>
                </CardContent>
              </Card>
            </div>
          </TabsContent>

          {/* Escrow Management Tab */}
          <TabsContent value="escrow" className="space-y-6">
            {escrowOverview && (
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <Card>
                  <CardHeader>
                    <CardTitle>Total Escrow Balance</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="text-3xl font-bold text-blue-600">
                      {formatCurrency(escrowOverview.totalBalance)}
                    </div>
                    <p className="text-sm text-muted-foreground mt-2">
                      {formatCurrency(escrowOverview.releasedToday)} released today
                    </p>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader>
                    <CardTitle>Pending Releases</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="text-3xl font-bold text-orange-600">
                      {formatCurrency(escrowOverview.pendingReleases)}
                    </div>
                    <p className="text-sm text-muted-foreground mt-2">
                      {escrowOverview.transactions.readyForRelease} ready for release
                    </p>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader>
                    <CardTitle>Disputed Amount</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="text-3xl font-bold text-red-600">
                      {formatCurrency(escrowOverview.disputedAmount)}
                    </div>
                    <p className="text-sm text-muted-foreground mt-2">
                      {escrowOverview.transactions.disputed} active disputes
                    </p>
                  </CardContent>
                </Card>
              </div>
            )}
          </TabsContent>

          {/* Disputes Tab */}
          <TabsContent value="disputes" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Active Disputes</CardTitle>
                <CardDescription>Disputes requiring admin resolution</CardDescription>
              </CardHeader>
              <CardContent>
                {disputes?.disputes?.map((dispute: any) => (
                  <div key={dispute.id} className="border rounded-lg p-4 mb-4">
                    <div className="flex justify-between items-start">
                      <div>
                        <h4 className="font-semibold">{dispute.disputeType.replace('_', ' ').toUpperCase()}</h4>
                        <p className="text-sm text-gray-600 mt-1">{dispute.description}</p>
                        <div className="flex items-center space-x-4 mt-2">
                          <Badge variant={dispute.priority === 'HIGH' ? 'destructive' : 'secondary'}>
                            {dispute.priority}
                          </Badge>
                          <span className="text-sm text-gray-500">
                            {formatCurrency(dispute.transactionAmount)}
                          </span>
                        </div>
                      </div>
                      <Button 
                        onClick={() => {
                          setSelectedDispute(dispute);
                          setShowDisputeModal(true);
                        }}
                        size="sm"
                      >
                        <Gavel className="w-4 h-4 mr-2" />
                        Resolve
                      </Button>
                    </div>
                  </div>
                )) || <p>No active disputes</p>}
              </CardContent>
            </Card>
          </TabsContent>

          {/* Analytics Tab */}
          <TabsContent value="analytics" className="space-y-6">
            {analytics && (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <Card>
                  <CardHeader>
                    <CardTitle>Financial Analytics</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div className="flex justify-between">
                      <span>Total Revenue</span>
                      <span className="font-semibold">{formatCurrency(analytics.analytics.financial.revenueGrowth)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Revenue Growth</span>
                      <span className="font-semibold text-green-600">+{analytics.analytics.financial.revenueGrowth}%</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Escrow Turnover</span>
                      <span className="font-semibold">{analytics.analytics.financial.escrowTurnover} days</span>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader>
                    <CardTitle>User Behavior</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div className="flex justify-between">
                      <span>Daily Active Users</span>
                      <span className="font-semibold">{analytics.analytics.userBehavior.dailyActiveUsers.toLocaleString()}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Conversion Rate</span>
                      <span className="font-semibold">{analytics.analytics.userBehavior.conversionRate}%</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Retention Rate</span>
                      <span className="font-semibold">{analytics.analytics.userBehavior.retentionRate}%</span>
                    </div>
                  </CardContent>
                </Card>
              </div>
            )}
          </TabsContent>
        </Tabs>

        {/* Dispute Resolution Modal */}
        <Dialog open={showDisputeModal} onOpenChange={setShowDisputeModal}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Resolve Dispute</DialogTitle>
            </DialogHeader>
            {selectedDispute && (
              <div className="space-y-4">
                <div>
                  <h4 className="font-semibold">Dispute Details</h4>
                  <p className="text-sm text-gray-600">{selectedDispute.description}</p>
                  <p className="text-sm mt-2">Amount: {formatCurrency(selectedDispute.transactionAmount)}</p>
                </div>

                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium">Resolution</label>
                    <Select>
                      <SelectTrigger>
                        <SelectValue placeholder="Select resolution" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="approve_customer">Approve Customer</SelectItem>
                        <SelectItem value="approve_merchant">Approve Merchant</SelectItem>
                        <SelectItem value="partial_refund">Partial Refund</SelectItem>
                        <SelectItem value="no_action">No Action</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <label className="text-sm font-medium">Admin Notes</label>
                    <Textarea placeholder="Enter resolution notes..." />
                  </div>

                  <div className="flex justify-end space-x-2">
                    <Button variant="outline" onClick={() => setShowDisputeModal(false)}>
                      Cancel
                    </Button>
                    <Button onClick={() => resolveDisputeMutation.mutate({
                      disputeId: selectedDispute.id,
                      resolution: 'approve_customer',
                      adminNotes: 'Resolved in favor of customer',
                      evidenceReviewed: true
                    })}>
                      Resolve Dispute
                    </Button>
                  </div>
                </div>
              </div>
            )}
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

client/src/pages/admin-dashboard.tsx.content:
import React, { useState } from 'react';
import { useAdmin } from '../lib/admin-auth';
import { AdminLayout } from '../components/admin-layout';
import { AdminDashboardMain } from '../components/admin-dashboard-main';
import { AdminUserManagement } from './admin-user-management';
import { AdminKYCVerification } from './admin-kyc-verification';
import { AdminTransactions } from './admin-transactions';
import { AdminMonitoring } from './admin-monitoring';
import { AdminFraud } from './admin-fraud';
import { AdminSupport } from './admin-support';
import { AdminEscrowManagement } from './admin-escrow-management';
import { AdminModeration } from './admin-moderation';

type AdminPageType = 'dashboard' | 'users' | 'kyc' | 'escrow' | 'transactions' | 'support' | 'analytics' | 'security' | 'monitoring' | 'fraud' | 'moderation';

function AdminDashboard() {
  const { user, isAuthenticated, isLoading } = useAdmin();
  const [currentPage, setCurrentPage] = useState<AdminPageType>('dashboard');

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading admin dashboard...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    // This should not happen as the route is protected, but just in case
    window.location.href = '/admin';
    return null;
  }

  const renderPage = () => {
    switch (currentPage) {
      case 'dashboard':
        return <AdminDashboardMain />;
      case 'users':
        return <AdminUserManagement />;
      case 'kyc':
        return <AdminKYCVerification />;
      case 'transactions':
        return <AdminTransactions />;
      case 'monitoring':
        return <AdminMonitoring />;
      case 'fraud':
        return <AdminFraud />;
      case 'support':
        return <AdminSupport />;
      case 'escrow':
        return <AdminEscrowManagement />;
      case 'moderation':
        return <AdminModeration />;
      case 'analytics':
        return (
          <div className="p-6">
            <h1 className="text-2xl font-bold text-gray-900">Platform Analytics</h1>
            <p className="text-gray-600 mt-2">Advanced analytics and reporting tools coming soon...</p>
          </div>
        );
      case 'security':
        return (
          <div className="p-6">
            <h1 className="text-2xl font-bold text-gray-900">Security Center</h1>
            <p className="text-gray-600 mt-2">Security monitoring and threat detection tools coming soon...</p>
          </div>
        );
      default:
        return <AdminDashboardMain />;
    }
  };

  const handlePageChange = (page: string) => {
    setCurrentPage(page as AdminPageType);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <AdminLayout currentPage={currentPage} onPageChange={handlePageChange}>
        {renderPage()}
      </AdminLayout>
    </div>
  );
}

export default AdminDashboard;

