import { useState } from "react";
import { ArrowLeft, Save, MapPin, Phone, Mail, User } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useAuth } from "@/hooks/use-auth";
import { useLocation } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { NotificationModal } from "@/components/ui/notification-modal";
import { LoadingButton } from "@/components/ui/loading-button";
import { ImagePicker } from "@/components/ui/image-picker";
import accountCircleIcon from "../assets/images/account_circle.svg";
import cameraIcon from "../assets/images/camera_icon.png";

interface ProfileFormData {
  fullName: string;
  email: string;
  phone: string;
  address: string;
  city: string;
  state: string;
  country: string;
  bio: string;
  profilePicture?: File | null;
}

const nigerianStates = [
  "Abia", "Adamawa", "Akwa Ibom", "Anambra", "Bauchi", "Bayelsa", "Benue", "Borno",
  "Cross River", "Delta", "Ebonyi", "Edo", "Ekiti", "Enugu", "FCT", "Gombe", "Imo",
  "Jigawa", "Kaduna", "Kano", "Katsina", "Kebbi", "Kogi", "Kwara", "Lagos", "Nasarawa",
  "Niger", "Ogun", "Ondo", "Osun", "Oyo", "Plateau", "Rivers", "Sokoto", "Taraba",
  "Yobe", "Zamfara"
];

export default function EditProfilePage() {
  const { user, updateUser } = useAuth();
  const [, setLocation] = useLocation();

  const [formData, setFormData] = useState<ProfileFormData>({
    fullName: user?.fullName || "",
    email: user?.email || "",
    phone: user?.phone || "",
    address: user?.address || "",
    city: user?.city || "",
    state: user?.state || "",
    country: user?.country || "Nigeria",
    bio: user?.bio || "",
    profilePicture: null,
  });

  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showErrorModal, setShowErrorModal] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const updateProfileMutation = useMutation({
    mutationFn: async (data: ProfileFormData) => {
      // Simulate API call with profile picture handling
      if (data.profilePicture) {
        // In a real app, you would upload the image to a storage service
        // For now, we'll create a data URL for preview
        const reader = new FileReader();
        return new Promise((resolve) => {
          reader.onload = () => {
            const imageUrl = reader.result as string;
            setTimeout(() => resolve({ success: true, imageUrl }), 1500);
          };
          reader.readAsDataURL(data.profilePicture);
        });
      } else {
        return new Promise((resolve) => {
          setTimeout(() => resolve({ success: true }), 1500);
        });
      }
    },
    onSuccess: (result: any) => {
      // Update user in auth context with new profile data
      const updatedProfile: any = {
        fullName: formData.fullName,
        email: formData.email,
        phone: formData.phone,
        address: formData.address,
        city: formData.city,
        state: formData.state,
        country: formData.country,
        bio: formData.bio,
      };

      if (result.imageUrl) {
        updatedProfile.profilePicture = result.imageUrl;
      }

      updateUser(updatedProfile);
      setShowSuccessModal(true);
    },
    onError: (error: any) => {
      setErrorMessage(error.message || "Failed to update profile. Please try again.");
      setShowErrorModal(true);
    },
  });

  const handleInputChange = (field: keyof ProfileFormData, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateProfileMutation.mutate(formData);
  };



  return (
    <div className="w-full max-w-sm sm:max-w-md md:max-w-lg lg:max-w-xl xl:max-w-2xl mx-auto min-h-screen bg-white px-2 sm:px-4">{/*Responsive container*/}
      {/* Header */}
      <div className="flex items-center justify-between p-6 pt-12 border-b border-gray-100">
        <Button
          variant="ghost"
          size="sm"
          onClick={() => setLocation("/profile")}
          className="p-2"
        >
          <ArrowLeft className="h-5 w-5 text-[var(--brill-primary)]" />
        </Button>
        <h1 className="text-lg font-bold text-[var(--brill-primary)]">Edit Profile</h1>
        <div className="w-9"></div>
      </div>

      <form onSubmit={handleSubmit} className="px-6 py-8">
        {/* Profile Picture */}
        <div className="text-center mb-8">
          <div className="relative inline-block">
            <div 
              className="w-24 h-24 rounded-full mx-auto mb-2 cursor-pointer transition-transform duration-200 hover:scale-105 overflow-hidden bg-gray-100 flex items-center justify-center"
              onClick={() => document.getElementById('profile-image-input')?.click()}
            >
              {user?.profilePicture ? (
                <img 
                  src={user.profilePicture} 
                  alt="Profile" 
                  className="w-full h-full object-cover"
                />
              ) : (
                <img 
                  src={accountCircleIcon} 
                  alt="Default Profile" 
                  className="w-full h-full object-cover"
                />
              )}
              {/* Camera Icon */}
              <div className="absolute bottom-0 right-0 w-7 h-7 bg-[var(--brill-secondary)] rounded-full flex items-center justify-center border-2 border-white shadow-md">
                <img src={cameraIcon} alt="Camera" className="h-3 w-3" />
              </div>
            </div>
            <input
              id="profile-image-input"
              type="file"
              accept="image/jpeg,image/jpg,image/png,image/webp"
              onChange={(e) => {
                const file = e.target.files?.[0];
                if (file) {
                  setFormData(prev => ({ ...prev, profilePicture: file }));
                }
              }}
              className="hidden"
            />
          </div>
        </div>

        {/* Form Fields */}
        <div className="space-y-6">
          <div className="mb-6">
            <div className="text-center mb-2">
              <Label htmlFor="fullName" className="text-[var(--brill-text)] text-lg font-bold">
                Full Name
              </Label>
            </div>
            <Input
              id="fullName"
              value={formData.fullName}
              onChange={(e) => handleInputChange("fullName", e.target.value)}
              placeholder="Enter your full name"
              className="w-full px-4 py-4 rounded-3xl border-gray-300 focus:ring-2 focus:ring-[var(--brill-primary)] focus:border-[var(--brill-primary)] text-base"
              required
            />
          </div>

          <div className="mb-6">
            <div className="text-center mb-2">
              <Label htmlFor="email" className="text-[var(--brill-text)] text-lg font-bold">
                Email
              </Label>
            </div>
            <Input
              id="email"
              type="email"
              value={formData.email}
              onChange={(e) => handleInputChange("email", e.target.value)}
              placeholder="Enter your email"
              className="w-full px-4 py-4 rounded-3xl border-gray-300 focus:ring-2 focus:ring-[var(--brill-primary)] focus:border-[var(--brill-primary)] text-base"
              required
            />
          </div>

          <div className="mb-6">
            <div className="text-center mb-2">
              <Label htmlFor="phone" className="text-[var(--brill-text)] text-lg font-bold">
                Number
              </Label>
            </div>
            <Input
              id="phone"
              type="tel"
              value={formData.phone}
              onChange={(e) => handleInputChange("phone", e.target.value)}
              placeholder="+234 801 234 5678"
              className="w-full px-4 py-4 rounded-3xl border-gray-300 focus:ring-2 focus:ring-[var(--brill-primary)] focus:border-[var(--brill-primary)] text-base"
              required
            />
          </div>

          <div className="mb-8">
            <div className="text-center mb-2">
              <Label htmlFor="address" className="text-[var(--brill-text)] text-lg font-bold">
                Location
              </Label>
            </div>
            <Input
              id="address"
              value={`${formData.city}${formData.state ? ', ' + formData.state : ''}`}
              onChange={(e) => {
                const parts = e.target.value.split(',');
                handleInputChange("city", parts[0]?.trim() || '');
                handleInputChange("state", parts[1]?.trim() || '');
              }}
              placeholder="Jahi, Abuja"
              className="w-full px-4 py-4 rounded-3xl border-gray-300 focus:ring-2 focus:ring-[var(--brill-primary)] focus:border-[var(--brill-primary)] text-base"
              required
            />
          </div>


        </div>

        <div className="flex justify-center mb-8">
          <LoadingButton
            type="submit"
            loading={updateProfileMutation.isPending}
            className="w-48 bg-[var(--brill-secondary)] text-white py-4 px-4 rounded-3xl font-medium hover:bg-[var(--brill-active)] transition duration-200 text-lg flex items-center justify-center disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <Save className="w-4 h-4 mr-2" />
            Save
          </LoadingButton>
        </div>
      </form>

      {/* Success Modal */}
      <NotificationModal
        isOpen={showSuccessModal}
        onClose={() => setShowSuccessModal(false)}
        type="success"
        title="Profile Updated!"
        description="Your profile has been successfully updated."
        actionText="Continue"
        onAction={() => setLocation("/profile")}
      />

      {/* Error Modal */}
      <NotificationModal
        isOpen={showErrorModal}
        onClose={() => setShowErrorModal(false)}
        type="error"
        title="Update Failed"
        description={errorMessage}
        actionText="Try Again"
        onAction={() => setShowErrorModal(false)}
      />
    </div>
  );
}

client/src/pages/enhanced-verification.t.content:
import React, { useState, useEffect } from 'react';
import { useAuth } from '../hooks/use-auth';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { Label } from '../components/ui/label';
import { Badge } from '../components/ui/badge';
import { Progress } from '../components/ui/progress';
import { 
  CheckCircle, 
  Upload, 
  Camera, 
  FileText, 
  Shield, 
  Fingerprint,
  Eye,
  AlertCircle,
  Star
} from 'lucide-react';
import { useToast } from '../hooks/use-toast';

const COLORS = {
  PRIMARY: '#4682b4',
  SECONDARY: '#131313',
  SUCCESS: '#10b981',
  WARNING: '#f59e0b',
  ERROR: '#ef4444'
};

interface VerificationStatus {
  overall: {
    isVerified: boolean;
    level: string;
    progress: number;
  };
  email: {
    verified: boolean;
    email: string;
  };
  phone: {
    verified: boolean;
    phone: string;
  };
  biometric: {
    verified: boolean;
    type?: string;
  };
  documents: Array<{
    id: number;
    type: string;
    status: string;
    validationScore: number;
    uploadedAt: string;
    expiryDate?: string;
  }>;
  requiredSteps: string[];
}

export default function EnhancedVerificationPage() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [verificationStatus, setVerificationStatus] = useState<VerificationStatus | null>(null);
  const [loading, setLoading] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [documentType, setDocumentType] = useState<string>('');
  const [documentNumber, setDocumentNumber] = useState<string>('');
  const [expiryDate, setExpiryDate] = useState<string>('');
  const [biometricData, setBiometricData] = useState<string>('');

  useEffect(() => {
    fetchVerificationStatus();
  }, []);

  const fetchVerificationStatus = async () => {
    try {
      const response = await fetch('/api/verification-enhanced/status', {
        credentials: 'include'
      });

      if (response.ok) {
        const data = await response.json();
        setVerificationStatus(data.verification);
      }
    } catch (error) {
      console.error('Failed to fetch verification status:', error);
    }
  };

  const handleDocumentUpload = async () => {
    if (!selectedFile || !documentType || !documentNumber) {
      toast({
        title: "Missing Information",
        description: "Please select a file, document type, and number",
        variant: "destructive"
      });
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('document', selectedFile);
    formData.append('documentType', documentType);
    formData.append('documentNumber', documentNumber);
    if (expiryDate) formData.append('expiryDate', expiryDate);

    try {
      const response = await fetch('/api/verification-enhanced/documents/upload', {
        method: 'POST',
        credentials: 'include',
        body: formData
      });

      const data = await response.json();

      if (data.success) {
        toast({
          title: "Document Uploaded",
          description: `Document uploaded with ${Math.round(data.document.validationScore * 100)}% confidence`,
          variant: "default"
        });

        // Reset form
        setSelectedFile(null);
        setDocumentType('');
        setDocumentNumber('');
        setExpiryDate('');

        // Refresh status
        fetchVerificationStatus();
      } else {
        throw new Error(data.message);
      }
    } catch (error: any) {
      toast({
        title: "Upload Failed",
        description: error.message || "Failed to upload document",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const handleBiometricVerification = async (biometricType: 'FACE' | 'FINGERPRINT') => {
    if (!biometricData) {
      // Simulate biometric capture
      setBiometricData(btoa(Math.random().toString()));

      toast({
        title: "Biometric Captured",
        description: "Biometric data captured successfully",
        variant: "default"
      });
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('/api/verification-enhanced/biometric/verify', {
        method: 'POST',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          biometricType,
          biometricData,
          deviceInfo: {
            deviceId: navigator.userAgent,
            platform: navigator.platform,
            version: '1.0.0'
          }
        })
      });

      const data = await response.json();

      if (data.success) {
        toast({
          title: "Biometric Verified",
          description: `${biometricType} verification completed successfully`,
          variant: "default"
        });

        setBiometricData('');
        fetchVerificationStatus();
      } else {
        throw new Error(data.message);
      }
    } catch (error: any) {
      toast({
        title: "Verification Failed",
        description: error.message || "Biometric verification failed",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const getVerificationLevelColor = (level: string) => {
    switch (level) {
      case 'PREMIUM': return COLORS.SUCCESS;
      case 'STANDARD': return COLORS.PRIMARY;
      case 'BASIC': return COLORS.WARNING;
      default: return COLORS.ERROR;
    }
  };

  const getVerificationLevelIcon = (level: string) => {
    switch (level) {
      case 'PREMIUM': return <Star className="h-5 w-5" />;
      case 'STANDARD': return <Shield className="h-5 w-5" />;
      case 'BASIC': return <CheckCircle className="h-5 w-5" />;
      default: return <AlertCircle className="h-5 w-5" />;
    }
  };

  if (!verificationStatus) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-white p-4 flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-white p-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold mb-2" style={{ color: COLORS.SECONDARY }}>
            Enhanced Verification
          </h1>
          <p className="text-gray-600">
            Complete advanced verification for maximum security and benefits
          </p>

          {/* Verification Level Badge */}
          <div className="mt-4 flex justify-center">
            <Badge 
              className="px-4 py-2 text-sm font-medium flex items-center"
              style={{ 
                backgroundColor: `${getVerificationLevelColor(verificationStatus.overall.level)}20`,
                color: getVerificationLevelColor(verificationStatus.overall.level),
                border: `1px solid ${getVerificationLevelColor(verificationStatus.overall.level)}40`
              }}
            >
              {getVerificationLevelIcon(verificationStatus.overall.level)}
              <span className="ml-2">{verificationStatus.overall.level} Level</span>
            </Badge>
          </div>

          {/* Progress Bar */}
          <div className="mt-6 max-w-md mx-auto">
            <Progress value={verificationStatus.overall.progress} className="h-3" />
            <p className="text-sm text-gray-500 mt-2">
              {Math.round(verificationStatus.overall.progress)}% Complete
            </p>
          </div>
        </div>

        {/* Document Upload Section */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center" style={{ color: COLORS.SECONDARY }}>
              <FileText className="h-6 w-6 mr-3" />
              Document Verification
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {/* Document Type Selection */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <Label htmlFor="documentType">Document Type</Label>
                <select
                  id="documentType"
                  value={documentType}
                  onChange={(e) => setDocumentType(e.target.value)}
                  className="w-full p-3 border rounded-xl"
                >
                  <option value="">Select Document</option>
                  <option value="LICENSE">Driver's License</option>
                  <option value="NIN">National ID (NIN)</option>
                  <option value="PASSPORT">International Passport</option>
                  <option value="VEHICLE_REGISTRATION">Vehicle Registration</option>
                </select>
              </div>

              <div>
                <Label htmlFor="documentNumber">Document Number</Label>
                <Input
                  id="documentNumber"
                  value={documentNumber}
                  onChange={(e) => setDocumentNumber(e.target.value)}
                  placeholder="Enter document number"
                  className="rounded-xl"
                />
              </div>

              <div>
                <Label htmlFor="expiryDate">Expiry Date (Optional)</Label>
                <Input
                  id="expiryDate"
                  type="date"
                  value={expiryDate}
                  onChange={(e) => setExpiryDate(e.target.value)}
                  className="rounded-xl"
                />
              </div>
            </div>

            {/* File Upload */}
            <div className="border-2 border-dashed border-gray-300 rounded-xl p-6 text-center">
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setSelectedFile(e.target.files?.[0] || null)}
                className="hidden"
                id="document-upload"
              />
              <label htmlFor="document-upload" className="cursor-pointer">
                <Upload className="h-12 w-12 mx-auto text-gray-400 mb-4" />
                <p className="text-gray-600">
                  {selectedFile ? selectedFile.name : "Click to upload document image"}
                </p>
                <p className="text-sm text-gray-500 mt-2">
                  PNG, JPG, JPEG up to 5MB
                </p>
              </label>
            </div>

            <Button
              onClick={handleDocumentUpload}
              disabled={loading || !selectedFile || !documentType || !documentNumber}
              className="w-full rounded-xl"
              style={{ backgroundColor: COLORS.PRIMARY }}
            >
              {loading ? "Uploading..." : "Upload Document"}
            </Button>
          </CardContent>
        </Card>

        {/* Biometric Verification Section */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center" style={{ color: COLORS.SECONDARY }}>
              <Fingerprint className="h-6 w-6 mr-3" />
              Biometric Verification
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {/* Face Recognition */}
              <div className="border rounded-xl p-6 text-center">
                <Eye className="h-12 w-12 mx-auto text-blue-500 mb-4" />
                <h3 className="font-semibold mb-2">Face Recognition</h3>
                <p className="text-gray-600 text-sm mb-4">
                  Verify your identity using facial recognition
                </p>
                <Button
                  onClick={() => handleBiometricVerification('FACE')}
                  disabled={loading}
                  className="w-full rounded-xl"
                  variant={verificationStatus.biometric.verified ? "outline" : "default"}
                  style={!verificationStatus.biometric.verified ? { backgroundColor: COLORS.PRIMARY } : {}}
                >
                  {verificationStatus.biometric.verified ? "âœ“ Verified" : 
                   biometricData ? "Complete Verification" : "Capture Face"}
                </Button>
              </div>

              {/* Fingerprint */}
              <div className="border rounded-xl p-6 text-center">
                <Fingerprint className="h-12 w-12 mx-auto text-blue-500 mb-4" />
                <h3 className="font-semibold mb-2">Fingerprint</h3>
                <p className="text-gray-600 text-sm mb-4">
                  Secure verification using fingerprint
                </p>
                <Button
                  onClick={() => handleBiometricVerification('FINGERPRINT')}
                  disabled={loading}
                  className="w-full rounded-xl"
                  variant={verificationStatus.biometric.verified ? "outline" : "default"}
                  style={!verificationStatus.biometric.verified ? { backgroundColor: COLORS.PRIMARY } : {}}
                >
                  {verificationStatus.biometric.verified ? "âœ“ Verified" : 
                   biometricData ? "Complete Verification" : "Scan Fingerprint"}
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Verification Status Overview */}
        <Card>
          <CardHeader>
            <CardTitle style={{ color: COLORS.SECONDARY }}>Verification Status</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {/* Current Documents */}
              {verificationStatus.documents.length > 0 && (
                <div>
                  <h4 className="font-semibold mb-3">Uploaded Documents</h4>
                  <div className="space-y-2">
                    {verificationStatus.documents.map((doc) => (
                      <div key={doc.id} className="flex justify-between items-center p-3 border rounded-lg">
                        <div>
                          <span className="font-medium">{doc.type.replace('_', ' ')}</span>
                          <p className="text-sm text-gray-600">
                            Confidence: {Math.round(doc.validationScore * 100)}%
                          </p>
                        </div>
                        <Badge
                          variant={doc.status === 'VERIFIED' ? 'default' : 'secondary'}
                          style={{
                            backgroundColor: doc.status === 'VERIFIED' ? COLORS.SUCCESS : COLORS.WARNING,
                            color: 'white'
                          }}
                        >
                          {doc.status}
                        </Badge>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Required Steps */}
              {verificationStatus.requiredSteps.length > 0 && (
                <div>
                  <h4 className="font-semibold mb-3">Remaining Steps</h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                    {verificationStatus.requiredSteps.map((step) => (
                      <div key={step} className="flex items-center p-2 border rounded-lg">
                        <AlertCircle className="h-4 w-4 text-orange-500 mr-2" />
                        <span className="text-sm">{step.replace('_', ' ')}</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/forgot-password.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { ArrowLeft, Mail } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useMutation } from "@tanstack/react-query";
import { authAPI } from "@/lib/auth";
import { LoadingButton } from "@/components/ui/loading-button";
import { SimpleNotificationModal } from "@/components/ui/notification-modal";
import logo from "../assets/images/logo.png";

export default function ForgotPasswordPage() {
  const [email, setEmail] = useState("");
  const [showEmailModal, setShowEmailModal] = useState(false);
  const [showErrorModal, setShowErrorModal] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [, setLocation] = useLocation();

  const resetPasswordMutation = useMutation({
    mutationFn: async (email: string) => {
      const response = await fetch('/api/auth/forgot-password', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to send reset link');
      }

      return response.json();
    },
    onSuccess: () => {
      setShowEmailModal(true);
    },
    onError: (error: any) => {
      setErrorMessage(error.message || "Failed to send reset link. Please try again.");
      setShowErrorModal(true);
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (email) {
      resetPasswordMutation.mutate(email);
    }
  };

  const handleBackToSignIn = () => {
    setLocation("/signin");
  };

  // Removed unused handleResendEmail function

  return (
    <div className="w-full max-w-md mx-auto min-h-screen bg-white flex flex-col">
      <div className="px-6 py-8 flex-1 flex flex-col justify-center">
        {/* Header */}
        <div className="flex items-center mb-8">
          <Button
            onClick={handleBackToSignIn}
            variant="ghost"
            size="icon"
            className="mr-4 w-10 h-10 rounded-full bg-gray-100 hover:bg-gray-200"
          >
            <ArrowLeft className="w-5 h-5" />
          </Button>
          <h1 className="text-xl font-bold text-[#2d3748]">Forgot Password</h1>
        </div>

        {/* Logo */}
        <div className="text-center mb-8">
          <img 
            src={logo} 
            alt="Brillprime Logo" 
            className="w-16 h-16 mx-auto mb-4"
          />
          <h2 className="text-2xl font-bold text-[#2d3748] mb-2">
            Reset Your Password
          </h2>
          <p className="text-[#718096] text-sm">
            Enter your email address and we'll send you a link to reset your password.
          </p>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="space-y-2">
            <Label htmlFor="email" className="text-[#2d3748] font-medium">
              Email Address
            </Label>
            <div className="relative">
              <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-[#718096] w-5 h-5" />
              <Input
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email address"
                className="pl-12 py-3 rounded-xl border-gray-300 focus:border-[#4682B4] focus:ring-[#4682B4]"
                required
              />
            </div>
          </div>

          <LoadingButton
            type="submit"
            loading={resetPasswordMutation.isPending}
            className="w-full py-3 rounded-xl bg-[#4682B4] hover:bg-[#3a70a0] text-white font-medium"
            disabled={!email}
          >
            Send Reset Link
          </LoadingButton>
        </form>

        {/* Footer */}
        <div className="mt-8 text-center">
          <p className="text-[#718096] text-sm">
            Remember your password?{" "}
            <button
              onClick={handleBackToSignIn}
              className="text-[#4682B4] font-medium hover:underline"
            >
              Sign In
            </button>
          </p>
        </div>
      </div>

      {/* Email Sent Modal */}
      <SimpleNotificationModal
        isOpen={showEmailModal}
        onClose={() => setShowEmailModal(false)}
        type="success"
        title="Email Sent!"
        message={`We've sent a password reset link to ${email}. Please check your email and click the link to reset your password.`}
        confirmText="Back to Sign In"
        onConfirm={handleBackToSignIn}
      />

      {/* Error Modal */}
      <SimpleNotificationModal
        isOpen={showErrorModal}
        onClose={() => setShowErrorModal(false)}
        type="error"
        title="Reset Failed"
        message={errorMessage}
        confirmText="Try Again"
        onConfirm={() => setShowErrorModal(false)}
      />
    </div>
  );
}

