import { useEffect } from "react";
import { useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { 
  ArrowLeft, 
  MapPin, 
  Clock, 
  Phone, 
  MessageSquare, 
  Navigation,
  Fuel,
  User,
  CheckCircle,
  AlertCircle,
  Truck
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useAuth } from "@/hooks/use-auth";
import { useWebSocketOrders } from "@/hooks/use-websocket";
import LiveMap from "@/components/ui/live-map";



export default function FuelDeliveryTracking() {
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const { connected } = useWebSocketOrders();

  // Get order ID from URL params
  const urlParams = new URLSearchParams(window.location.search);
  const orderId = urlParams.get('orderId') || '';

  // Fetch fuel order details
  const { data: order, isLoading } = useQuery({
    queryKey: ["/api/fuel-orders", orderId],
    queryFn: async () => {
      const response = await fetch(`/api/fuel-orders/${orderId}`);
      const data = await response.json();
      return data.success ? data.order : null;
    },
    enabled: !!orderId,
    refetchInterval: 30000
  });

  // Update order status mutation
  const updateStatusMutation = useMutation({
    mutationFn: async ({ status, notes }: { status: string; notes?: string }) => {
      const response = await fetch(`/api/fuel-orders/${orderId}/status`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status, notes })
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/fuel-orders", orderId] });
    }
  });

  const getStatusConfig = (status: string) => {
    const configs = {
      PENDING: { 
        color: 'bg-yellow-100 text-yellow-800', 
        icon: Clock, 
        message: 'Waiting for driver acceptance' 
      },
      ACCEPTED: { 
        color: 'bg-blue-100 text-blue-800', 
        icon: User, 
        message: 'Driver assigned and heading to pickup' 
      },
      PICKED_UP: { 
        color: 'bg-purple-100 text-purple-800', 
        icon: Truck, 
        message: 'Fuel picked up, heading to delivery location' 
      },
      IN_TRANSIT: { 
        color: 'bg-indigo-100 text-indigo-800', 
        icon: Navigation, 
        message: 'Driver is on the way to your location' 
      },
      DELIVERED: { 
        color: 'bg-green-100 text-green-800', 
        icon: CheckCircle, 
        message: 'Fuel delivered successfully' 
      },
      CANCELLED: { 
        color: 'bg-red-100 text-red-800', 
        icon: AlertCircle, 
        message: 'Order has been cancelled' 
      }
    };
    return configs[status as keyof typeof configs] || configs.PENDING;
  };

  const formatFuelType = (type: string) => {
    const fuelTypes: Record<string, string> = {
      PMS: 'Premium Motor Spirit',
      AGO: 'Automotive Gas Oil', 
      DPK: 'Dual Purpose Kerosene'
    };
    return fuelTypes[type] || type;
  };

  const handleStatusUpdate = (status: string) => {
    updateStatusMutation.mutate({ status });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Loading fuel order details...</p>
        </div>
      </div>
    );
  }

  if (!order) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <AlertCircle className="h-12 w-12 text-red-500 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">Order Not Found</h3>
          <p className="text-gray-600 mb-4">The fuel order you're looking for doesn't exist.</p>
          <Button onClick={() => setLocation('/dashboard')}>
            Go to Dashboard
          </Button>
        </div>
      </div>
    );
  }

  const statusConfig = getStatusConfig(order.status);
  const StatusIcon = statusConfig.icon;

  return (
    <div className="min-h-screen bg-gray-50 pb-20">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="flex items-center justify-between p-4">
          <div className="flex items-center space-x-3">
            <Button 
              variant="ghost" 
              size="icon" 
              onClick={() => setLocation('/dashboard')}
            >
              <ArrowLeft className="h-5 w-5" />
            </Button>
            <div>
              <h1 className="text-xl font-semibold text-[#131313]">Fuel Delivery</h1>
              <p className="text-sm text-gray-600">Order #{order.id.slice(-8)}</p>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <div className={`w-3 h-3 rounded-full ${connected ? 'bg-green-500' : 'bg-red-500'}`}></div>
            <span className="text-xs text-gray-500">
              {connected ? 'Live' : 'Offline'}
            </span>
          </div>
        </div>
      </div>

      <div className="p-4 space-y-4">
        {/* Status Card */}
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-3 mb-4">
              <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                <StatusIcon className="h-6 w-6 text-blue-600" />
              </div>
              <div className="flex-1">
                <Badge className={statusConfig.color}>
                  {order.status.replace('_', ' ')}
                </Badge>
                <p className="text-sm text-gray-600 mt-1">
                  {statusConfig.message}
                </p>
              </div>
            </div>

            {order.estimatedDeliveryTime && (
              <div className="flex items-center text-sm text-gray-600">
                <Clock className="h-4 w-4 mr-2" />
                <span>
                  Estimated delivery: {new Date(order.estimatedDeliveryTime).toLocaleTimeString()}
                </span>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Order Details */}
        <Card>
          <CardContent className="p-4">
            <h3 className="font-semibold text-[#131313] mb-4 flex items-center">
              <Fuel className="h-5 w-5 mr-2 text-blue-600" />
              Order Details
            </h3>

            <div className="space-y-3">
              <div className="flex justify-between">
                <span className="text-gray-600">Fuel Type:</span>
                <span className="font-medium">{formatFuelType(order.fuelType)}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Quantity:</span>
                <span className="font-medium">{order.quantity}L</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Unit Price:</span>
                <span className="font-medium">â‚¦{parseFloat(order.unitPrice).toLocaleString()}</span>
              </div>
              <div className="flex justify-between border-t pt-2">
                <span className="text-gray-600">Total Amount:</span>
                <span className="font-bold text-lg">â‚¦{parseFloat(order.totalAmount).toLocaleString()}</span>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Delivery Information */}
        <Card>
          <CardContent className="p-4">
            <h3 className="font-semibold text-[#131313] mb-4 flex items-center">
              <MapPin className="h-5 w-5 mr-2 text-green-600" />
              Delivery Information
            </h3>

            <div className="space-y-3">
              <div>
                <span className="text-gray-600 text-sm">Delivery Address:</span>
                <p className="font-medium">{order.deliveryAddress}</p>
              </div>

              {order.customerName && user?.role === 'DRIVER' && (
                <div>
                  <span className="text-gray-600 text-sm">Customer:</span>
                  <p className="font-medium">{order.customerName}</p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Driver Information */}
        {order.driverName && user?.role === 'CONSUMER' && (
          <Card>
            <CardContent className="p-4">
              <h3 className="font-semibold text-[#131313] mb-4 flex items-center">
                <User className="h-5 w-5 mr-2 text-purple-600" />
                Driver Information
              </h3>

              <div className="flex items-center justify-between">
                <div>
                  <p className="font-medium">{order.driverName}</p>
                  <p className="text-sm text-gray-600">{order.driverPhone}</p>
                </div>

                <div className="flex space-x-2">
                  <Button variant="outline" size="sm">
                    <Phone className="h-4 w-4" />
                  </Button>
                  <Button variant="outline" size="sm">
                    <MessageSquare className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Live Map */}
        {(order.status === 'ACCEPTED' || order.status === 'PICKED_UP' || order.status === 'IN_TRANSIT') && (
          <Card>
            <CardContent className="p-4">
              <h3 className="font-semibold text-[#131313] mb-4 flex items-center">
                <Navigation className="h-5 w-5 mr-2 text-indigo-600" />
                Live Tracking
              </h3>

              <div className="h-64 rounded-lg overflow-hidden">
                <LiveMap
                  showUserLocation={true}
                  showDriverLocation={user?.role === 'CONSUMER'}
                  orderId={order.id}
                  className="h-full w-full"
                />
              </div>
            </CardContent>
          </Card>
        )}

        {/* Driver Actions */}
        {user?.role === 'DRIVER' && order.status !== 'DELIVERED' && order.status !== 'CANCELLED' && (
          <Card>
            <CardContent className="p-4">
              <h3 className="font-semibold text-[#131313] mb-4">Update Status</h3>

              <div className="space-y-2">
                {order.status === 'ACCEPTED' && (
                  <Button 
                    onClick={() => handleStatusUpdate('PICKED_UP')}
                    disabled={updateStatusMutation.isPending}
                    className="w-full bg-purple-600 hover:bg-purple-700 text-white"
                  >
                    Confirm Pickup
                  </Button>
                )}

                {order.status === 'PICKED_UP' && (
                  <Button 
                    onClick={() => handleStatusUpdate('IN_TRANSIT')}
                    disabled={updateStatusMutation.isPending}
                    className="w-full bg-indigo-600 hover:bg-indigo-700 text-white"
                  >
                    Start Delivery
                  </Button>
                )}

                {order.status === 'IN_TRANSIT' && (
                  <Button 
                    onClick={() => handleStatusUpdate('DELIVERED')}
                    disabled={updateStatusMutation.isPending}
                    className="w-full bg-green-600 hover:bg-green-700 text-white"
                  >
                    Mark as Delivered
                  </Button>
                )}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Order Timeline */}
        <Card>
          <CardContent className="p-4">
            <h3 className="font-semibold text-[#131313] mb-4">Order Timeline</h3>

            <div className="space-y-4">
              <div className="flex items-center space-x-3">
                <div className="w-3 h-3 bg-green-500 rounded-full"></div>
                <div>
                  <p className="font-medium text-sm">Order Placed</p>
                  <p className="text-xs text-gray-600">
                    {new Date(order.createdAt).toLocaleString()}
                  </p>
                </div>
              </div>

              {order.acceptedAt && (
                <div className="flex items-center space-x-3">
                  <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
                  <div>
                    <p className="font-medium text-sm">Driver Assigned</p>
                    <p className="text-xs text-gray-600">
                      {new Date(order.acceptedAt).toLocaleString()}
                    </p>
                  </div>
                </div>
              )}

              {order.pickedUpAt && (
                <div className="flex items-center space-x-3">
                  <div className="w-3 h-3 bg-purple-500 rounded-full"></div>
                  <div>
                    <p className="font-medium text-sm">Fuel Picked Up</p>
                    <p className="text-xs text-gray-600">
                      {new Date(order.pickedUpAt).toLocaleString()}
                    </p>
                  </div>
                </div>
              )}

              {order.deliveredAt && (
                <div className="flex items-center space-x-3">
                  <div className="w-3 h-3 bg-green-500 rounded-full"></div>
                  <div>
                    <p className="font-medium text-sm">Delivered</p>
                    <p className="text-xs text-gray-600">
                      {new Date(order.deliveredAt).toLocaleString()}
                    </p>
                  </div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/fuel-order-details.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { ArrowLeft, MapPin, Clock, Fuel, Plus, Minus, Calendar, MessageSquare, Phone } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { useAuth } from "@/hooks/use-auth";
import { useWebSocketOrders, useWebSocketFuelOrders } from "@/hooks/use-websocket";
import LiveMap from "@/components/ui/live-map";

interface FuelStation {
  id: string;
  name: string;
  brand: string;
  address: string;
  latitude: number;
  longitude: number;
  distance: number;
  rating: number;
  reviewCount: number;
  prices: {
    PMS: number;
    AGO: number;
    DPK: number;
  };
  fuelTypes: string[];
  isOpen: boolean;
  deliveryTime: string;
  phone: string;
}

export default function FuelOrderDetails() {
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const { connected } = useWebSocketOrders();
  const { 
    connected: fuelOrderConnected, 
    orderUpdates, 
    joinOrderRoom 
  } = useWebSocketFuelOrders();

  const [station, setStation] = useState<FuelStation | null>(null);
  const [selectedFuelType, setSelectedFuelType] = useState<"PMS" | "AGO" | "DPK">("PMS");
  const [quantity, setQuantity] = useState(20);
  const [deliveryAddress, setDeliveryAddress] = useState("");
  const [userLocation, setUserLocation] = useState<{lat: number, lng: number} | null>(null);
  const [scheduledTime, setScheduledTime] = useState("");
  const [notes, setNotes] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [stationLoading, setStationLoading] = useState(true);

  // Get station ID from URL
  const stationId = window.location.pathname.split('/').pop();

  // Get user's current GPS location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setUserLocation({ lat: latitude, lng: longitude });

          // Reverse geocode to get address
          fetch(`https://api.openstreetmap.org/reverse?format=json&lat=${latitude}&lon=${longitude}`)
            .then(res => res.json())
            .then(data => {
              if (data.display_name) {
                setDeliveryAddress(data.display_name);
              }
            })
            .catch(console.error);
        },
        (error) => {
          console.error('Error getting location:', error);
          // Fallback to default location (Abuja)
          setUserLocation({ lat: 9.0765, lng: 7.3986 });
        },
        { enableHighAccuracy: true, timeout: 10000, maximumAge: 300000 }
      );
    }
  }, []);

  // Fetch station data from API
  useEffect(() => {
    const fetchStation = async () => {
      if (!stationId) return;

      try {
        const response = await fetch(`/api/fuel/stations/${stationId}`, {
          credentials: 'include'
        });

        if (response.ok) {
          const data = await response.json();
          setStation(data.station);
        } else {
          console.error('Failed to fetch station data');
        }
      } catch (error) {
        console.error('Error fetching station:', error);
      } finally {
        setStationLoading(false);
      }
    };

    fetchStation();
  }, [stationId]);

  const calculateTotal = () => {
    if (!station) return 0;
    const unitPrice = station.prices[selectedFuelType];
    const deliveryFee = 500;
    return (quantity * unitPrice) + deliveryFee;
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-NG', {
      style: 'currency',
      currency: 'NGN',
      minimumFractionDigits: 0
    }).format(amount);
  };

  const handleQuantityChange = (delta: number) => {
    const newQuantity = quantity + delta;
    if (newQuantity >= 5 && newQuantity <= 200) {
      setQuantity(newQuantity);
    }
  };

  const handlePlaceOrder = async () => {
    if (!station || !user || !deliveryAddress.trim() || !userLocation) return;

    setIsLoading(true);
    try {
      const orderData = {
        stationId: station.id,
        fuelType: selectedFuelType,
        quantity,
        unitPrice: station.prices[selectedFuelType],
        totalAmount: calculateTotal(),
        deliveryAddress,
        deliveryLatitude: userLocation.lat,
        deliveryLongitude: userLocation.lng,
        scheduledDeliveryTime: scheduledTime || undefined,
        notes: notes || undefined
      };

      const response = await fetch('/api/fuel/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(orderData),
      });

      const result = await response.json();

      if (result.success) {
        // Real-time WebSocket notifications would be handled by the backend
        if (connected) {
          console.log('Order created, WebSocket connected for real-time updates');
        }

        // Join order room for real-time updates
        if (fuelOrderConnected) {
          joinOrderRoom(result.order.id);
        }

        setLocation(`/order-confirmation/${result.order.id}`);
      } else {
        alert('Failed to place order: ' + result.message);
      }
    } catch (error) {
      console.error('Order placement error:', error);
      alert('Failed to place order. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  if (stationLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Loading station details...</p>
        </div>
      </div>
    );
  }

  if (!station) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <p className="text-gray-600 mb-4">Station not found</p>
          <Button onClick={() => setLocation("/fuel-ordering")}>
            Back to Fuel Ordering
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 w-full max-w-md mx-auto">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="flex items-center justify-between p-4">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setLocation("/fuel-ordering")}
          >
            <ArrowLeft className="w-5 h-5" />
          </Button>
          <h1 className="text-lg font-semibold text-[#131313]">Order Fuel</h1>
          <div className="w-10"></div>
        </div>
      </div>

      <div className="p-4 space-y-6">
        {/* Station Info */}
        <Card>
          <CardContent className="p-4">
            <div className="flex items-start justify-between mb-3">
              <div className="flex-1">
                <h3 className="font-semibold text-[#131313]">{station.name}</h3>
                <p className="text-sm text-gray-600 mb-2">{station.address}</p>
                <div className="flex items-center space-x-4 text-sm text-gray-500">
                  <div className="flex items-center space-x-1">
                    <MapPin className="w-3 h-3" />
                    <span>{station.distance.toFixed(1)} km away</span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <Clock className="w-3 h-3" />
                    <span>{station.deliveryTime}</span>
                  </div>
                </div>
              </div>
              <Badge className={station.isOpen ? "bg-green-100 text-green-800" : "bg-red-100 text-red-800"}>
                {station.isOpen ? "Open" : "Closed"}
              </Badge>
            </div>
            <div className="flex space-x-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => window.location.href = `tel:${station.phone}`}
              >
                <Phone className="w-4 h-4 mr-1" />
                Call
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setLocation(`/chat/station/${station.id}`)}
              >
                <MessageSquare className="w-4 h-4 mr-1" />
                Chat
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Live Map */}
        {userLocation && (
          <Card>
            <CardContent className="p-4">
              <h4 className="font-medium text-[#131313] mb-3">Station Location</h4>
              <div className="h-48 rounded-lg overflow-hidden">
                <LiveMap
                  showUserLocation={true}
                  showNearbyUsers={false}
                  className="w-full h-full"
                  userRole="CONSUMER"
                  center={{ lat: station.latitude, lng: station.longitude }}
                  markers={[
                    {
                      lat: station.latitude,
                      lng: station.longitude,
                      title: station.name,
                      type: 'station'
                    },
                    {
                      lat: userLocation.lat,
                      lng: userLocation.lng,
                      title: 'Your Location',
                      type: 'user'
                    }
                  ]}
                />
              </div>
            </CardContent>
          </Card>
        )}

        {/* Fuel Type Selection */}
        <Card>
          <CardContent className="p-4">
            <h4 className="font-medium text-[#131313] mb-3">Select Fuel Type</h4>
            <div className="grid grid-cols-3 gap-2">
              {station.fuelTypes.map((type) => (
                <button
                  key={type}
                  onClick={() => setSelectedFuelType(type as "PMS" | "AGO" | "DPK")}
                  className={`p-3 rounded-lg border text-center transition-colors ${
                    selectedFuelType === type
                      ? "border-[#4682b4] bg-[#4682b4]/10 text-[#4682b4]"
                      : "border-gray-200 hover:border-gray-300"
                  }`}
                >
                  <div className="font-medium">{type}</div>
                  <div className="text-sm text-gray-600">
                    {formatCurrency(station.prices[type as keyof typeof station.prices])}/L
                  </div>
                </button>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Quantity Selection */}
        <Card>
          <CardContent className="p-4">
            <h4 className="font-medium text-[#131313] mb-3">Quantity (Liters)</h4>
            <div className="flex items-center justify-center space-x-4">
              <Button
                variant="outline"
                size="icon"
                onClick={() => handleQuantityChange(-5)}
                disabled={quantity <= 5}
              >
                <Minus className="w-4 h-4" />
              </Button>
              <div className="text-center">
                <div className="text-2xl font-bold text-[#4682b4]">{quantity}L</div>
                <div className="text-sm text-gray-600">
                  {formatCurrency(quantity * station.prices[selectedFuelType])}
                </div>
              </div>
              <Button
                variant="outline"
                size="icon"
                onClick={() => handleQuantityChange(5)}
                disabled={quantity >= 200}
              >
                <Plus className="w-4 h-4" />
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Delivery Details */}
        <Card>
          <CardContent className="p-4 space-y-4">
            <h4 className="font-medium text-[#131313]">Delivery Details</h4>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Delivery Address *
              </label>
              <Textarea
                placeholder="Enter your delivery address"
                value={deliveryAddress}
                onChange={(e) => setDeliveryAddress(e.target.value)}
                className="resize-none"
                rows={3}
              />
              {userLocation && (
                <p className="text-xs text-gray-500 mt-1">
                  GPS coordinates: {userLocation.lat.toFixed(6)}, {userLocation.lng.toFixed(6)}
                </p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Preferred Delivery Time (Optional)
              </label>
              <Input
                type="datetime-local"
                value={scheduledTime}
                onChange={(e) => setScheduledTime(e.target.value)}
                min={new Date().toISOString().slice(0, 16)}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Special Notes (Optional)
              </label>
              <Textarea
                placeholder="Any special instructions for delivery"
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                className="resize-none"
                rows={2}
              />
            </div>
          </CardContent>
        </Card>

        {/* Order Summary */}
        <Card>
          <CardContent className="p-4">
            <h4 className="font-medium text-[#131313] mb-3">Order Summary</h4>
            <div className="space-y-2">
              <div className="flex justify-between">
                <span>Fuel ({quantity}L {selectedFuelType})</span>
                <span>{formatCurrency(quantity * station.prices[selectedFuelType])}</span>
              </div>
              <div className="flex justify-between">
                <span>Delivery Fee</span>
                <span>{formatCurrency(500)}</span>
              </div>
              <div className="border-t pt-2 flex justify-between font-semibold text-lg">
                <span>Total</span>
                <span className="text-[#4682b4]">{formatCurrency(calculateTotal())}</span>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Place Order Button */}
        <Button
          onClick={handlePlaceOrder}
          disabled={!deliveryAddress.trim() || isLoading || !userLocation}
          className="w-full h-12 bg-[#4682b4] hover:bg-[#0b1a51] text-white rounded-xl"
        >
          {isLoading ? (
            <div className="flex items-center space-x-2">
              <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
              <span>Placing Order...</span>
            </div>
          ) : (
            `Place Order - ${formatCurrency(calculateTotal())}`
          )}
        </Button>
      </div>
    </div>
  );
}

