import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { ArrowLeft, MapPin, Search, Filter, Phone, Star, Navigation, Truck } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";

interface FuelStation {
  id: string;
  name: string;
  brand: string;
  address: string;
  distance: number;
  rating: number;
  reviewCount: number;
  pricePerLiter: number;
  fuelTypes: string[];
  isOpen: boolean;
  deliveryTime: string;
  phone: string;
  logo?: string;
}

interface LocationArea {
  id: string;
  name: string;
  stationCount: number;
  averagePrice: number;
}

export default function FuelOrdering() {
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const [selectedStation, setSelectedStation] = useState<Station | null>(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  // Get user's current location for distance calculation
  const [userLocation, setUserLocation] = useState<Location | null>(null);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          });
        },
        (error) => {
          console.error('Error getting location:', error);
        }
      );
    }
  }, []);

  const filteredStations = mockStations.filter(station =>
    station.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    station.address.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleStationSelect = (station: Station) => {
    setSelectedStation(station);
  };

  const handlePlaceOrder = (fuelType: string, quantity: number, unitPrice: number) => {
    if (!selectedStation || !user) return;

    setIsLoading(true);

    // Navigate to fuel order details with pre-filled data
    const orderData = {
      stationId: selectedStation.id,
      stationName: selectedStation.name,
      fuelType,
      quantity,
      unitPrice,
      totalAmount: quantity * unitPrice
    };

    setTimeout(() => {
      setIsLoading(false);
      setLocation('/fuel-order-details', { state: orderData });
    }, 500);
  };

  const handleCreateFuelOrder = async (orderData: any) => {
    setIsLoading(true);

    try {
      // Create fuel order with enhanced data
      const response = await fetch('/api/fuel/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          stationId: selectedStation?.id || 'station_1',
          fuelType: orderData.fuelType,
          quantity: parseFloat(orderData.quantity),
          unitPrice: parseFloat(orderData.unitPrice),
          totalAmount: parseFloat(orderData.totalAmount),
          deliveryAddress: orderData.deliveryAddress,
          deliveryLatitude: orderData.coordinates?.latitude || 6.5244,
          deliveryLongitude: orderData.coordinates?.longitude || 3.3792,
          scheduledDeliveryTime: orderData.scheduledTime,
          notes: orderData.notes || `Fuel delivery order - ${orderData.fuelType}`,
          paymentMethod: orderData.paymentMethod || 'wallet'
        }),
        credentials: 'include'
      });

      const result = await response.json();

      if (response.ok && result.success) {
        // Show success message
        setModalData({
          isOpen: true,
          type: "success",
          title: "Order Placed Successfully! ğŸšš",
          message: `Your ${orderData.fuelType} order has been placed. A driver will be assigned shortly. Track your order in real-time.`
        });

        // Navigate to tracking page after delay
        setTimeout(() => {
          setLocation(`/fuel-delivery-tracking?orderId=${result.order.id}`);
        }, 2000);
      } else {
        throw new Error(result.error || 'Failed to place order');
      }
    } catch (error) {
      console.error('Error creating fuel order:', error);
      setModalData({
        isOpen: true,
        type: "error",
        title: "Order Failed",
        message: error.message || "Unable to place your order. Please try again or contact support."
      });
    } finally {
      setIsLoading(false);
    }
  };
}

client/src/pages/identity-verification.t.content:
import { useState, useRef, useEffect } from "react";
import { useLocation, Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { useAuth } from "@/hooks/use-auth";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { 
  Camera, 
  Upload, 
  CheckCircle, 
  XCircle, 
  User, 
  Phone, 
  Mail, 
  Car,
  FileText,
  Shield,
  ArrowLeft,
  ArrowRight
} from "lucide-react";

// Color constants
const COLORS = {
  PRIMARY: '#4682b4',
  SECONDARY: '#0b1a51', 
  ACTIVE: '#010e42',
  TEXT: '#131313',
  WHITE: '#ffffff'
} as const;

interface VerificationStep {
  id: string;
  title: string;
  description: string;
  completed: boolean;
  required: boolean;
}

interface DriverVerification {
  licenseNumber: string;
  licenseExpiry: string;
  licenseImage?: File;
  vehicleType: string;
  vehiclePlate: string;
  vehicleModel: string;
  vehicleYear: string;
  faceVerification: boolean;
}

interface ConsumerVerification {
  phoneVerification: boolean;
  emailVerification: boolean;
  faceVerification: boolean;
}

export default function IdentityVerification() {
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const [currentStep, setCurrentStep] = useState(0);
  const [verificationData, setVerificationData] = useState<DriverVerification | ConsumerVerification>(
    user?.role === 'DRIVER' 
      ? { licenseNumber: '', licenseExpiry: '', vehicleType: '', vehiclePlate: '', vehicleModel: '', vehicleYear: '', faceVerification: false }
      : { phoneVerification: false, emailVerification: user?.isVerified || false, faceVerification: false }
  );
  const [faceImage, setFaceImage] = useState<File | null>(null);
  const [licenseImage, setLicenseImage] = useState<File | null>(null);
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [verificationProgress, setVerificationProgress] = useState(0);

  // Driver verification steps
  const driverSteps: VerificationStep[] = [
    {
      id: 'license',
      title: 'Driver License Verification',
      description: 'Upload your valid driver license',
      completed: false,
      required: true
    },
    {
      id: 'vehicle',
      title: 'Vehicle Registration',
      description: 'Register your vehicle details',
      completed: false,
      required: true
    },
    {
      id: 'face',
      title: 'Face Verification',
      description: 'Verify your identity with face recognition',
      completed: false,
      required: true
    }
  ];

  // Consumer verification steps
  const consumerSteps: VerificationStep[] = [
    {
      id: 'email',
      title: 'Email Verification',
      description: 'Verify your email address',
      completed: user?.isVerified || false,
      required: true
    },
    {
      id: 'phone',
      title: 'Phone Verification',
      description: 'Verify your phone number',
      completed: false,
      required: true
    },
    {
      id: 'face',
      title: 'Face Verification',
      description: 'Verify your identity with face recognition',
      completed: false,
      required: true
    }
  ];

  const steps = user?.role === 'DRIVER' ? driverSteps : consumerSteps;

  const vehicleTypes = [
    'Motorcycle',
    'Car',
    'Van',
    'Truck',
    'Bicycle'
  ];

  // Start camera for face verification
  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          width: 640, 
          height: 480,
          facingMode: 'user'
        } 
      });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setIsCameraActive(true);
      }
    } catch (error) {
      console.error('Error accessing camera:', error);
    }
  };

  // Stop camera
  const stopCamera = () => {
    if (videoRef.current?.srcObject) {
      const tracks = (videoRef.current.srcObject as MediaStream).getTracks();
      tracks.forEach(track => track.stop());
      setIsCameraActive(false);
    }
  };

  // Capture face photo
  const captureFace = () => {
    if (videoRef.current && canvasRef.current) {
      const canvas = canvasRef.current;
      const video = videoRef.current;
      const context = canvas.getContext('2d');

      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;

      if (context) {
        context.drawImage(video, 0, 0);
        canvas.toBlob((blob) => {
          if (blob) {
            const file = new File([blob], 'face-verification.jpg', { type: 'image/jpeg' });
            setFaceImage(file);
            stopCamera();
          }
        }, 'image/jpeg', 0.8);
      }
    }
  };

  // Handle file upload
  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>, type: 'license' | 'face') => {
    const file = event.target.files?.[0];
    if (file) {
      if (type === 'license') {
        setLicenseImage(file);
      } else {
        setFaceImage(file);
      }
    }
  };

  // Submit verification mutation
  const submitVerificationMutation = useMutation({
    mutationFn: (data: FormData) => 
      apiRequest("POST", "/api/auth/verify-identity", data),
    onSuccess: () => {
      setLocation(user?.role === 'DRIVER' ? '/driver-dashboard' : '/consumer-home');
    },
  });

  // Calculate progress
  useEffect(() => {
    const completedSteps = steps.filter(step => step.completed).length;
    setVerificationProgress((completedSteps / steps.length) * 100);
  }, [steps]);

  const renderDriverLicenseStep = () => (
    <Card className="rounded-3xl border-2 shadow-xl" style={{ borderColor: COLORS.PRIMARY }}>
      <CardHeader>
        <CardTitle className="flex items-center" style={{ color: COLORS.SECONDARY }}>
          <FileText className="h-6 w-6 mr-3" />
          Driver License Verification
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <Label htmlFor="licenseNumber">License Number</Label>
            <Input
              id="licenseNumber"
              placeholder="Enter license number"
              value={(verificationData as DriverVerification).licenseNumber || ''}
              onChange={(e) => setVerificationData(prev => ({
                ...prev,
                licenseNumber: e.target.value
              }))}
              className="rounded-xl border-2"
              style={{ borderColor: COLORS.PRIMARY + '40' }}
            />
          </div>
          <div>
            <Label htmlFor="licenseExpiry">Expiry Date</Label>
            <Input
              id="licenseExpiry"
              type="date"
              value={(verificationData as DriverVerification).licenseExpiry || ''}
              onChange={(e) => setVerificationData(prev => ({
                ...prev,
                licenseExpiry: e.target.value
              }))}
              className="rounded-xl border-2"
              style={{ borderColor: COLORS.PRIMARY + '40' }}
            />
          </div>
        </div>

        <div>
          <Label>Upload License Image</Label>
          <div className="mt-2">
            {licenseImage ? (
              <div className="p-4 border-2 border-dashed rounded-xl" style={{ borderColor: COLORS.PRIMARY }}>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <CheckCircle className="h-6 w-6 text-green-500" />
                    <span className="text-sm font-medium">{licenseImage.name}</span>
                  </div>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setLicenseImage(null)}
                  >
                    <XCircle className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            ) : (
              <label className="flex flex-col items-center justify-center w-full h-32 border-2 border-dashed rounded-xl cursor-pointer hover:bg-gray-50" style={{ borderColor: COLORS.PRIMARY }}>
                <div className="flex flex-col items-center justify-center pt-5 pb-6">
                  <Upload className="h-8 w-8 mb-2" style={{ color: COLORS.PRIMARY }} />
                  <p className="text-sm" style={{ color: COLORS.TEXT }}>
                    <span className="font-semibold">Click to upload</span> driver license
                  </p>
                  <p className="text-xs text-gray-500">PNG, JPG up to 5MB</p>
                </div>
                <input
                  type="file"
                  className="hidden"
                  accept="image/*"
                  onChange={(e) => handleFileUpload(e, 'license')}
                />
              </label>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );

  const renderVehicleRegistrationStep = () => (
    <Card className="rounded-3xl border-2 shadow-xl" style={{ borderColor: COLORS.PRIMARY }}>
      <CardHeader>
        <CardTitle className="flex items-center" style={{ color: COLORS.SECONDARY }}>
          <Car className="h-6 w-6 mr-3" />
          Vehicle Registration
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <Label>Vehicle Type</Label>
            <Select
              value={(verificationData as DriverVerification).vehicleType || ''}
              onValueChange={(value) => setVerificationData(prev => ({
                ...prev,
                vehicleType: value
              }))}
            >
              <SelectTrigger className="rounded-xl border-2" style={{ borderColor: COLORS.PRIMARY + '40' }}>
                <SelectValue placeholder="Select vehicle type" />
              </SelectTrigger>
              <SelectContent>
                {vehicleTypes.map((type) => (
                  <SelectItem key={type} value={type}>{type}</SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
          <div>
            <Label htmlFor="vehiclePlate">Vehicle Plate Number</Label>
            <Input
              id="vehiclePlate"
              placeholder="e.g., LAG-123-AA"
              value={(verificationData as DriverVerification).vehiclePlate || ''}
              onChange={(e) => setVerificationData(prev => ({
                ...prev,
                vehiclePlate: e.target.value.toUpperCase()
              }))}
              className="rounded-xl border-2"
              style={{ borderColor: COLORS.PRIMARY + '40' }}
            />
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <Label htmlFor="vehicleModel">Vehicle Model</Label>
            <Input
              id="vehicleModel"
              placeholder="e.g., Honda CB 150"
              value={(verificationData as DriverVerification).vehicleModel || ''}
              onChange={(e) => setVerificationData(prev => ({
                ...prev,
                vehicleModel: e.target.value
              }))}
              className="rounded-xl border-2"
              style={{ borderColor: COLORS.PRIMARY + '40' }}
            />
          </div>
          <div>
            <Label htmlFor="vehicleYear">Vehicle Year</Label>
            <Input
              id="vehicleYear"
              type="number"
              placeholder="e.g., 2020"
              min="2000"
              max={new Date().getFullYear() + 1}
              value={(verificationData as DriverVerification).vehicleYear || ''}
              onChange={(e) => setVerificationData(prev => ({
                ...prev,
                vehicleYear: e.target.value
              }))}
              className="rounded-xl border-2"
              style={{ borderColor: COLORS.PRIMARY + '40' }}
            />
          </div>
        </div>
      </CardContent>
    </Card>
  );

  const renderFaceVerificationStep = () => (
    <Card className="rounded-3xl border-2 shadow-xl" style={{ borderColor: COLORS.PRIMARY }}>
      <CardHeader>
        <CardTitle className="flex items-center" style={{ color: COLORS.SECONDARY }}>
          <Shield className="h-6 w-6 mr-3" />
          Face Verification
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        <div className="text-center">
          <p className="text-sm text-gray-600 mb-4">
            Take a clear photo of your face for identity verification
          </p>

          {faceImage ? (
            <div className="space-y-4">
              <div className="flex justify-center">
                <img
                  src={URL.createObjectURL(faceImage)}
                  alt="Face verification"
                  className="w-48 h-48 object-cover rounded-xl border-2"
                  style={{ borderColor: COLORS.PRIMARY }}
                />
              </div>
              <div className="flex space-x-3 justify-center">
                <Button
                  variant="outline"
                  onClick={() => setFaceImage(null)}
                  className="rounded-xl"
                >
                  Retake Photo
                </Button>
                <Button
                  onClick={() => setVerificationData(prev => ({
                    ...prev,
                    faceVerification: true
                  }))}
                  className="rounded-xl"
                  style={{ backgroundColor: COLORS.PRIMARY, color: COLORS.WHITE }}
                >
                  Confirm Photo
                </Button>
              </div>
            </div>
          ) : (
            <div className="space-y-4">
              {isCameraActive ? (
                <div className="space-y-4">
                  <div className="flex justify-center">
                    <video
                      ref={videoRef}
                      autoPlay
                      playsInline
                      className="w-64 h-48 object-cover rounded-xl border-2"
                      style={{ borderColor: COLORS.PRIMARY }}
                    />
                  </div>
                  <div className="flex space-x-3 justify-center">
                    <Button
                      variant="outline"
                      onClick={stopCamera}
                      className="rounded-xl"
                    >
                      Cancel
                    </Button>
                    <Button
                      onClick={captureFace}
                      className="rounded-xl"
                      style={{ backgroundColor: COLORS.PRIMARY, color: COLORS.WHITE }}
                    >
                      <Camera className="h-4 w-4 mr-2" />
                      Capture
                    </Button>
                  </div>
                </div>
              ) : (
                <div className="space-y-4">
                  <div className="w-64 h-48 mx-auto border-2 border-dashed rounded-xl flex items-center justify-center" style={{ borderColor: COLORS.PRIMARY }}>
                    <Camera className="h-12 w-12" style={{ color: COLORS.PRIMARY }} />
                  </div>
                  <div className="flex space-x-3 justify-center">
                    <Button
                      onClick={startCamera}
                      className="rounded-xl"
                      style={{ backgroundColor: COLORS.PRIMARY, color: COLORS.WHITE }}
                    >
                      <Camera className="h-4 w-4 mr-2" />
                      Start Camera
                    </Button>
                    <label>
                      <Button
                        variant="outline"
                        className="rounded-xl"
                        asChild
                      >
                        <span>
                          <Upload className="h-4 w-4 mr-2" />
                          Upload Photo
                        </span>
                      </Button>
                      <input
                        type="file"
                        className="hidden"
                        accept="image/*"
                        onChange={(e) => handleFileUpload(e, 'face')}
                      />
                    </label>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
        <canvas ref={canvasRef} className="hidden" />
      </CardContent>
    </Card>
  );

  const renderConsumerVerificationSteps = () => (
    <div className="space-y-6">
      {/* Email Verification */}
      <Card className="rounded-3xl border-2 shadow-xl" style={{ borderColor: COLORS.PRIMARY }}>
        <CardHeader>
          <CardTitle className="flex items-center justify-between" style={{ color: COLORS.SECONDARY }}>
            <div className="flex items-center">
              <Mail className="h-6 w-6 mr-3" />
              Email Verification
            </div>
            {user?.isVerified ? (
              <Badge className="bg-green-100 text-green-800">
                <CheckCircle className="h-4 w-4 mr-1" />
                Verified
              </Badge>
            ) : (
              <Badge className="bg-yellow-100 text-yellow-800">
                Pending
              </Badge>
            )}
          </CardTitle>
        </CardHeader>
        <CardContent>
          {user?.isVerified ? (
            <p className="text-green-600">Your email address has been verified successfully.</p>
          ) : (
            <div className="space-y-4">
              <p className="text-gray-600">Please check your email and click the verification link.</p>
              <Button
                variant="outline"
                className="rounded-xl"
                onClick={() => {/* Resend verification email */}}
              >
                Resend Verification Email
              </Button>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Phone Verification */}
      <Card className="rounded-3xl border-2 shadow-xl" style={{ borderColor: COLORS.PRIMARY }}>
        <CardHeader>
          <CardTitle className="flex items-center" style={{ color: COLORS.SECONDARY }}>
            <Phone className="h-6 w-6 mr-3" />
            Phone Verification
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="flex items-center space-x-3">
              <span className="text-sm">Phone: {user?.phone}</span>
              <Badge className="bg-yellow-100 text-yellow-800">
                Pending
              </Badge>
            </div>
            <Button
              className="rounded-xl"
              style={{ backgroundColor: COLORS.PRIMARY, color: COLORS.WHITE }}
              onClick={() => setLocation('/otp-verification?type=phone')}
            >
              Verify Phone Number
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );

  const handleSubmit = () => {
    const formData = new FormData();
    formData.append('userId', user?.id.toString() || '');
    formData.append('role', user?.role || '');
    formData.append('verificationData', JSON.stringify(verificationData));

    if (faceImage) {
      formData.append('faceImage', faceImage);
    }
    if (licenseImage) {
      formData.append('licenseImage', licenseImage);
    }

    submitVerificationMutation.mutate(formData);
  };

  const canProceed = () => {
    if (user?.role === 'DRIVER') {
      const data = verificationData as DriverVerification;
      switch (currentStep) {
        case 0:
          return data.licenseNumber && data.licenseExpiry && licenseImage;
        case 1:
          return data.vehicleType && data.vehiclePlate && data.vehicleModel && data.vehicleYear;
        case 2:
          return data.faceVerification && faceImage;
        default:
          return false;
      }
    } else {
      // For consumer, only face verification is the last step after email/phone
      // We need to check if email and phone are verified before proceeding to face verification
      const isEmailVerified = user?.isVerified;
      const isPhoneVerified = (verificationData as ConsumerVerification).phoneVerification; // Assuming this state is updated elsewhere or via OTP flow

      if (currentStep === 0) { // Email verification step
        return isEmailVerified;
      } else if (currentStep === 1) { // Phone verification step
        return isPhoneVerified;
      } else { // Face verification step
        return (verificationData as ConsumerVerification).faceVerification && faceImage;
      }
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-white p-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold mb-2" style={{ color: COLORS.SECONDARY }}>
            Identity Verification
          </h1>
          <p className="text-gray-600">
            Complete your verification to access all features
          </p>

          {/* Progress Bar */}
          <div className="mt-6">
            <Progress value={verificationProgress} className="h-2" />
            <p className="text-sm text-gray-500 mt-2">
              {Math.round(verificationProgress)}% Complete
            </p>
          </div>
        </div>

        {/* Steps Navigation */}
        <div className="flex justify-center mb-8">
          <div className="flex space-x-4 overflow-x-auto pb-2">
            {steps.map((step, index) => (
              <div
                key={step.id}
                className={`flex items-center space-x-2 px-4 py-2 rounded-full border-2 cursor-pointer transition-colors duration-300 ${
                  index === currentStep
                    ? 'bg-blue-100 border-blue-500 text-blue-700'
                    : step.completed || (user?.role === 'CONSUMER' && index < 2 && user?.isVerified && index === 0) || (user?.role === 'CONSUMER' && index < 2 && (verificationData as ConsumerVerification).phoneVerification && index === 1)
                    ? 'bg-green-100 border-green-500 text-green-700'
                    : 'bg-gray-100 border-gray-300 text-gray-600'
                }`}
                onClick={() => setCurrentStep(index)}
              >
                {step.completed || (user?.role === 'CONSUMER' && index < 2 && user?.isVerified && index === 0) || (user?.role === 'CONSUMER' && index < 2 && (verificationData as ConsumerVerification).phoneVerification && index === 1) ? (
                  <CheckCircle className="h-5 w-5 text-green-600" />
                ) : (
                  <div className={`w-5 h-5 rounded-full border-2 flex items-center justify-center font-bold text-xs ${
                    index === currentStep ? 'border-blue-500 bg-blue-500 text-white' : 'border-gray-400 text-gray-400'
                  }`}>
                    {index + 1}
                  </div>
                )}
                <span className="text-sm font-medium">
                  {step.title}
                </span>
              </div>
            ))}
          </div>
        </div>

        {/* Current Step Content */}
        <div className="mb-8">
          {user?.role === 'DRIVER' ? (
            <>
              {currentStep === 0 && renderDriverLicenseStep()}
              {currentStep === 1 && renderVehicleRegistrationStep()}
              {currentStep === 2 && renderFaceVerificationStep()}
            </>
          ) : (
            <>
              {currentStep === 0 && renderConsumerVerificationSteps()}
              {currentStep === 1 && renderConsumerVerificationSteps()}
              {currentStep === 2 && renderFaceVerificationStep()}
            </>
          )}
        </div>

        {/* Navigation Buttons */}
        <div className="flex justify-between">
          <Button
            variant="outline"
            onClick={() => currentStep > 0 ? setCurrentStep(currentStep - 1) : setLocation(user?.role === 'DRIVER' ? '/driver-dashboard' : '/consumer-home')}
            className="rounded-xl"
            disabled={currentStep === 0}
          >
            <ArrowLeft className="h-4 w-4 mr-2" />
            {currentStep === 0 ? 'Back to Dashboard' : 'Previous'}
          </Button>

          {currentStep < steps.length - 1 ? (
            <Button
              onClick={() => {
                // Mark current step as completed before moving to the next
                const newSteps = [...steps];
                newSteps[currentStep].completed = true;
                setVerificationProgress((newSteps.filter(s => s.completed).length / newSteps.length) * 100);
                setCurrentStep(currentStep + 1);
              }}
              disabled={!canProceed()}
              className="rounded-xl"
              style={{ backgroundColor: COLORS.PRIMARY, color: COLORS.WHITE }}
            >
              Next
              <ArrowRight className="h-4 w-4 ml-2" />
            </Button>
          ) : (
            <Button
              onClick={handleSubmit}
              disabled={!canProceed() || submitVerificationMutation.isPending}
              className="rounded-xl"
              style={{ backgroundColor: COLORS.ACTIVE, color: COLORS.WHITE }}
            >
              {submitVerificationMutation.isPending ? 'Submitting...' : 'Complete Verification'}
            </Button>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/pages/live-chat-enhanced.tsx.content:
import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarImage, AvatarFallback } from '@/components/ui/avatar';
import { useAuth } from '@/hooks/use-auth';
import { useWebSocket } from '@/hooks/use-websocket';
import { 
  ArrowLeft, 
  Send, 
  Phone, 
  Video, 
  MoreVertical, 
  Paperclip, 
  Image as ImageIcon,
  MapPin,
  Clock,
  Check,
  CheckCheck
} from 'lucide-react';
import { toast } from '@/hooks/use-toast';

interface ChatMessage {
  id: string;
  senderId: string;
  senderName: string;
  senderRole: string;
  message: string;
  timestamp: number;
  type: 'text' | 'image' | 'location' | 'system';
  attachments?: Array<{
    type: string;
    url: string;
    name: string;
  }>;
  status: 'sending' | 'sent' | 'delivered' | 'read';
  orderId?: string;
}

interface ChatParticipant {
  id: string;
  name: string;
  role: string;
  avatar?: string;
  isOnline: boolean;
  lastSeen?: number;
}

interface ChatSession {
  id: string;
  orderId?: string;
  participants: ChatParticipant[];
  messages: ChatMessage[];
  type: 'support' | 'order' | 'general';
  status: 'active' | 'closed';
}

export default function LiveChatEnhanced() {
  const { user } = useAuth();
  const [chatSessions, setChatSessions] = useState<ChatSession[]>([]);
  const [activeSession, setActiveSession] = useState<ChatSession | null>(null);
  const [message, setMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [typingUsers, setTypingUsers] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const messageInputRef = useRef<HTMLInputElement>(null);
  const typingTimeoutRef = useRef<NodeJS.Timeout>();

  const { socket, isConnected } = useWebSocket({
    onNewMessage: (data: any) => {
      const newMessage: ChatMessage = {
        id: data.id,
        senderId: data.senderId,
        senderName: data.senderName,
        senderRole: data.senderRole,
        message: data.message,
        timestamp: data.timestamp,
        type: data.type || 'text',
        attachments: data.attachments,
        status: 'delivered',
        orderId: data.orderId
      };

      setChatSessions(prev => prev.map(session => {
        if (session.id === data.chatId || session.orderId === data.orderId) {
          return {
            ...session,
            messages: [...session.messages, newMessage]
          };
        }
        return session;
      }));

      // Show notification if not in active session
      if (activeSession?.id !== data.chatId) {
        toast({
          title: `New message from ${data.senderName}`,
          description: data.message.slice(0, 50) + (data.message.length > 50 ? '...' : ''),
        });
      }
    },
    onTypingUpdate: (data: any) => {
      setTypingUsers(prev => {
        if (data.isTyping) {
          return prev.includes(data.userId) ? prev : [...prev, data.userId];
        } else {
          return prev.filter(id => id !== data.userId);
        }
      });

      // Clear typing indicator after 3 seconds
      setTimeout(() => {
        setTypingUsers(prev => prev.filter(id => id !== data.userId));
      }, 3000);
    },
    onUserStatusUpdate: (data: any) => {
      setChatSessions(prev => prev.map(session => ({
        ...session,
        participants: session.participants.map(participant => 
          participant.id === data.userId 
            ? { ...participant, isOnline: data.isOnline, lastSeen: data.lastSeen }
            : participant
        )
      })));
    },
    onRoleBasedMessage: (data: any) => {
      // Handle role-specific message features
      if (data.messageType === 'driver_location') {
        // Handle driver location updates
        setChatSessions(prev => prev.map(session => {
          if (session.id === data.chatId) {
            const locationMessage: ChatMessage = {
              id: data.id,
              senderId: data.senderId,
              senderName: data.senderName,
              senderRole: 'driver',
              message: `ğŸ“ Location shared`,
              timestamp: data.timestamp,
              type: 'location',
              status: 'delivered',
              orderId: data.orderId
            };
            return { ...session, messages: [...session.messages, locationMessage] };
          }
          return session;
        }));
      }
    }
  });

  const loadChatSessions = async () => {
    try {
      const response = await fetch('/api/chat/sessions', {
        credentials: 'include'
      });

      if (!response.ok) throw new Error('Failed to load chat sessions');

      const data = await response.json();
      setChatSessions(data.sessions || []);

      // Auto-select first active session
      const activeSessions = data.sessions.filter((s: ChatSession) => s.status === 'active');
      if (activeSessions.length > 0) {
        setActiveSession(activeSessions[0]);
        loadChatMessages(activeSessions[0].id);
      }
    } catch (error) {
      console.error('Error loading chat sessions:', error);
      toast({
        title: "Error",
        description: "Failed to load chat sessions",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const loadChatMessages = async (chatId: string) => {
    try {
      const response = await fetch(`/api/chat/messages/${chatId}`, {
        credentials: 'include'
      });

      if (!response.ok) throw new Error('Failed to load messages');

      const data = await response.json();

      setChatSessions(prev => prev.map(session => 
        session.id === chatId 
          ? { ...session, messages: data.messages || [] }
          : session
      ));
    } catch (error) {
      console.error('Error loading messages:', error);
    }
  };

  const sendMessage = async () => {
    if (!message.trim() || !activeSession || !socket) return;

    const tempMessage: ChatMessage = {
      id: 'temp_' + Date.now(),
      senderId: user?.id || '',
      senderName: user?.firstName + ' ' + user?.lastName || '',
      senderRole: user?.role || '',
      message: message.trim(),
      timestamp: Date.now(),
      type: 'text',
      status: 'sending'
    };

    // Add message optimistically
    setChatSessions(prev => prev.map(session => 
      session.id === activeSession.id 
        ? { ...session, messages: [...session.messages, tempMessage] }
        : session
    ));

    try {
      const response = await fetch('/api/chat/send-message', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify({
          chatId: activeSession.id,
          message: message.trim(),
          type: 'text',
          orderId: activeSession.orderId
        })
      });

      if (!response.ok) throw new Error('Failed to send message');

      const data = await response.json();

      // Update message with server response
      setChatSessions(prev => prev.map(session => 
        session.id === activeSession.id 
          ? { 
              ...session, 
              messages: session.messages.map(msg => 
                msg.id === tempMessage.id 
                  ? { ...msg, id: data.messageId, status: 'sent' }
                  : msg
              )
            }
          : session
      ));

      setMessage('');
      setIsTyping(false);
    } catch (error) {
      console.error('Error sending message:', error);

      // Update message status to failed
      setChatSessions(prev => prev.map(session => 
        session.id === activeSession.id 
          ? { 
              ...session, 
              messages: session.messages.map(msg => 
                msg.id === tempMessage.id 
                  ? { ...msg, status: 'sent' } // Keep as sent for now
                  : msg
              )
            }
          : session
      ));

      toast({
        title: "Error",
        description: "Failed to send message",
        variant: "destructive"
      });
    }
  };

  const handleTyping = useCallback(() => {
    if (!activeSession || !socket) return;

    if (!isTyping) {
      setIsTyping(true);
      socket.emit('typing_start', { 
        chatId: activeSession.id,
        userId: user?.id,
        userName: user?.firstName + ' ' + user?.lastName
      });
    }

    // Clear existing timeout
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    // Set new timeout
    typingTimeoutRef.current = setTimeout(() => {
      setIsTyping(false);
      socket.emit('typing_stop', { 
        chatId: activeSession.id,
        userId: user?.id 
      });
    }, 2000);
  }, [activeSession, socket, user, isTyping]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const formatTime = (timestamp: number) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const getMessageStatusIcon = (status: string) => {
    switch (status) {
      case 'sending': return <Clock className="h-3 w-3 text-gray-400" />;
      case 'sent': return <Check className="h-3 w-3 text-gray-400" />;
      case 'delivered': return <CheckCheck className="h-3 w-3 text-gray-400" />;
      case 'read': return <CheckCheck className="h-3 w-3 text-blue-500" />;
      default: return null;
    }
  };

  useEffect(() => {
    loadChatSessions();
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [activeSession?.messages]);

  useEffect(() => {
    if (activeSession && socket) {
      socket.emit('join_chat', { chatId: activeSession.id });

      return () => {
        socket.emit('leave_chat', { chatId: activeSession.id });
      };
    }
  }, [activeSession, socket]);

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 p-4">
        <div className="max-w-4xl mx-auto">
          <div className="animate-pulse space-y-4">
            <div className="h-8 bg-gray-200 rounded w-1/4"></div>
            <div className="h-96 bg-gray-200 rounded-xl"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-6xl mx-auto flex h-screen">
        {/* Chat Sessions Sidebar */}
        <div className="w-80 bg-white border-r flex flex-col">
          <div className="p-4 border-b">
            <h2 className="text-lg font-semibold">Messages</h2>
            <div className="flex items-center space-x-2 mt-2">
              <div className={`w-2 h-2 rounded-full ${isConnected ? 'bg-green-400' : 'bg-red-400'}`}></div>
              <span className="text-xs text-gray-600">
                {isConnected ? 'Connected' : 'Disconnected'}
              </span>
            </div>
          </div>

          <div className="flex-1 overflow-y-auto">
            {chatSessions.map(session => (
              <div
                key={session.id}
                onClick={() => {
                  setActiveSession(session);
                  loadChatMessages(session.id);
                }}
                className={`p-4 border-b cursor-pointer hover:bg-gray-50 ${
                  activeSession?.id === session.id ? 'bg-blue-50 border-r-4 border-r-blue-500' : ''
                }`}
              >
                <div className="flex items-center space-x-3">
                  <div className="relative">
                    <Avatar className="w-10 h-10">
                      <AvatarImage src={session.participants[0]?.avatar} />
                      <AvatarFallback>
                        {session.participants[0]?.name.split(' ').map(n => n[0]).join('').toUpperCase()}
                      </AvatarFallback>
                    </Avatar>
                    {session.participants[0]?.isOnline && (
                      <div className="absolute -bottom-0.5 -right-0.5 w-3 h-3 bg-green-400 rounded-full border-2 border-white"></div>
                    )}
                  </div>

                  <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between">
                      <p className="font-medium text-sm truncate">
                        {session.participants.filter(p => p.id !== user?.id)[0]?.name || 'Unknown'}
                      </p>
                      <Badge 
                        variant="secondary" 
                        className="text-xs"
                      >
                        {session.type}
                      </Badge>
                    </div>

                    {session.orderId && (
                      <p className="text-xs text-gray-500">Order #{session.orderId.slice(-6)}</p>
                    )}

                    {session.messages.length > 0 && (
                      <p className="text-xs text-gray-600 truncate mt-1">
                        {session.messages[session.messages.length - 1].message}
                      </p>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Chat Area */}
        <div className="flex-1 flex flex-col">
          {activeSession ? (
            <>
              {/* Chat Header */}
              <div className="p-4 bg-white border-b flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <Avatar className="w-10 h-10">
                    <AvatarImage src={activeSession.participants[0]?.avatar} />
                    <AvatarFallback>
                      {activeSession.participants.filter(p => p.id !== user?.id)[0]?.name.split(' ').map(n => n[0]).join('').toUpperCase()}
                    </AvatarFallback>
                  </Avatar>
                  <div>
                    <h3 className="font-medium">
                      {activeSession.participants.filter(p => p.id !== user?.id)[0]?.name}
                    </h3>
                    <p className="text-xs text-gray-600">
                      {activeSession.participants.filter(p => p.id !== user?.id)[0]?.isOnline 
                        ? 'Online' 
                        : 'Last seen ' + formatTime(activeSession.participants.filter(p => p.id !== user?.id)[0]?.lastSeen || Date.now())}
                    </p>
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  <Button variant="outline" size="sm">
                    <Phone className="h-4 w-4" />
                  </Button>
                  <Button variant="outline" size="sm">
                    <Video className="h-4 w-4" />
                  </Button>
                  <Button variant="outline" size="sm">
                    <MoreVertical className="h-4 w-4" />
                  </Button>
                </div>
              </div>

              {/* Messages Area */}
              <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {activeSession.messages.map((msg, index) => (
                  <div
                    key={msg.id}
                    className={`flex ${msg.senderId === user?.id ? 'justify-end' : 'justify-start'}`}
                  >
                    <div
                      className={`max-w-xs lg:max-w-md px-4 py-2 rounded-2xl ${
                        msg.senderId === user?.id
                          ? 'bg-blue-500 text-white'
                          : 'bg-gray-100 text-gray-900'
                      }`}
                    >
                      {msg.type === 'location' ? (
                        <div className="flex items-center space-x-2">
                          <MapPin className="h-5 w-5" />
                          <p className="text-sm">Location shared</p>
                        </div>
                      ) : (
                        <p className="text-sm">{msg.message}</p>
                      )}
                      <div className={`flex items-center justify-end space-x-1 mt-1 ${
                        msg.senderId === user?.id ? 'text-blue-100' : 'text-gray-500'
                      }`}>
                        <span className="text-xs">{formatTime(msg.timestamp)}</span>
                        {msg.senderId === user?.id && getMessageStatusIcon(msg.status)}
                      </div>
                    </div>
                  </div>
                ))}

                {typingUsers.length > 0 && (
                  <div className="flex justify-start">
                    <div className="bg-gray-100 px-4 py-2 rounded-2xl">
                      <div className="flex space-x-1">
                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                      </div>
                    </div>
                  </div>
                )}

                <div ref={messagesEndRef} />
              </div>

              {/* Message Input */}
              <div className="p-4 bg-white border-t">
                <div className="flex items-center space-x-2">
                  <Button variant="outline" size="sm">
                    <Paperclip className="h-4 w-4" />
                  </Button>

                  <div className="flex-1 relative">
                    <Input
                      ref={messageInputRef}
                      value={message}
                      onChange={(e) => {
                        setMessage(e.target.value);
                        handleTyping();
                      }}
                      onKeyPress={(e) => {
                        if (e.key === 'Enter') {
                          sendMessage();
                        }
                      }}
                      placeholder="Type a message..."
                      className="rounded-full pr-12"
                    />

                    <Button
                      onClick={sendMessage}
                      disabled={!message.trim()}
                      size="sm"
                      className="absolute right-1 top-1 rounded-full"
                    >
                      <Send className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </div>
            </>
          ) : (
            <div className="flex-1 flex items-center justify-center">
              <div className="text-center">
                <div className="w-16 h-16 bg-gray-200 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Send className="h-8 w-8 text-gray-400" />
                </div>
                <h3 className="text-lg font-medium text-gray-900 mb-2">No conversation selected</h3>
                <p className="text-gray-600">Choose a conversation from the sidebar to start messaging</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/pages/location-setup.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { MapPin, Navigation, ArrowLeft } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import logo from "../assets/images/logo.png";
import globeIcon from "../assets/images/globe_img.png";
import mapBackground from "../assets/images/map_background.png";

interface LocationData {
  latitude: number;
  longitude: number;
  address: string;
  isAutomatic: boolean;
}

export default function LocationSetup() {
  const [, setLocation] = useLocation();
  const [isLoading, setIsLoading] = useState(false);
  const [manualAddress, setManualAddress] = useState("");
  const [showManualInput, setShowManualInput] = useState(false);

  const handleAutomaticLocation = async () => {
    setIsLoading(true);

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const locationData: LocationData = {
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
            address: await reverseGeocode(position.coords.latitude, position.coords.longitude),
            isAutomatic: true
          };

          localStorage.setItem("userLocation", JSON.stringify(locationData));
          setIsLoading(false);
          setLocation("/map-home");
        },
        (error) => {
          console.error("Error getting location:", error);
          setIsLoading(false);
          setShowManualInput(true);
        },
        {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 60000
        }
      );
    } else {
      setIsLoading(false);
      setShowManualInput(true);
    }
  };

  const handleManualLocation = () => {
    if (manualAddress.trim()) {
      const locationData: LocationData = {
        latitude: 9.0765, // Default Abuja coordinates
        longitude: 7.3986,
        address: manualAddress,
        isAutomatic: false
      };

      localStorage.setItem("userLocation", JSON.stringify(locationData));
      setLocation("/map-home");
    }
  };

  const handleSetLater = () => {
    // Set default location and proceed
    const locationData: LocationData = {
      latitude: 9.0765, // Abuja coordinates
      longitude: 7.3986,
      address: "Abuja, Nigeria",
      isAutomatic: false
    };

    localStorage.setItem("userLocation", JSON.stringify(locationData));
    setLocation("/map-home");
  };

  // Mock reverse geocoding - would use real service in production
  const reverseGeocode = async (lat: number, lng: number): Promise<string> => {
    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Mock address based on coordinates
    if (lat > 9 && lat < 10 && lng > 7 && lng < 8) {
      return "Wuse II, Abuja, Nigeria";
    } else if (lat > 6 && lat < 7 && lng > 3 && lng < 4) {
      return "Victoria Island, Lagos, Nigeria";
    } else {
      return `${lat.toFixed(4)}, ${lng.toFixed(4)}`;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-green-100 relative">
      {/* Map Background Pattern */}
      <div className="absolute inset-0 opacity-20">
        <div className="grid grid-cols-8 grid-rows-12 h-full w-full">
          {Array.from({ length: 96 }).map((_, i) => (
            <div key={i} className="border border-gray-300"></div>
          ))}
        </div>
      </div>

      {/* Header */}
      <div className="relative z-10 bg-white/95 backdrop-blur-sm shadow-sm">
        <div className="flex items-center justify-between p-4">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setLocation("/onboarding")}
          >
            <ArrowLeft className="w-5 h-5" />
          </Button>

          <div className="flex items-center space-x-2">
            <img src={logo} alt="Brillprime" className="w-8 h-8" />
            <span className="font-semibold text-[#131313]">Location Setup</span>
          </div>

          <div className="w-10" /> {/* Spacer */}
        </div>
      </div>

      {/* Content */}
      <div 
        className="relative z-10 flex items-center justify-center min-h-[80vh] p-4 pt-20"
        style={{
          backgroundImage: `url(${mapBackground})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat'
        }}
      >
        {/* Background overlay for better text readability - gradient from transparent to semi-opaque */}
        <div className="absolute inset-0 bg-gradient-to-b from-transparent via-white/40 to-white/80"></div>
        <div className="relative z-10 w-full max-w-lg space-y-4">
          {/* Location Icon */}
          <div className="text-center mb-4">
            <div className="w-20 h-20 bg-[#4682b4] rounded-full flex items-center justify-center mx-auto mb-3 shadow-xl overflow-hidden">
              <img src={globeIcon} alt="Globe" className="w-14 h-14" />
            </div>
            <h1 className="text-xl font-bold text-[#131313] mb-2">
              Where are you?
            </h1>
            <p className="text-gray-600 text-sm">
              We need your location to find nearby fuel stations and provide accurate delivery services
            </p>
          </div>

          {/* Action Buttons */}
          <div className="space-y-3">
            <Button
              onClick={handleAutomaticLocation}
              disabled={isLoading}
              className="w-full h-12 bg-[#4682b4] hover:bg-[#0b1a51] text-white rounded-xl shadow-lg"
            >
              <div className="flex items-center space-x-3">
                <Navigation className="w-6 h-6" />
                <div className="text-left">
                  <div className="font-semibold">
                    {isLoading ? "Getting Location..." : "Set Automatically"}
                  </div>
                  <div className="text-sm opacity-90">Use GPS location</div>
                </div>
              </div>
            </Button>

            <Button
              variant="outline"
