                        {conversation.productName && (
                          <>
                            <span className="text-xs text-gray-400">â€¢</span>
                            <span className="text-xs text-gray-600 truncate">
                              {conversation.productName}
                            </span>
                          </>
                        )}
                      </div>

                      <p className="text-sm text-gray-600 line-clamp-2 leading-snug">
                        {conversation.lastMessage}
                      </p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

