          <TabsContent value="read" className="space-y-3 mt-4">
            {readNotifications.length === 0 ? (
              <Card className="rounded-3xl border-2 border-blue-100/50 bg-white animate-fade-in-up">
                <CardContent className="p-8 text-center">
