import { useState } from "react";
import { useLocation } from "wouter";
import { ArrowLeft, MapPin, Car, Clock, CreditCard, Navigation, Ticket, CheckCircle, AlertCircle } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";

interface VehicleType {
  id: string;
  name: string;
  icon: string;
  description: string;
}

interface TollGate {
  id: string;
  name: string;
  location: string;
  highway: string;
  distance: number;
  pricePerVehicle: {
    car: number;
    suv: number;
    truck: number;
    motorcycle: number;
  };
  operatingHours: string;
  isOpen: boolean;
  estimatedTime: string;
  paymentMethods: string[];
  trafficStatus: 'light' | 'moderate' | 'heavy';
  queueTime: string;
}

interface TollTransaction {
  id: string;
  tollGateId: string;
  vehicleType: string;
  amount: number;
  timestamp: string;
  status: 'pending' | 'completed' | 'failed';
  qrCode: string;
  paymentMethod?: string;
  reference?: string;
}

export default function TollPayments() {
  const [, setLocation] = useLocation();
  const [selectedVehicle, setSelectedVehicle] = useState<string>("car");
  const [selectedTollGate, setSelectedTollGate] = useState<any>(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [activeTransactions, setActiveTransactions] = useState<TollTransaction[]>([
    {
      id: "TP-2024-0001",
      tollGateId: "lagos-ibadan-1",
      vehicleType: "car",
      amount: 600,
      timestamp: "2024-01-15 14:30",
      status: "completed",
      qrCode: "QR123456789"
    }
  ]);

  const vehicleTypes: VehicleType[] = [
    {
      id: "motorcycle",
      name: "Motorcycle", 
      icon: "ğŸï¸",
      description: "2-wheeled vehicles"
    },
    {
      id: "car",
      name: "Car",
      icon: "ğŸš—", 
      description: "Private cars & sedans"
    },
    {
      id: "suv",
      name: "SUV/Bus",
      icon: "ğŸš™",
      description: "SUVs, vans & small buses"
    },
    {
      id: "truck",
      name: "Truck",
      icon: "ğŸš›",
      description: "Heavy vehicles & trailers"
    }
  ];

  const [tollGatesData, setTollGatesData] = useState<TollGate[]>([
    {
      id: "lagos-ibadan-1",
      name: "Lagos-Ibadan Toll Plaza",
      location: "Km 20, Lagos-Ibadan Expressway",
      highway: "Lagos-Ibadan Expressway",
      distance: 18.5,
      pricePerVehicle: {
        motorcycle: 300,
        car: 600,
        suv: 1000,
        truck: 1500
      },
      operatingHours: "24 hours",
      isOpen: true,
      estimatedTime: "22 mins",
      paymentMethods: ["Cash", "Card", "Mobile"],
      trafficStatus: "moderate",
      queueTime: "5-10 mins"
    },
    {
      id: "abuja-kaduna-1",
      name: "Abuja-Kaduna Toll Gate",
      location: "Km 15, Abuja-Kaduna Highway",
      highway: "Abuja-Kaduna Highway", 
      distance: 28.2,
      pricePerVehicle: {
        motorcycle: 200,
        car: 400,
        suv: 700,
        truck: 1200
      },
      operatingHours: "24 hours",
      isOpen: true,
      estimatedTime: "35 mins",
      paymentMethods: ["Cash", "Card", "Mobile"],
      trafficStatus: "light",
      queueTime: "2-5 mins"
    },
    {
      id: "lekki-toll",
      name: "Lekki Toll Gate",
      location: "Lekki-Epe Expressway, Lagos",
      highway: "Lekki-Epe Expressway",
      distance: 12.1,
      pricePerVehicle: {
        motorcycle: 150,
        car: 300,
        suv: 500,
        truck: 800
      },
      operatingHours: "5:00 AM - 11:00 PM",
      isOpen: true,
      estimatedTime: "18 mins",
      paymentMethods: ["Card", "Mobile"],
      trafficStatus: "heavy",
      queueTime: "15-20 mins"
    },
    {
      id: "kara-bridge",
      name: "Kara Bridge Toll",
      location: "Kara, Lagos-Ibadan Expressway",
      highway: "Lagos-Ibadan Expressway",
      distance: 25.8,
      pricePerVehicle: {
        motorcycle: 250,
        car: 500,
        suv: 800,
        truck: 1300
      },
      operatingHours: "24 hours",
      isOpen: false,
      estimatedTime: "32 mins",
      paymentMethods: ["Cash", "Card"],
      trafficStatus: "light",
      queueTime: "N/A"
    }
  ]);

  const filteredTollGates = tollGatesData.filter(gate =>
    gate.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    gate.highway.toLowerCase().includes(searchQuery.toLowerCase()) ||
    gate.location.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-NG', {
      style: 'currency',
      currency: 'NGN',
      minimumFractionDigits: 0
    }).format(amount);
  };

  const handleTollSelection = (tollGate: TollGate) => {
    setSelectedTollGate(tollGate);
  };

  const handlePurchase = async () => {
    if (selectedTollGate) {
      const amount = selectedTollGate?.pricePerVehicle[selectedVehicle as keyof TollGate['pricePerVehicle']];

      try {
        const response = await fetch('/api/toll/payment', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            tollGateId: selectedTollGate.id,
            vehicleType: selectedVehicle,
            amount: amount,
            paymentMethod: 'wallet'
          }),
          credentials: 'include'
        });

        if (response.ok) {
          const result = await response.json();
          if (result.success) {
            const newTransaction: TollTransaction = {
              id: result.transaction.id,
              tollGateId: selectedTollGate.id,
              vehicleType: selectedVehicle,
              amount: amount || 0,
              timestamp: new Date().toISOString(),
              status: 'completed',
              qrCode: result.qrCode,
              paymentMethod: 'wallet',
              reference: result.transaction.reference
            };

            setActiveTransactions(prev => [...prev, newTransaction]);
            setLocation(`/toll-payment-success?transactionId=${result.transaction.id}&qrCode=${result.qrCode}`);
          } else {
             setModalData({
              isOpen: true,
              type: "error",
              title: "Payment Failed",
              message: result.message || "An error occurred during payment processing."
            });
          }
        } else {
          console.error('Failed to process toll payment');
          setModalData({
            isOpen: true,
            type: "error",
            title: "Payment Error",
            message: "Server error. Please try again later."
          });
        }
      } catch (error) {
        console.error('Error processing toll payment:', error);
        setModalData({
          isOpen: true,
          type: "error",
          title: "Network Error",
          message: "Failed to connect. Check your internet connection."
        });
      }
    }
  };

  const getTrafficStatusColor = (status: string) => {
    switch (status) {
      case 'light': return 'text-green-600';
      case 'moderate': return 'text-yellow-600';
      case 'heavy': return 'text-red-600';
      default: return 'text-gray-600';
    }
  };

  const getTrafficStatusBadgeClass = (status: string) => {
    switch (status) {
      case 'light': return 'bg-green-100 text-green-800';
      case 'moderate': return 'bg-yellow-100 text-yellow-800';
      case 'heavy': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getTransactionStatusIcon = (status: string) => {
    switch (status) {
      case 'completed': return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'pending': return <Clock className="w-4 h-4 text-yellow-500" />;
      case 'failed': return <AlertCircle className="w-4 h-4 text-red-500" />;
      default: return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 w-full max-w-sm sm:max-w-md md:max-w-lg lg:max-w-xl xl:max-w-2xl mx-auto px-2 sm:px-4">
      <div className="bg-white shadow-sm border-b">
        <div className="flex items-center justify-between p-4">
          <div className="flex items-center space-x-4">
            <Button
              variant="ghost"
              size="icon"
              onClick={() => setLocation("/consumer-home")}
            >
              <ArrowLeft className="w-5 h-5" />
            </Button>
            <div>
              <h1 className="text-lg font-semibold text-[#131313]">Electronic Toll Payments</h1>
              <p className="text-sm text-gray-600">Pay toll fees in advance</p>
            </div>
          </div>
        </div>

        <div className="px-4 pb-4">
          <Input
            placeholder="Search toll gates or highways..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="border-[#4682b4]/30 focus:border-[#4682b4]"
          />
        </div>
      </div>

      <div className="p-4 space-y-6">
        {activeTransactions.length > 0 && (
          <Card>
            <CardContent className="p-4">
              <h3 className="font-semibold text-[#131313] mb-3">Recent Transactions</h3>
              <div className="space-y-3">
                {activeTransactions.slice(0, 3).map((transaction) => {
                  const gate = tollGatesData.find(g => g.id === transaction.tollGateId);
                  return (
                    <div key={transaction.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div className="flex items-center space-x-3">
                        {getTransactionStatusIcon(transaction.status)}
                        <div>
                          <p className="font-medium text-sm">{gate?.name}</p>
                          <p className="text-xs text-gray-600">{transaction.timestamp}</p>
                        </div>
                      </div>
                      <div className="text-right">
                        <p className="font-medium text-sm">{formatCurrency(transaction.amount)}</p>
                        <Badge variant="secondary" className={`text-xs ${
                          transaction.status === 'completed' ? 'bg-green-100 text-green-800' :
                          transaction.status === 'pending' ? 'bg-yellow-100 text-yellow-800' :
                          'bg-red-100 text-red-800'
                        }`}>
                          {transaction.status}
                        </Badge>
                      </div>
                    </div>
                  );
                })}
              </div>
            </CardContent>
          </Card>
        )}

        <Card>
          <CardContent className="p-4">
            <Label className="text-sm font-medium text-[#131313] mb-3 block">Select Vehicle Type</Label>
            <div className="grid grid-cols-2 gap-3">
              {vehicleTypes.map((vehicle) => (
                <button
                  key={vehicle.id}
                  onClick={() => setSelectedVehicle(vehicle.id)}
                  className={`p-3 rounded-lg border text-left transition-colors ${
                    selectedVehicle === vehicle.id
                      ? "border-[#4682b4] bg-[#4682b4]/5"
                      : "border-gray-200 hover:border-gray-300"
                  }`}
                >
                  <div className="flex items-center space-x-2 mb-1">
                    <span className="text-lg">{vehicle.icon}</span>
                    <span className="font-medium text-sm">{vehicle.name}</span>
                  </div>
                  <p className="text-xs text-gray-600">{vehicle.description}</p>
                </button>
              ))}
            </div>
          </CardContent>
        </Card>

        <div className="space-y-4">
          <h2 className="font-semibold text-[#131313]">Available Toll Gates</h2>

          {filteredTollGates.map((gate) => (
            <Card
              key={gate.id}
              className={`cursor-pointer transition-all hover:shadow-md ${
                selectedTollGate?.id === gate.id ? "ring-2 ring-[#4682b4] ring-opacity-50" : ""
              } ${!gate.isOpen ? "opacity-60" : ""}`}
              onClick={() => gate.isOpen && handleTollSelection(gate)}
            >
              <CardContent className="p-4">
                <div className="space-y-3">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <h3 className="font-semibold text-[#131313] mb-1">{gate.name}</h3>
                      <p className="text-sm text-gray-600 mb-2">{gate.location}</p>
                      <div className="flex items-center space-x-4 text-sm text-gray-500">
                        <div className="flex items-center space-x-1">
                          <MapPin className="w-3 h-3" />
                          <span>{gate.distance} km away</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <Clock className="w-3 h-3" />
                          <span>{gate.estimatedTime}</span>
                        </div>
                      </div>
                    </div>
                    <div className="text-right">
                      <Badge variant={gate.isOpen ? "default" : "secondary"}>
                        {gate.isOpen ? "Open" : "Closed"}
                      </Badge>
                    </div>
                  </div>

                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      <Badge className={getTrafficStatusBadgeClass(gate.trafficStatus)}>
                        {gate.trafficStatus} traffic
                      </Badge>
                      <span className="text-sm text-gray-600">Queue: {gate.queueTime}</span>
                    </div>
                    <div className="text-right">
                      <p className="text-lg font-bold text-[#4682b4]">
                        {formatCurrency(gate.pricePerVehicle[selectedVehicle as keyof TollGate['pricePerVehicle']])}
                      </p>
                      <p className="text-sm text-gray-600">{gate.operatingHours}</p>
                    </div>
                  </div>

                  <div className="flex items-center space-x-2">
                    <span className="text-sm text-gray-600">Payment:</span>
                    {gate.paymentMethods.map((method) => (
                      <Badge key={method} variant="outline" className="text-xs">
                        {method}
                      </Badge>
                    ))}
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {selectedTollGate && (
          <Card className="border-[#4682b4]">
            <CardContent className="p-6">
              <h3 className="font-semibold text-[#131313] mb-4">Purchase Summary</h3>
              <div className="space-y-3">
                <div className="flex justify-between">
                  <span className="text-gray-600">Toll Gate:</span>
                  <span className="font-medium">{selectedTollGate?.name}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Vehicle Type:</span>
                  <span className="font-medium">
                    {vehicleTypes.find(v => v.id === selectedVehicle)?.name}
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Distance:</span>
                  <span className="font-medium">{selectedTollGate?.distance} km</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Current Traffic:</span>
                  <Badge className={getTrafficStatusBadgeClass(selectedTollGate?.trafficStatus || 'light')}>
                    {selectedTollGate?.trafficStatus} traffic
                  </Badge>
                </div>
                <hr />
                <div className="flex justify-between font-semibold text-lg">
                  <span>Total Amount:</span>
                  <span className="text-[#4682b4]">
                    {selectedTollGate && formatCurrency(
                      selectedTollGate!.pricePerVehicle[selectedVehicle as keyof TollGate['pricePerVehicle']]
                    )}
                  </span>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {filteredTollGates.length === 0 && (
          <div className="text-center py-12">
            <Car className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No toll gates found</h3>
            <p className="text-gray-500">Try adjusting your search or check back later</p>
          </div>
        )}
      </div>

      {selectedTollGate && (
        <div className="fixed bottom-0 left-0 right-0 p-4 bg-white border-t">
          <Button
            className="w-full h-12 bg-[#4682b4] hover:bg-[#0b1a51]"
            onClick={handlePurchase}
          >
            <Ticket className="w-5 h-5 mr-2" />
            Purchase Toll Pass
          </Button>
        </div>
      )}

      <Dialog open={modalData.isOpen} onOpenChange={(open) => setModalData({ ...modalData, isOpen: open })}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>
              {modalData.type === "success" ? (
                <div className="flex items-center text-green-600">
                  <CheckCircle className="w-6 h-6 mr-2" /> {modalData.title}
                </div>
              ) : (
                <div className="flex items-center text-red-600">
                  <AlertCircle className="w-6 h-6 mr-2" /> {modalData.title}
                </div>
              )}
            </DialogTitle>
          </DialogHeader>
          <DialogDescription>{modalData.message}</DialogDescription>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/track-order.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { ArrowLeft, MapPin, Clock, Phone, MessageSquare, Fuel, CheckCircle, Truck, Navigation } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useWebSocketDriverTracking, useWebSocketOrders } from "@/hooks/use-websocket";
import LiveMap from "@/components/ui/live-map";

interface OrderTracking {
  id: string;
  status: string;
  customerName: string;
  customerPhone: string;
  driverName: string;
  driverPhone: string;
  driverId: string;
  fuelType: string;
  quantity: number;
  pickupLocation: string;
  deliveryLocation: string;
  deliveryLatitude: number;
  deliveryLongitude: number;
  currentLocation?: {
    latitude: number;
    longitude: number;
  };
  estimatedArrival: string;
  timeline: {
    status: string;
    message: string;
    timestamp: Date;
    completed: boolean;
  }[];
}

export default function TrackOrder() {
  const [, setLocation] = useLocation();
  const { 
    connected: trackingConnected,
    driverLocations,
    etaUpdates,
    subscribeToDriverTracking
  } = useWebSocketDriverTracking();
  const { orderUpdates } = useWebSocketOrders();

  const [order, setOrder] = useState<OrderTracking | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  // Get order ID from URL
  const orderId = window.location.pathname.split('/').pop();

  // Fetch order tracking data from API
  useEffect(() => {
    const fetchOrderTracking = async () => {
      if (!orderId) return;

      try {
        const response = await fetch(`/api/tracking/order/${orderId}`, {
          credentials: 'include'
        });

        if (response.ok) {
          const data = await response.json();
          setOrder(data.tracking);
        } else {
          console.error('Failed to fetch order tracking data');
        }
      } catch (error) {
        console.error('Error fetching order tracking:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchOrderTracking();
  }, [orderId]);

  // Subscribe to real-time driver tracking
  useEffect(() => {
    if (trackingConnected && orderId) {
      subscribeToDriverTracking(orderId);
    }
  }, [trackingConnected, orderId, subscribeToDriverTracking]);

  // Update order location from WebSocket
  useEffect(() => {
    if (order && driverLocations[order.id]) {
      const locationData = driverLocations[order.id];
      setOrder(prev => prev ? {
        ...prev,
        currentLocation: {
          latitude: locationData.location.lat,
          longitude: locationData.location.lng
        },
        estimatedArrival: locationData.eta || prev.estimatedArrival
      } : null);
    }
  }, [driverLocations, order]);

  // Update order status from WebSocket
  useEffect(() => {
    if (orderUpdates[orderId || ''] && order) {
      const update = orderUpdates[orderId || ''];
      setOrder(prev => prev ? { ...prev, status: update.status } : null);
    }
  }, [orderUpdates, orderId, order]);

  // Update ETA from WebSocket
  useEffect(() => {
    if (etaUpdates[orderId || ''] && order) {
      const etaData = etaUpdates[orderId || ''];
      setOrder(prev => prev ? { 
        ...prev, 
        estimatedArrival: etaData.eta || prev.estimatedArrival 
      } : null);
    }
  }, [etaUpdates, orderId, order]);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'CONFIRMED': return 'bg-blue-100 text-blue-800';
      case 'PREPARING': return 'bg-yellow-100 text-yellow-800';
      case 'OUT_FOR_DELIVERY': return 'bg-orange-100 text-orange-800';
      case 'DELIVERED': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Loading order tracking...</p>
        </div>
      </div>
    );
  }

  if (!order) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <p className="text-gray-600">Order not found</p>
          <Button onClick={() => setLocation("/order-history")} className="mt-4">
            View Order History
          </Button>
        </div>
      </div>
    );
  }

  // Prepare map markers
  const mapMarkers = [
    {
      lat: order.deliveryLatitude,
      lng: order.deliveryLongitude,
      title: 'Delivery Location',
      type: 'delivery' as const
    }
  ];

  if (order.currentLocation) {
    mapMarkers.push({
      lat: order.currentLocation.latitude,
      lng: order.currentLocation.longitude,
      title: `Driver: ${order.driverName}`,
      type: 'delivery' as const
    });
  }

  return (
    <div className="min-h-screen bg-gray-50 w-full max-w-md mx-auto">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="flex items-center justify-between p-4">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setLocation("/order-history")}
          >
            <ArrowLeft className="w-5 h-5" />
          </Button>
          <h1 className="text-lg font-semibold text-[#131313]">Track Order</h1>
          <div className="w-10"></div>
        </div>
      </div>

      <div className="p-4 space-y-6">
        {/* Order Status */}
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between mb-3">
              <h3 className="font-semibold text-[#131313]">Order #{order.id.slice(-6)}</h3>
              <Badge className={getStatusColor(order.status)}>
                {order.status.replace('_', ' ')}
              </Badge>
            </div>
            <div className="flex items-center space-x-2 text-sm text-gray-500">
              <Clock className="w-4 h-4" />
              <span>Estimated arrival: {order.estimatedArrival}</span>
            </div>
            {trackingConnected && (
              <div className="flex items-center space-x-2 text-xs text-green-600 mt-2">
                <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                <span>Live tracking active</span>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Live Map */}
        <Card>
          <CardContent className="p-4">
            <h3 className="font-semibold text-[#131313] mb-3">Live Location</h3>
            <div className="h-64 rounded-lg overflow-hidden">
              <LiveMap
                showUserLocation={false}
                showNearbyUsers={false}
                className="w-full h-full"
                userRole="CONSUMER"
                center={order.currentLocation ? 
                  { lat: order.currentLocation.latitude, lng: order.currentLocation.longitude } :
                  { lat: order.deliveryLatitude, lng: order.deliveryLongitude }
                }
                markers={mapMarkers}
                showRoute={order.currentLocation ? {
                  start: { lat: order.currentLocation.latitude, lng: order.currentLocation.longitude },
                  end: { lat: order.deliveryLatitude, lng: order.deliveryLongitude }
                } : undefined}
              />
            </div>
          </CardContent>
        </Card>

        {/* Driver Info */}
        <Card>
          <CardContent className="p-4">
            <h3 className="font-semibold text-[#131313] mb-3">Your Driver</h3>
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div className="w-12 h-12 bg-[#4682b4] rounded-full flex items-center justify-center">
                  <span className="text-white font-medium">
                    {order.driverName.charAt(0)}
                  </span>
                </div>
                <div>
                  <p className="font-medium text-[#131313]">{order.driverName}</p>
                  <p className="text-sm text-gray-600">Fuel Delivery Driver</p>
                  <div className="flex items-center space-x-1 text-xs text-green-600">
                    <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                    <span>Live tracking</span>
                  </div>
                </div>
              </div>
              <div className="flex space-x-2">
                <Button
                  variant="outline"
                  size="icon"
                  onClick={() => window.location.href = `tel:${order.driverPhone}`}
                >
                  <Phone className="w-4 h-4" />
                </Button>
                <Button
                  variant="outline"
                  size="icon"
                  onClick={() => setLocation(`/chat/driver/${order.id}`)}
                >
                  <MessageSquare className="w-4 h-4" />
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Order Timeline */}
        <Card>
          <CardContent className="p-4">
            <h3 className="font-semibold text-[#131313] mb-4">Order Timeline</h3>
            <div className="space-y-4">
              {order.timeline.map((item, index) => (
                <div key={index} className="flex items-start space-x-3">
                  <div className={`w-6 h-6 rounded-full flex items-center justify-center ${
                    item.completed ? 'bg-green-500' : 'bg-gray-300'
                  }`}>
                    {item.completed ? (
                      <CheckCircle className="w-4 h-4 text-white" />
                    ) : (
                      <div className="w-2 h-2 bg-white rounded-full"></div>
                    )}
                  </div>
                  <div className="flex-1">
                    <p className={`font-medium ${
                      item.completed ? 'text-[#131313]' : 'text-gray-400'
                    }`}>
                      {item.message}
                    </p>
                    <p className="text-xs text-gray-500">
                      {formatTime(item.timestamp)}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Order Details */}
        <Card>
          <CardContent className="p-4">
            <h3 className="font-semibold text-[#131313] mb-3">Order Details</h3>
            <div className="space-y-2">
              <div className="flex justify-between">
                <span className="text-gray-600">Fuel Type</span>
                <span className="font-medium">{order.fuelType}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Quantity</span>
                <span className="font-medium">{order.quantity}L</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Pickup</span>
                <span className="font-medium text-right">{order.pickupLocation}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Delivery</span>
                <span className="font-medium text-right">{order.deliveryLocation}</span>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Emergency Contact */}
        <Card className="border-red-200">
          <CardContent className="p-4">
            <h3 className="font-semibold text-red-600 mb-2">Need Help?</h3>
            <p className="text-sm text-gray-600 mb-3">
              If you have any issues with your delivery, contact our support team.
            </p>
            <Button
              variant="outline"
              className="w-full border-red-200 text-red-600 hover:bg-red-50"
              onClick={() => setLocation("/support")}
            >
              Contact Support
            </Button>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/vendor-feed.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useAuth } from "@/hooks/use-auth";
import { useLocation } from "wouter";
import { 
  ArrowLeft, 
  Plus,
  Calendar,
  Clock,
  Tag,
  ShoppingCart,
  Percent,
  MapPin,
  MessageSquare,
  Heart,
  Quote
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import type { VendorPost, InsertVendorPost } from "@shared/schema";

interface ExtendedVendorPost extends VendorPost {
  vendorName: string;
  vendorProfilePicture?: string;
  productName?: string;
  productPrice?: string;
  productImage?: string;
  productId?: string; // Added to ensure it exists for mutations
  vendorId: string; // Added to ensure it exists for mutations
}

const POST_TYPES = [
  { value: "NEW_PRODUCT", label: "New Product", icon: "ğŸ†•" },
  { value: "PRODUCT_UPDATE", label: "Product Update", icon: "ğŸ”„" },
  { value: "PROMOTION", label: "Promotion", icon: "ğŸ·ï¸" },
  { value: "RESTOCK", label: "Restock Alert", icon: "ğŸ“¦" },
  { value: "ANNOUNCEMENT", label: "Announcement", icon: "ğŸ“¢" },
] as const;

export default function VendorFeed() {
  const { user } = useAuth();
  const [, setLocation] = useLocation();
  const [isCreatePostOpen, setIsCreatePostOpen] = useState(false);
  const [selectedFilter, setSelectedFilter] = useState<string>("ALL");
  const queryClient = useQueryClient();

  // Create post form state
  type PostFormState = {
    title: string;
    content: string;
    postType: typeof POST_TYPES[number]['value'];
    tags: string;
    originalPrice: string;
    discountPrice: string;
    discountPercentage: string;
    validUntil: string;
  };

  const [newPost, setNewPost] = useState<PostFormState>({
    title: "",
    content: "",
    postType: "ANNOUNCEMENT",
    tags: "",
    originalPrice: "",
    discountPrice: "",
    discountPercentage: "",
    validUntil: ""
  });

  // Create post mutation
  const createPostMutation = useMutation({
    mutationFn: (postData: InsertVendorPost) => apiRequest('POST', '/api/vendor-posts', postData),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/vendor-posts'] });
      setIsCreatePostOpen(false);
      setNewPost({
        title: "",
        content: "",
        postType: "ANNOUNCEMENT",
        tags: "",
        originalPrice: "",
        discountPrice: "",
        discountPercentage: "",
        validUntil: "",
      });
    }
  });

  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchVendorPosts = async () => {
      try {
        setLoading(true);
        setError(null);

        const params = new URLSearchParams();
        if (selectedFilter !== 'ALL') {
          params.append('postType', selectedFilter);
        }

        const response = await fetch(`/api/vendor-posts?${params}`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('authToken')}`,
            'Content-Type': 'application/json'
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();

        if (data.success) {
          setPosts(data.data || []);
        } else {
          throw new Error(data.message || 'Failed to fetch posts');
        }
      } catch (error) {
        console.error('Error fetching vendor posts:', error);
        setError(error.message);
        setPosts([]); // Set empty array on error
      } finally {
        setLoading(false);
      }
    };

    fetchVendorPosts();
  }, [selectedFilter]); // Re-fetch when filter changes

  // Add to cart mutation
  const addToCartMutation = useMutation<unknown, Error, { productId: string; quantity: number }>({
    mutationFn: (data: { productId: string; quantity: number }) => 
      apiRequest('POST', '/api/cart', { 
        userId: user?.id, 
        productId: data.productId, 
        quantity: data.quantity 
      }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/cart'] });
    }
  });

  // Add to wishlist mutation
  const addToWishlistMutation = useMutation<unknown, Error, string>({
    mutationFn: (productId: string) => 
      apiRequest('POST', '/api/wishlist', { userId: user?.id, productId }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/wishlist'] });
    }
  });

  const handleCreatePost = () => {
    if (!user || !newPost.title || !newPost.content) return;

    const postData: InsertVendorPost = {
      vendorId: user.id,
      title: newPost.title,
      content: newPost.content,
      postType: newPost.postType,
      tags: newPost.tags ? newPost.tags.split(',').map(tag => tag.trim()).filter(Boolean) : [],
      ...(newPost.originalPrice && { originalPrice: newPost.originalPrice }),
      ...(newPost.discountPrice && { discountPrice: newPost.discountPrice }),
      ...(newPost.discountPercentage && { discountPercentage: parseInt(newPost.discountPercentage) }),
      ...(newPost.validUntil && { validUntil: new Date(newPost.validUntil) })
    };

    createPostMutation.mutate(postData);
  };

  const formatTimeAgo = (date: Date | string | null | undefined) => {
    if (!date) return "";
    const postDate = typeof date === 'string' ? new Date(date) : date;
    if (!(postDate instanceof Date) || isNaN(postDate.getTime())) return 'Invalid Date';

    const now = new Date();
    const diffInSeconds = Math.floor((now.getTime() - postDate.getTime()) / 1000);
    const diffInMinutes = Math.floor(diffInSeconds / 60);
    const diffInHours = Math.floor(diffInMinutes / 60);
    const diffInDays = Math.floor(diffInHours / 24);

    if (diffInDays > 0) return `${diffInDays}d ago`;
    if (diffInHours > 0) return `${diffInHours}h ago`;
    if (diffInMinutes > 0) return `${diffInMinutes}m ago`;
    return "Just now";
  };

  const getPostTypeIcon = (postType: typeof POST_TYPES[number]['value']) => {
    const type = POST_TYPES.find(t => t.value === postType);
    return type?.icon || "ğŸ“";
  };

  const getPostTypeBadgeColor = (postType: typeof POST_TYPES[number]['value']) => {
    switch (postType) {
      case "NEW_PRODUCT": return "bg-green-100 text-green-800";
      case "PROMOTION": return "bg-red-100 text-red-800";
      case "RESTOCK": return "bg-blue-100 text-blue-800";
      case "PRODUCT_UPDATE": return "bg-yellow-100 text-yellow-800";
      default: return "bg-gray-100 text-gray-800";
    }
  };

  const handleLikePost = async (postId: string) => {
    try {
      const response = await fetch(`/api/vendor-posts/${postId}/like`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const result = await response.json();
        // Update post likes in state
        setPosts(prevPosts => 
          prevPosts.map(post => 
            post.id === postId 
              ? { ...post, likes: result.likes, isLiked: result.isLiked }
              : post
          )
        );
      }
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleSharePost = async (postId: string) => {
    try {
      const response = await fetch(`/api/vendor-posts/${postId}/share`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const result = await response.json();
        setPosts(prevPosts => 
          prevPosts.map(post => 
            post.id === postId 
              ? { ...post, shares: result.shares }
              : post
          )
        );
      }
    } catch (error) {
      console.error('Error sharing post:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b sticky top-0 z-40">
        <div className="flex items-center justify-between p-4">
          <div className="flex items-center space-x-3">
            <Button
              variant="ghost"
              size="icon"
              onClick={() => setLocation("/dashboard")}
            >
              <ArrowLeft className="h-5 w-5" />
            </Button>
            <h1 className="text-xl font-semibold text-[#131313]">Vendor Feed</h1>
          </div>

          {user?.role === "MERCHANT" && (
            <Dialog open={isCreatePostOpen} onOpenChange={setIsCreatePostOpen}>
              <DialogTrigger asChild>
                <Button className="bg-[#4682b4] hover:bg-[#0b1a51] text-white">
                  <Plus className="h-4 w-4 mr-2" />
                  Create Post
                </Button>
              </DialogTrigger>
              <DialogContent className="sm:max-w-md">
                <DialogHeader>
                  <DialogTitle>Create New Post</DialogTitle>
                </DialogHeader>
                <div className="space-y-4">
                  <div>
                    <Input
                      placeholder="Post title..."
                      value={newPost.title}
                      onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
                    />
                  </div>

                  <div>
                    <Select value={newPost.postType} onValueChange={(value: typeof POST_TYPES[number]['value']) => setNewPost({ ...newPost, postType: value })}>
                      <SelectTrigger>
                        <SelectValue placeholder="Select post type" />
                      </SelectTrigger>
                      <SelectContent>
                        {POST_TYPES.map((type) => (
                          <SelectItem key={type.value} value={type.value}>
                            {type.icon} {type.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <Textarea
                      placeholder="What's happening with your business?"
                      value={newPost.content}
                      onChange={(e) => setNewPost({ ...newPost, content: e.target.value })}
                      rows={4}
                    />
                  </div>

                  {(newPost.postType === "PROMOTION") && (
                    <div className="grid grid-cols-2 gap-2">
                      <Input
                        placeholder="Original price"
                        type="number"
                        value={newPost.originalPrice}
                        onChange={(e) => setNewPost({ ...newPost, originalPrice: e.target.value })}
                      />
                      <Input
                        placeholder="Discount %"
                        type="number"
                        value={newPost.discountPercentage}
                        onChange={(e) => setNewPost({ ...newPost, discountPercentage: e.target.value })}
                      />
                    </div>
                  )}

                  <div>
                    <Input
                      placeholder="Tags (comma separated)"
                      value={newPost.tags}
                      onChange={(e) => setNewPost({ ...newPost, tags: e.target.value })}
                    />
                  </div>

                  <Button 
                    onClick={handleCreatePost}
                    disabled={createPostMutation.isPending || !newPost.title || !newPost.content}
                    className="w-full bg-[#4682b4] hover:bg-[#0b1a51] text-white"
                  >
                    {createPostMutation.isPending ? "Creating..." : "Create Post"}
                  </Button>
                </div>
              </DialogContent>
            </Dialog>
          )}
        </div>

        {/* Filter Tabs */}
        <div className="px-4 pb-3">
          <div className="flex space-x-2 overflow-x-auto">
            <Button
              variant={selectedFilter === "ALL" ? "default" : "outline"}
              size="sm"
              onClick={() => setSelectedFilter("ALL")}
              className={selectedFilter === "ALL" ? "bg-[#4682b4] hover:bg-[#0b1a51] text-white" : ""}
            >
              All Posts
            </Button>
            {POST_TYPES.map((type) => (
              <Button
                key={type.value}
                variant={selectedFilter === type.value ? "default" : "outline"}
                size="sm"
                onClick={() => setSelectedFilter(type.value)}
                className={selectedFilter === type.value ? "bg-[#4682b4] hover:bg-[#0b1a51] text-white" : ""}
              >
                {type.icon} {type.label}
              </Button>
            ))}
          </div>
        </div>
      </div>

      {/* Posts Feed */}
      <div className="p-4 space-y-4">
        {loading ? (
          <div className="flex items-center justify-center py-12">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
          </div>
        ) : error ? (
          <div className="text-center py-8">
            <p className="text-red-600 mb-4">Error loading posts: {error}</p>
            <Button onClick={() => window.location.reload()}>
              Try Again
            </Button>
          </div>
        ) : posts.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            No posts available
          </div>
        ) : (
          posts.map((post: ExtendedVendorPost) => (
            <Card key={post.id} className="hover:shadow-md transition-shadow">
              <CardHeader className="pb-3">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <Avatar className="h-10 w-10">
                      <AvatarImage src={post.vendorProfilePicture} />
                      <AvatarFallback className="bg-[#4682b4] text-white">
                        {post.vendorName?.charAt(0)}
                      </AvatarFallback>
                    </Avatar>
                    <div>
                      <p className="font-medium text-[#131313]">{post.vendorName}</p>
                      <div className="flex items-center space-x-2 text-sm text-gray-500">
                        <Clock className="h-3 w-3" />
                        <span>{post.createdAt ? formatTimeAgo(post.createdAt) : 'Unknown'}</span>
                      </div>
                    </div>
                  </div>
                  <Badge className={`${getPostTypeBadgeColor(post.postType)} border-0`}>
                    {getPostTypeIcon(post.postType)} {POST_TYPES.find(t => t.value === post.postType)?.label}
                  </Badge>
                </div>
              </CardHeader>

              <CardContent className="pt-0">
                <h3 className="font-semibold text-[#131313] mb-2">{post.title}</h3>
                <p className="text-gray-700 mb-3 whitespace-pre-wrap">{post.content}</p>

                {/* Promotion Details */}
                {post.postType === "PROMOTION" && (post.originalPrice || post.discountPercentage) && (
                  <div className="bg-red-50 border border-red-200 rounded-lg p-3 mb-3">
                    <div className="flex items-center space-x-2 mb-2">
                      <Percent className="h-4 w-4 text-red-600" />
                      <span className="font-medium text-red-800">Special Offer</span>
                    </div>
                    {post.originalPrice && (
                      <div className="text-sm">
                        <span className="text-gray-500 line-through">â‚¦{parseFloat(post.originalPrice).toLocaleString()}</span>
                        {post.discountPercentage && (
                          <span className="ml-2 text-red-600 font-bold">
                            {post.discountPercentage}% OFF
                          </span>
                        )}
                      </div>
                    )}
                    {post.validUntil && (
                      <div className="flex items-center space-x-1 text-xs text-red-600 mt-1">
                        <Calendar className="h-3 w-3" />
                        <span>Valid until {new Date(post.validUntil).toLocaleDateString()}</span>
                      </div>
                    )}
                  </div>
                )}

                {/* Tags */}
                {post.tags && post.tags.length > 0 && (
                  <div className="flex items-center space-x-2 mb-3">
                    <Tag className="h-3 w-3 text-gray-400" />
                    <div className="flex flex-wrap gap-1">
                      {post.tags.map((tag, index) => (
                        <Badge key={index} variant="secondary" className="text-xs">
                          {tag}
                        </Badge>
                      ))}
                    </div>
                  </div>
                )}

                {/* Linked Product */}
                {post.productName && (
                  <div className="bg-blue-50 border border-blue-200 rounded-lg p-3 mb-3">
                    <div className="flex items-center justify-between">
                      <div className="flex-1">
                        <p className="font-medium text-[#131313]">{post.productName}</p>
                        {post.productPrice && (
                          <p className="text-[#4682b4] font-bold">â‚¦{parseFloat(post.productPrice).toLocaleString()}</p>
                        )}
                      </div>
                      <div className="flex space-x-2">
                        <Button 
                          size="sm" 
                          onClick={() => addToCartMutation.mutate({ productId: post.productId!, quantity: 1 })}
                          className="bg-[#4682b4] hover:bg-[#0b1a51] text-white"
                          disabled={addToCartMutation.isPending}
                        >
                          <ShoppingCart className="h-3 w-3 mr-1" />
                          Cart
                        </Button>
                        <Button 
                          size="sm" 
                          variant="outline"
                          onClick={() => addToWishlistMutation.mutate(post.productId!)}
                          className="border-[#4682b4] text-[#4682b4] hover:bg-[#4682b4] hover:text-white"
                          disabled={addToWishlistMutation.isPending}
                        >
                          <Heart className="h-3 w-3" />
                        </Button>
                      </div>
                    </div>
                  </div>
                )}

                {/* Business Actions */}
                <div className="flex items-center justify-between pt-3 border-t">
                  <div className="flex items-center space-x-2">
                    {/* Add to Cart - Show for all posts */}
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => {
                        if (post.productId) {
                          addToCartMutation.mutate({ productId: post.productId, quantity: 1 });
                        }
                      }}
                      className="flex items-center space-x-1 border-[#4682b4] text-[#4682b4] hover:bg-[#4682b4] hover:text-white"
                      disabled={!post.productId || addToCartMutation.isPending}
                    >
                      <ShoppingCart className="h-3 w-3" />
                      <span>Add to Cart</span>
                    </Button>

                    {/* Get Quote */}
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => {
                        // Navigate to chat with quote request
                        setLocation(`/chat?vendorId=${post.vendorId}&productId=${post.productId}&type=QUOTE`);
                      }}
                      className="flex items-center space-x-1 border-gray-300 text-gray-600 hover:bg-gray-50"
                    >
                      <MessageSquare className="h-3 w-3" />
                      <span>Quote</span>
                    </Button>
                  </div>

                  {/* Add to Wishlist */}
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => {
                      if (post.productId) {
                        addToWishlistMutation.mutate(post.productId);
                      }
                    }}
                    className="text-gray-600 hover:text-red-500"
                    disabled={!post.productId || addToWishlistMutation.isPending}
                  >
                    <Heart className="h-4 w-4" />
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

