client/src/pages/wallet-fund-callback.ts.content:
import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { CheckCircle, XCircle, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";

export default function WalletFundCallback() {
  const [, setLocation] = useLocation();
  const [status, setStatus] = useState<'loading' | 'success' | 'failed'>('loading');
  const [amount, setAmount] = useState<number>(0);
  const [message, setMessage] = useState<string>('');

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const reference = urlParams.get('reference');

    if (reference) {
      verifyPayment(reference);
    } else {
      setStatus('failed');
      setMessage('No payment reference found');
    }
  }, []);

  const verifyPayment = async (reference: string) => {
    try {
      const response = await fetch('/api/wallet/fund/verify', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ reference }),
      });

      const result = await response.json();

      if (result.success) {
        setStatus('success');
        setAmount(result.data.amount);
        setMessage(result.data.message);
      } else {
        setStatus('failed');
        setMessage(result.error || 'Payment verification failed');
      }
    } catch (error) {
      console.error('Payment verification error:', error);
      setStatus('failed');
      setMessage('Failed to verify payment');
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-NG', {
      style: 'currency',
      currency: 'NGN',
      minimumFractionDigits: 0
    }).format(amount);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardContent className="p-6 text-center">
          {status === 'loading' && (
            <>
              <Loader2 className="w-16 h-16 animate-spin text-[#4682b4] mx-auto mb-4" />
              <h2 className="text-xl font-semibold mb-2">Verifying Payment</h2>
              <p className="text-gray-600">Please wait while we confirm your payment...</p>
            </>
          )}

          {status === 'success' && (
            <>
              <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-4" />
              <h2 className="text-xl font-semibold text-green-700 mb-2">Payment Successful!</h2>
              <p className="text-gray-600 mb-4">{message}</p>
              <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
                <p className="text-green-800 font-semibold">
                  {formatCurrency(amount)} has been added to your wallet
                </p>
              </div>
            </>
          )}

          {status === 'failed' && (
            <>
              <XCircle className="w-16 h-16 text-red-500 mx-auto mb-4" />
              <h2 className="text-xl font-semibold text-red-700 mb-2">Payment Failed</h2>
              <p className="text-gray-600 mb-6">{message}</p>
            </>
          )}

          <div className="space-y-3">
            <Button
              className="w-full bg-[#4682b4] hover:bg-[#0b1a51]"
              onClick={() => setLocation("/consumer-home")}
            >
              {status === 'success' ? 'Continue to Home' : 'Back to Home'}
            </Button>

            {status === 'failed' && (
              <Button
                variant="outline"
                className="w-full border-[#4682b4] text-[#4682b4]"
                onClick={() => setLocation("/wallet-fund")}
              >
                Try Again
              </Button>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/wallet-fund.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { ArrowLeft, Plus, CreditCard } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import masterCardLogo from "../assets/images/master_card_logo.png";
import visaCardLogo from "../assets/images/visa_card_logo.png";

const fundingAmounts = [1000, 2500, 5000, 10000, 25000, 50000];

export default function WalletFund() {
  const [, setLocation] = useLocation();
  const [selectedAmount, setSelectedAmount] = useState<number | null>(null);
  const [customAmount, setCustomAmount] = useState("");
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const paymentMethods = [
    {
      id: "card1",
      type: "Mastercard",
      number: "**** **** **** 4532",
      logo: masterCardLogo
    },
    {
      id: "card2",
      type: "Visa",
      number: "**** **** **** 8901",
      logo: visaCardLogo
    }
  ];

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-NG', {
      style: 'currency',
      currency: 'NGN',
      minimumFractionDigits: 0
    }).format(amount);
  };

  const getFinalAmount = () => {
    if (customAmount) {
      return parseInt(customAmount);
    }
    return selectedAmount || 0;
  };

  const handleFundWallet = async () => {
    const amount = getFinalAmount();
    if (amount > 0 && selectedPaymentMethod) {
      try {
        setIsLoading(true);

        // Initialize payment with wallet funding API
        const response = await fetch('/api/wallet/fund', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
          body: JSON.stringify({
            amount,
            paymentMethod: selectedPaymentMethod,
            reference: `fund_${Date.now()}`
          }),
        });

        const result = await response.json();

        if (response.ok) {
          // Show success message and redirect
          alert(`Wallet funding initiated! Amount: ${formatCurrency(amount)}`);
          setLocation('/wallet-fund/callback?status=success&amount=' + amount);
        } else {
          throw new Error(result.message || 'Payment initialization failed');
        }
      } catch (error: any) {
        console.error('Payment error:', error);
        alert(error.message || 'Payment failed. Please try again.');
      } finally {
        setIsLoading(false);
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 w-full max-w-sm sm:max-w-md md:max-w-lg lg:max-w-xl xl:max-w-2xl mx-auto px-2 sm:px-4">{/*Responsive container*/}
      {/* Header */}
      <div className="bg-white shadow-sm border-b p-4">
        <div className="flex items-center space-x-4">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setLocation("/consumer-home")}
          >
            <ArrowLeft className="w-5 h-5" />
          </Button>
          <h1 className="text-lg font-semibold text-[#131313]">Add Money</h1>
        </div>
      </div>

      <div className="p-4 space-y-6">
        {/* Quick Amount Selection */}
        <Card>
          <CardContent className="p-6">
            <h3 className="font-semibold text-[#131313] mb-4">Select Amount</h3>
            <div className="grid grid-cols-3 gap-3 mb-4">
              {fundingAmounts.map((amount) => (
                <Button
                  key={amount}
                  variant={selectedAmount === amount ? "default" : "outline"}
                  className={`h-12 ${
                    selectedAmount === amount 
                      ? "bg-[#4682b4] text-white" 
                      : "border-[#4682b4] text-[#4682b4] hover:bg-[#4682b4]/10"
                  }`}
                  onClick={() => {
                    setSelectedAmount(amount);
                    setCustomAmount("");
                  }}
                >
                  {formatCurrency(amount)}
                </Button>
              ))}
            </div>

            <div className="space-y-2">
              <Label htmlFor="custom-amount" className="text-sm font-medium">
                Or enter custom amount
              </Label>
              <Input
                id="custom-amount"
                type="number"
                placeholder="Enter amount"
                value={customAmount}
                onChange={(e) => {
                  setCustomAmount(e.target.value);
                  setSelectedAmount(null);
                }}
                className="border-[#4682b4]/30 focus:border-[#4682b4]"
              />
            </div>
          </CardContent>
        </Card>

        {/* Payment Methods */}
        <Card>
          <CardContent className="p-6">
            <h3 className="font-semibold text-[#131313] mb-4">Payment Method</h3>
            <div className="space-y-3">
              {paymentMethods.map((method) => (
                <div
                  key={method.id}
                  className={`border rounded-lg p-4 cursor-pointer transition-colors ${
                    selectedPaymentMethod === method.id
                      ? "border-[#4682b4] bg-[#4682b4]/5"
                      : "border-gray-200 hover:border-[#4682b4]/50"
                  }`}
                  onClick={() => setSelectedPaymentMethod(method.id)}
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <img src={method.logo} alt={method.type} className="w-8 h-5" />
                      <div>
                        <p className="font-medium text-[#131313]">{method.type}</p>
                        <p className="text-sm text-gray-600">{method.number}</p>
                      </div>
                    </div>
                    <div className={`w-4 h-4 rounded-full border-2 ${
                      selectedPaymentMethod === method.id
                        ? "border-[#4682b4] bg-[#4682b4]"
                        : "border-gray-300"
                    }`}>
                      {selectedPaymentMethod === method.id && (
