import { useCallback } from 'react';
import { useDeviceInfo } from './useDeviceInfo';
import { storageService } from '../utils/storage';
import { apiService } from '../services/api';

interface AnalyticsEvent {
  eventName: string;
  properties?: Record<string, any>;
  timestamp: string;
  userId?: string;
  sessionId?: string;
}

export const useAnalytics = () => {
  const { deviceInfo } = useDeviceInfo();

  const trackEvent = useCallback(async (
    eventName: string,
    properties: Record<string, any> = {}
  ) => {
    try {
      const userSession = await storageService.getUserSession();

      const event: AnalyticsEvent = {
        eventName,
        properties: {
          ...properties,
          platform: deviceInfo?.platform,
          deviceModel: deviceInfo?.model,
          appVersion: deviceInfo?.appVersion,
          screenWidth: deviceInfo?.screenWidth,
          screenHeight: deviceInfo?.screenHeight,
        },
        timestamp: new Date().toISOString(),
        userId: userSession?.user?.id,
        sessionId: userSession?.sessionId,
      };

      // Send to analytics service
      await apiService.post('/analytics/events', event);
    } catch (error) {
      console.error('Analytics tracking error:', error);
      // Fail silently for analytics
    }
  }, [deviceInfo]);

  const trackScreen = useCallback(async (screenName: string, properties: Record<string, any> = {}) => {
    await trackEvent('screen_view', {
      screen_name: screenName,
      ...properties,
    });
  }, [trackEvent]);

  const trackUserAction = useCallback(async (action: string, properties: Record<string, any> = {}) => {
    await trackEvent('user_action', {
      action,
      ...properties,
    });
  }, [trackEvent]);

  const trackError = useCallback(async (error: Error, context?: string) => {
    await trackEvent('error', {
      error_message: error.message,
      error_stack: error.stack,
      context,
    });
  }, [trackEvent]);

  return {
    trackEvent,
    trackScreen,
    trackUserAction,
    trackError,
  };
};

mobile/src/hooks/useAuth.tsx.content:
import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { mobileConfig } from '../shared/config';
import api from './api'; // Assuming you have an api instance configured

interface User {
  id: string;
  email: string;
  name: string;
  role: string;
}

interface AuthContextType {
  user: User | null;
  isLoading: boolean;
  signIn: (email: string, password: string) => Promise<{ success: boolean; error?: string }>;
  signUp: (userData: any) => Promise<{ success: boolean; error?: string }>;
  signOut: () => Promise<void>;
  isAuthenticated: boolean;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  const signIn = async (email: string, password: string) => {
    try {
      setIsLoading(true);
      const response = await api.post('/auth/signin', { email, password });
      const userData = response.data.user;
      setUser(userData);
      await AsyncStorage.setItem('user', JSON.stringify(userData));
      await AsyncStorage.setItem('token', response.data.token);
      return { success: true };
    } catch (error: any) {
      return {
        success: false,
        error: error.response?.data?.error || 'Sign in failed'
      };
    } finally {
      setIsLoading(false);
    }
  };

  const signUp = async (userData: any) => {
    try {
      setIsLoading(true);
      const response = await api.post('/auth/signup', userData);
      return { success: true };
    } catch (error: any) {
      return {
        success: false,
        error: error.response?.data?.error || 'Sign up failed'
      };
    } finally {
      setIsLoading(false);
    }
  };

  const signOut = async () => {
    setUser(null);
    await AsyncStorage.removeItem('user');
    await AsyncStorage.removeItem('token');
  };

  const checkAuthStatus = async () => {
    try {
      const storedUser = await AsyncStorage.getItem('user');
      const token = await AsyncStorage.getItem('token');

      if (storedUser && token) {
        setUser(JSON.parse(storedUser));
      }
    } catch (error) {
      console.error('Auth check failed:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    checkAuthStatus();
  }, []);

  const value = {
    user,
    isLoading,
    signIn,
    signUp,
    signOut,
    isAuthenticated: !!user,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

