import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity, Alert } from 'react-native';
import { NavigationProps } from '../shared/types';
import { apiService } from '../services/api';

interface CartItem {
  id: string;
  productName: string;
  price: number;
  quantity: number;
  merchantName: string;
}

interface PaymentMethod {
  id: string;
  type: 'card' | 'wallet' | 'bank';
  lastFour?: string;
  bankName?: string;
  cardType?: string;
}

const CheckoutScreen: React.FC<NavigationProps> = ({ navigation }) => {
  const [cartItems, setCartItems] = useState<CartItem[]>([]);
  const [paymentMethods, setPaymentMethods] = useState<PaymentMethod[]>([]);
  const [selectedPayment, setSelectedPayment] = useState<string | null>(null);
  const [deliveryAddress, setDeliveryAddress] = useState('');
  const [loading, setLoading] = useState(true);
  const [processing, setProcessing] = useState(false);

  useEffect(() => {
    loadCheckoutData();
  }, []);

  const loadCheckoutData = async () => {
    try {
      const [cartResponse, paymentResponse] = await Promise.all([
        apiService.get('/api/cart'),
        apiService.get('/api/payments/methods')
      ]);

      if (cartResponse.success) {
        setCartItems(cartResponse.data || []);
      }

      if (paymentResponse.success) {
        setPaymentMethods(paymentResponse.data || []);
        if (paymentResponse.data?.length > 0) {
          setSelectedPayment(paymentResponse.data[0].id);
        }
      }
    } catch (error) {
      console.error('Error loading checkout data:', error);
    } finally {
      setLoading(false);
    }
  };

  const calculateTotals = () => {
    const subtotal = cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
    const deliveryFee = 500; // Fixed delivery fee
    const total = subtotal + deliveryFee;
    return { subtotal, deliveryFee, total };
  };

  const processOrder = async () => {
    if (!selectedPayment) {
      Alert.alert('Payment Required', 'Please select a payment method');
      return;
    }

    if (!deliveryAddress.trim()) {
      Alert.alert('Address Required', 'Please provide a delivery address');
      return;
    }

    const { total } = calculateTotals();

    Alert.alert(
      'Confirm Order',
      `Total: â‚¦${total.toLocaleString()}\nProceed with payment?`,
      [
        { text: 'Cancel', style: 'cancel' },
        {
          text: 'Confirm',
          onPress: async () => {
            setProcessing(true);
            try {
              const response = await apiService.post('/api/orders', {
                items: cartItems,
                paymentMethodId: selectedPayment,
                deliveryAddress: deliveryAddress.trim(),
                totalAmount: total,
              });

              if (response.success) {
                // Clear cart after successful order
                await apiService.delete('/api/cart');

                Alert.alert(
                  'Order Placed!',
                  'Your order has been placed successfully. You can track it from your order history.',
                  [{ text: 'OK', onPress: () => navigation.navigate('OrderHistory') }]
                );
              }
            } catch (error: any) {
              Alert.alert('Order Failed', error.message || 'Failed to place order. Please try again.');
            } finally {
              setProcessing(false);
            }
          }
        }
      ]
    );
  };

  const getPaymentMethodIcon = (method: PaymentMethod) => {
    switch (method.type) {
      case 'card': return 'ğŸ’³';
      case 'wallet': return 'ğŸ’°';
      case 'bank': return 'ğŸ¦';
      default: return 'ğŸ’³';
    }
  };

  const getPaymentMethodDisplay = (method: PaymentMethod) => {
    switch (method.type) {
      case 'card':
        return `${method.cardType} ****${method.lastFour}`;
      case 'wallet':
        return 'BrillPrime Wallet';
      case 'bank':
        return `${method.bankName} ****${method.lastFour}`;
      default:
        return 'Payment Method';
    }
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <Text style={styles.loadingText}>Loading checkout...</Text>
      </View>
    );
  }

  const { subtotal, deliveryFee, total } = calculateTotals();

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()}>
          <Text style={styles.backButton}>â† Back</Text>
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Checkout</Text>
        <View style={styles.placeholder} />
      </View>

      {/* Order Summary */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Order Summary</Text>
        {cartItems.map((item) => (
          <View key={item.id} style={styles.orderItem}>
            <Text style={styles.itemName}>{item.productName}</Text>
            <Text style={styles.itemDetails}>
              {item.quantity}x â‚¦{item.price.toLocaleString()} from {item.merchantName}
            </Text>
            <Text style={styles.itemTotal}>â‚¦{(item.price * item.quantity).toLocaleString()}</Text>
          </View>
        ))}
      </View>

      {/* Delivery Address */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Delivery Address</Text>
        <TouchableOpacity style={styles.addressInput}>
          <Text style={styles.addressLabel}>Deliver to:</Text>
          <Text style={styles.addressText}>
            {deliveryAddress || 'Tap to set delivery address'}
          </Text>
        </TouchableOpacity>
      </View>

      {/* Payment Method */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Payment Method</Text>
        {paymentMethods.map((method) => (
          <TouchableOpacity
            key={method.id}
            style={[
              styles.paymentMethod,
              selectedPayment === method.id && styles.selectedPayment
            ]}
            onPress={() => setSelectedPayment(method.id)}
          >
            <Text style={styles.paymentIcon}>{getPaymentMethodIcon(method)}</Text>
            <View style={styles.paymentInfo}>
              <Text style={styles.paymentDisplay}>{getPaymentMethodDisplay(method)}</Text>
            </View>
            <View style={[
              styles.radioButton,
              selectedPayment === method.id && styles.radioButtonSelected
            ]} />
          </TouchableOpacity>
        ))}

        <TouchableOpacity
          style={styles.addPaymentButton}
          onPress={() => navigation.navigate('AddPaymentMethod')}
        >
          <Text style={styles.addPaymentText}>+ Add Payment Method</Text>
        </TouchableOpacity>
      </View>

      {/* Order Total */}
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Order Total</Text>
        <View style={styles.totalRow}>
          <Text style={styles.totalLabel}>Subtotal</Text>
          <Text style={styles.totalValue}>â‚¦{subtotal.toLocaleString()}</Text>
        </View>
        <View style={styles.totalRow}>
          <Text style={styles.totalLabel}>Delivery Fee</Text>
          <Text style={styles.totalValue}>â‚¦{deliveryFee.toLocaleString()}</Text>
        </View>
        <View style={[styles.totalRow, styles.grandTotalRow]}>
          <Text style={styles.grandTotalLabel}>Total</Text>
          <Text style={styles.grandTotalValue}>â‚¦{total.toLocaleString()}</Text>
        </View>
      </View>

      <TouchableOpacity
        style={[styles.checkoutButton, processing && styles.buttonDisabled]}
        onPress={processOrder}
        disabled={processing}
      >
        <Text style={styles.checkoutButtonText}>
          {processing ? 'Processing...' : `Pay â‚¦${total.toLocaleString()}`}
        </Text>
      </TouchableOpacity>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
