              <Text style={styles.benefitIcon}>ğŸ›¡ï¸</Text>
              <Text style={styles.benefitText}>Enhanced security features</Text>
            </View>
            <View style={styles.benefitItem}>
              <Text style={styles.benefitIcon}>â­</Text>
              <Text style={styles.benefitText}>Priority customer support</Text>
            </View>
            <View style={styles.benefitItem}>
              <Text style={styles.benefitIcon}>ğŸ’³</Text>
              <Text style={styles.benefitText}>Access to premium features</Text>
            </View>
          </View>
        </View>

        {/* Security Notice */}
        <View style={styles.securityNotice}>
