        <TabButton id="pci" title="Payment Security" />
        <TabButton id="nigerian" title="Nigerian" />
      </ScrollView>

      <ScrollView style={styles.content}>
        {renderContent()}
      </ScrollView>
    </SafeAreaView>
  );
}

