import React, { useState, useEffect, useRef, useCallback } from 'react';
import {
  View,
  Text,
  FlatList,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  KeyboardAvoidingView,
  Platform,
  Alert,
  ActivityIndicator,
  Image
} from 'react-native';
import { useNavigation } from '@react-navigation/native';

interface ChatMessage {
  id: string;
  senderId: string;
  senderName: string;
  senderRole: string;
  message: string;
  timestamp: number;
  type: 'text' | 'image' | 'location' | 'system';
  attachments?: Array<{
    type: string;
    url: string;
    name: string;
  }>;
  status: 'sending' | 'sent' | 'delivered' | 'read';
  orderId?: string;
}

interface ChatParticipant {
  id: string;
  name: string;
  role: string;
  avatar?: string;
  isOnline: boolean;
  lastSeen?: number;
}

interface ChatSession {
  id: string;
  orderId?: string;
  participants: ChatParticipant[];
  messages: ChatMessage[];
  type: 'support' | 'order' | 'general';
  status: 'active' | 'closed';
}

export default function LiveChatEnhancedScreen() {
  const navigation = useNavigation();
  const [chatSessions, setChatSessions] = useState<ChatSession[]>([]);
  const [activeSession, setActiveSession] = useState<ChatSession | null>(null);
  const [message, setMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [typingUsers, setTypingUsers] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);
  const [showSessions, setShowSessions] = useState(true);
  const flatListRef = useRef<FlatList>(null);
  const typingTimeoutRef = useRef<NodeJS.Timeout>();

  // Mock WebSocket connection
  const [isConnected, setIsConnected] = useState(true);

  const loadChatSessions = async () => {
    try {
      const response = await fetch('/api/chat/sessions', {
        credentials: 'include'
      });

      if (!response.ok) throw new Error('Failed to load chat sessions');

      const data = await response.json();
      setChatSessions(data.sessions || []);

      // Auto-select first active session
      const activeSessions = data.sessions.filter((s: ChatSession) => s.status === 'active');
      if (activeSessions.length > 0) {
        setActiveSession(activeSessions[0]);
        setShowSessions(false);
        loadChatMessages(activeSessions[0].id);
      }
    } catch (error) {
      console.error('Error loading chat sessions:', error);
      Alert.alert('Error', 'Failed to load chat sessions');
    } finally {
      setLoading(false);
    }
  };

  const loadChatMessages = async (chatId: string) => {
    try {
      const response = await fetch(`/api/chat/messages/${chatId}`, {
        credentials: 'include'
      });

      if (!response.ok) throw new Error('Failed to load messages');

      const data = await response.json();

      setChatSessions(prev => prev.map(session => 
        session.id === chatId 
          ? { ...session, messages: data.messages || [] }
          : session
      ));
    } catch (error) {
      console.error('Error loading messages:', error);
    }
  };

  const sendMessage = async () => {
    if (!message.trim() || !activeSession) return;

    const tempMessage: ChatMessage = {
      id: 'temp_' + Date.now(),
      senderId: 'current_user',
      senderName: 'You',
      senderRole: 'consumer',
      message: message.trim(),
      timestamp: Date.now(),
      type: 'text',
      status: 'sending'
    };

    // Add message optimistically
    setChatSessions(prev => prev.map(session => 
      session.id === activeSession.id 
        ? { ...session, messages: [...session.messages, tempMessage] }
        : session
    ));

    try {
      const response = await fetch('/api/chat/send-message', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify({
          chatId: activeSession.id,
          message: message.trim(),
          type: 'text',
          orderId: activeSession.orderId
        })
      });

      if (!response.ok) throw new Error('Failed to send message');

      const data = await response.json();

      // Update message with server response
      setChatSessions(prev => prev.map(session => 
        session.id === activeSession.id 
          ? { 
              ...session, 
              messages: session.messages.map(msg => 
                msg.id === tempMessage.id 
                  ? { ...msg, id: data.messageId, status: 'sent' }
                  : msg
              )
            }
          : session
      ));

      setMessage('');
      setIsTyping(false);
    } catch (error) {
      console.error('Error sending message:', error);
      Alert.alert('Error', 'Failed to send message');
    }
  };

  const formatTime = (timestamp: number) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const getMessageStatusIcon = (status: string) => {
    switch (status) {
      case 'sending': return 'â³';
      case 'sent': return 'âœ“';
      case 'delivered': return 'âœ“âœ“';
      case 'read': return 'âœ“âœ“';
      default: return '';
    }
  };

  const renderMessage = ({ item: msg, index }: { item: ChatMessage; index: number }) => {
    const isCurrentUser = msg.senderId === 'current_user';

    return (
      <View style={[
        styles.messageContainer,
        isCurrentUser ? styles.sentMessage : styles.receivedMessage
      ]}>
        <View style={[
          styles.messageBubble,
          isCurrentUser ? styles.sentBubble : styles.receivedBubble
        ]}>
          <Text style={[
            styles.messageText,
            isCurrentUser ? styles.sentText : styles.receivedText
          ]}>
            {msg.message}
          </Text>
          <View style={styles.messageFooter}>
            <Text style={[
              styles.messageTime,
              isCurrentUser ? styles.sentTime : styles.receivedTime
            ]}>
              {formatTime(msg.timestamp)}
            </Text>
            {isCurrentUser && (
              <Text style={styles.messageStatus}>
                {getMessageStatusIcon(msg.status)}
              </Text>
            )}
          </View>
        </View>
      </View>
    );
  };

  const renderSessionItem = ({ item: session }: { item: ChatSession }) => {
    const otherParticipant = session.participants.find(p => p.id !== 'current_user');
    const lastMessage = session.messages[session.messages.length - 1];

    return (
      <TouchableOpacity
        style={styles.sessionItem}
        onPress={() => {
          setActiveSession(session);
          setShowSessions(false);
          loadChatMessages(session.id);
        }}
      >
        <View style={styles.sessionAvatar}>
          <Text style={styles.avatarText}>
            {otherParticipant?.name.charAt(0).toUpperCase() || 'U'}
          </Text>
          {otherParticipant?.isOnline && (
            <View style={styles.onlineIndicator} />
          )}
        </View>

        <View style={styles.sessionInfo}>
          <View style={styles.sessionHeader}>
            <Text style={styles.sessionName}>
              {otherParticipant?.name || 'Unknown'}
            </Text>
            <View style={styles.sessionBadge}>
              <Text style={styles.sessionBadgeText}>{session.type}</Text>
            </View>
          </View>

          {session.orderId && (
            <Text style={styles.sessionOrder}>
              Order #{session.orderId.slice(-6)}
            </Text>
          )}

          {lastMessage && (
            <Text style={styles.sessionPreview} numberOfLines={1}>
              {lastMessage.message}
            </Text>
          )}
        </View>
      </TouchableOpacity>
    );
  };

  const renderTypingIndicator = () => {
    if (typingUsers.length === 0) return null;

    return (
      <View style={styles.typingContainer}>
        <View style={styles.typingBubble}>
          <View style={styles.typingDots}>
            <View style={[styles.typingDot, { animationDelay: '0ms' }]} />
            <View style={[styles.typingDot, { animationDelay: '150ms' }]} />
            <View style={[styles.typingDot, { animationDelay: '300ms' }]} />
          </View>
        </View>
      </View>
    );
  };

  useEffect(() => {
    loadChatSessions();
  }, []);

  useEffect(() => {
    if (activeSession?.messages && flatListRef.current) {
      setTimeout(() => {
        flatListRef.current?.scrollToEnd({ animated: true });
      }, 100);
    }
  }, [activeSession?.messages]);

  if (loading) {
    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#007bff" />
          <Text style={styles.loadingText}>Loading messages...</Text>
        </View>
      </SafeAreaView>
    );
  }

  if (showSessions) {
    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.header}>
          <TouchableOpacity onPress={() => navigation.goBack()}>
            <Text style={styles.backButton}>â†</Text>
          </TouchableOpacity>
          <Text style={styles.headerTitle}>Messages</Text>
          <View style={styles.connectionStatus}>
            <View style={[
              styles.connectionDot,
              { backgroundColor: isConnected ? '#28a745' : '#dc3545' }
            ]} />
            <Text style={styles.connectionText}>
              {isConnected ? 'Connected' : 'Disconnected'}
            </Text>
          </View>
        </View>

        <FlatList
          data={chatSessions}
          renderItem={renderSessionItem}
          keyExtractor={(item) => item.id}
          style={styles.sessionsList}
        />
      </SafeAreaView>
    );
  }

  return (
    <SafeAreaView style={styles.container}>
      <KeyboardAvoidingView 
        style={styles.container}
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
      >
        {/* Chat Header */}
        <View style={styles.chatHeader}>
          <TouchableOpacity onPress={() => setShowSessions(true)}>
            <Text style={styles.backButton}>â†</Text>
          </TouchableOpacity>

          <View style={styles.chatHeaderInfo}>
            <View style={styles.chatAvatar}>
              <Text style={styles.avatarText}>
                {activeSession?.participants.find(p => p.id !== 'current_user')?.name.charAt(0).toUpperCase() || 'U'}
              </Text>
            </View>
            <View>
              <Text style={styles.chatHeaderName}>
                {activeSession?.participants.find(p => p.id !== 'current_user')?.name || 'Unknown'}
              </Text>
              <Text style={styles.chatHeaderStatus}>
                {activeSession?.participants.find(p => p.id !== 'current_user')?.isOnline 
                  ? 'Online' 
                  : 'Last seen recently'}
              </Text>
            </View>
          </View>

          <View style={styles.chatActions}>
            <TouchableOpacity style={styles.actionButton}>
              <Text style={styles.actionButtonText}>ğŸ“</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.actionButton}>
              <Text style={styles.actionButtonText}>ğŸ“¹</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.actionButton}>
              <Text style={styles.actionButtonText}>â‹¯</Text>
            </TouchableOpacity>
          </View>
        </View>

        {/* Messages */}
        <FlatList
          ref={flatListRef}
          data={activeSession?.messages || []}
          renderItem={renderMessage}
          keyExtractor={(item) => item.id}
          style={styles.messagesList}
          onContentSizeChange={() => flatListRef.current?.scrollToEnd({ animated: true })}
        />

        {renderTypingIndicator()}

        {/* Message Input */}
        <View style={styles.inputContainer}>
          <TouchableOpacity style={styles.attachButton}>
            <Text style={styles.attachButtonText}>ğŸ“</Text>
          </TouchableOpacity>

          <TextInput
            style={styles.messageInput}
            value={message}
            onChangeText={setMessage}
            placeholder="Type a message..."
            multiline
            maxLength={2000}
          />

          <TouchableOpacity
            style={[
              styles.sendButton,
              { opacity: message.trim() ? 1 : 0.5 }
            ]}
            onPress={sendMessage}
            disabled={!message.trim()}
          >
            <Text style={styles.sendButtonText}>â¤</Text>
          </TouchableOpacity>
        </View>
      </KeyboardAvoidingView>
    </SafeAreaView>
  );
}

