import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView, RefreshControl, Alert } from 'react-native';
import { NavigationProps } from '../shared/types';
import { apiService } from '../services/api';

interface MerchantStats {
  totalRevenue: number;
  totalOrders: number;
  pendingOrders: number;
  completedOrders: number;
  inventoryItems: number;
  rating: number;
}

const MerchantDashboardScreen: React.FC<NavigationProps> = ({ navigation }) => {
  const [stats, setStats] = useState<MerchantStats>({
    totalRevenue: 0,
    totalOrders: 0,
    pendingOrders: 0,
    completedOrders: 0,
    inventoryItems: 0,
    rating: 0,
  });
  const [refreshing, setRefreshing] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadMerchantStats();
  }, []);

  const loadMerchantStats = async () => {
    try {
      const response = await apiService.get('/api/merchant/dashboard');
      setStats(response.data);
    } catch (error) {
      console.error('Error loading merchant stats:', error);
    } finally {
      setLoading(false);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadMerchantStats();
    setRefreshing(false);
  };

  const quickActions = [
    {
      title: 'Manage Inventory',
      icon: 'ğŸ“¦',
