      color: '#28a745',
    },
    {
      title: 'Analytics',
      icon: 'ğŸ“Š',
      onPress: () => Alert.alert('Feature', 'Analytics coming soon'),
      color: '#ffc107',
    },
    {
      title: 'Settings',
      icon: 'âš™ï¸',
      onPress: () => navigation.navigate('AccountSettings'),
      color: '#6c757d',
    },
