    {
      id: 'ORD-001',
      customerName: 'John Doe',
      items: ['Laptop Stand', 'Wireless Mouse'],
      amount: 25000,
      status: 'pending',
      time: '10 minutes ago',
    },
    {
      id: 'ORD-002',
      customerName: 'Jane Smith',
      items: ['Phone Case', 'Screen Protector'],
      amount: 8500,
      status: 'completed',
      time: '1 hour ago',
    },
