    flex: 1,
    padding: 15,
  },
  messageItem: {
    backgroundColor: '#e5f2ff',
    padding: 12,
    borderRadius: 15,
    marginBottom: 10,
    maxWidth: '80%',
    alignSelf: 'flex-start',
  },
  ownMessage: {
