import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, FlatList, TouchableOpacity, RefreshControl } from 'react-native';
import { NavigationProps } from '../shared/types';
import { apiService } from '../services/api';

interface Order {
  id: string;
  type: 'FUEL' | 'COMMODITY' | 'DELIVERY' | 'FOOD';
  productName: string;
  quantity: string;
  price: number;
  status: 'COMPLETED' | 'CANCELLED' | 'PENDING' | 'IN_PROGRESS';
  date: string;
  time: string;
  merchantName?: string;
  driverName?: string;
}

const OrderHistoryScreen: React.FC<NavigationProps> = ({ navigation }) => {
  const [orders, setOrders] = useState<Order[]>([]);
  const [refreshing, setRefreshing] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    try {
      const response = await apiService.get('/api/orders');
      if (response.success) {
        setOrders(response.data || []);
      }
    } catch (error) {
      console.error('Error loading orders:', error);
    } finally {
      setLoading(false);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadOrders();
    setRefreshing(false);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'COMPLETED': return '#00aa44';
      case 'CANCELLED': return '#ff4444';
      case 'IN_PROGRESS': return '#ffaa00';
      case 'PENDING': return '#666';
      default: return '#666';
    }
  };

  const getOrderIcon = (type: string) => {
    switch (type) {
      case 'FUEL': return 'â›½';
      case 'COMMODITY': return 'ğŸ›’';
      case 'DELIVERY': return 'ğŸ“¦';
      case 'FOOD': return 'ğŸ½ï¸';
      default: return 'ğŸ“‹';
    }
  };

  const renderOrder = ({ item }: { item: Order }) => (
    <TouchableOpacity
      style={styles.orderItem}
      onPress={() => navigation.navigate('OrderDetail', { orderId: item.id })}
    >
      <View style={styles.orderHeader}>
        <Text style={styles.orderIcon}>{getOrderIcon(item.type)}</Text>
        <View style={styles.orderInfo}>
          <Text style={styles.orderTitle}>{item.productName}</Text>
          <Text style={styles.orderQuantity}>{item.quantity}</Text>
          {item.merchantName && (
            <Text style={styles.orderMerchant}>from {item.merchantName}</Text>
          )}
          {item.driverName && (
            <Text style={styles.orderDriver}>Driver: {item.driverName}</Text>
          )}
        </View>
        <View style={styles.orderRight}>
          <Text style={styles.orderPrice}>â‚¦{item.price.toLocaleString()}</Text>
          <View style={[styles.statusBadge, { backgroundColor: getStatusColor(item.status) }]}>
            <Text style={styles.statusText}>{item.status}</Text>
          </View>
        </View>
      </View>
      <View style={styles.orderFooter}>
        <Text style={styles.orderDate}>{item.date}</Text>
        <Text style={styles.orderTime}>{item.time}</Text>
      </View>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()}>
          <Text style={styles.backButton}>â† Back</Text>
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Order History</Text>
        <View style={styles.placeholder} />
      </View>

      <FlatList
        data={orders}
