import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  ScrollView,
  Alert,
  ActivityIndicator
} from 'react-native';
import { useNavigation } from '@react-navigation/native';

interface PaymentMethod {
  id: string;
  type: 'card' | 'bank';
  last4: string;
  brand?: string;
  bankName?: string;
  isDefault: boolean;
}

export default function WalletFundScreen() {
  const navigation = useNavigation();
  const [amount, setAmount] = useState('');
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState<string>('');
  const [paymentMethods, setPaymentMethods] = useState<PaymentMethod[]>([]);
  const [loading, setLoading] = useState(false);
  const [loadingMethods, setLoadingMethods] = useState(true);

  const quickAmounts = [1000, 2500, 5000, 10000, 25000, 50000];

  useEffect(() => {
    loadPaymentMethods();
  }, []);

  const loadPaymentMethods = async () => {
    try {
      const response = await fetch('/api/payment-methods', {
        credentials: 'include'
      });

      if (!response.ok) throw new Error('Failed to load payment methods');

      const data = await response.json();
      setPaymentMethods(data.paymentMethods || []);

      // Auto-select default payment method
      const defaultMethod = data.paymentMethods?.find((pm: PaymentMethod) => pm.isDefault);
      if (defaultMethod) {
        setSelectedPaymentMethod(defaultMethod.id);
      }
    } catch (error) {
      console.error('Error loading payment methods:', error);
    } finally {
      setLoadingMethods(false);
    }
  };

  const formatAmount = (text: string) => {
    const cleaned = text.replace(/[^0-9]/g, '');
    return cleaned;
  };

  const formatCurrency = (amount: string) => {
    if (!amount) return '';
    const num = parseInt(amount);
    return new Intl.NumberFormat('en-NG', {
      style: 'currency',
      currency: 'NGN',
      minimumFractionDigits: 0
    }).format(num);
  };

  const handleQuickAmount = (quickAmount: number) => {
    setAmount(quickAmount.toString());
  };

  const validateForm = () => {
    const amountNum = parseInt(amount);

    if (!amount || amountNum < 100) {
      Alert.alert('Error', 'Minimum funding amount is â‚¦100');
      return false;
    }

    if (amountNum > 500000) {
      Alert.alert('Error', 'Maximum funding amount is â‚¦500,000');
      return false;
    }

    if (!selectedPaymentMethod) {
      Alert.alert('Error', 'Please select a payment method');
      return false;
    }

    return true;
  };

  const handleFundWallet = async () => {
    if (!validateForm()) return;

    setLoading(true);
    try {
      const response = await fetch('/api/wallet/fund', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify({
          amount: parseInt(amount),
          paymentMethodId: selectedPaymentMethod
        })
      });

      const result = await response.json();

      if (result.success) {
        Alert.alert(
          'Success',
          `Wallet funded successfully with ${formatCurrency(amount)}`,
          [
            {
              text: 'View Wallet',
              onPress: () => navigation.navigate('WalletBalance' as never)
            },
            {
              text: 'OK',
              onPress: () => navigation.goBack()
            }
          ]
        );
      } else {
        Alert.alert('Error', result.message || 'Failed to fund wallet');
      }
    } catch (error) {
      console.error('Fund wallet error:', error);
      Alert.alert('Error', 'Failed to fund wallet');
    } finally {
      setLoading(false);
    }
  };

  const renderPaymentMethod = (method: PaymentMethod) => {
    const isSelected = selectedPaymentMethod === method.id;

    return (
      <TouchableOpacity
        key={method.id}
        style={[
          styles.paymentMethodCard,
          isSelected && styles.selectedPaymentMethod
        ]}
        onPress={() => setSelectedPaymentMethod(method.id)}
      >
        <View style={styles.paymentMethodInfo}>
          <Text style={styles.paymentMethodType}>
            {method.type === 'card' ? 'ğŸ’³' : 'ğŸ¦'} {method.type === 'card' ? 'Card' : 'Bank Account'}
          </Text>
          <Text style={styles.paymentMethodDetails}>
            {method.type === 'card' 
              ? `â€¢â€¢â€¢â€¢ â€¢â€¢â€¢â€¢ â€¢â€¢â€¢â€¢ ${method.last4}${method.brand ? ` (${method.brand})` : ''}`
              : `${method.bankName} - â€¢â€¢â€¢â€¢â€¢${method.last4}`
            }
          </Text>
          {method.isDefault && (
            <Text style={styles.defaultBadge}>Default</Text>
          )}
        </View>

        <View style={[
          styles.radioButton,
          isSelected && styles.radioButtonSelected
        ]}>
          {isSelected && <View style={styles.radioButtonInner} />}
        </View>
      </TouchableOpacity>
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity onPress={() => navigation.goBack()}>
          <Text style={styles.backButton}>â†</Text>
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Fund Wallet</Text>
      </View>

      <ScrollView style={styles.content}>
        {/* Amount Section */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Enter Amount</Text>

          <View style={styles.amountContainer}>
            <Text style={styles.currencySymbol}>â‚¦</Text>
            <TextInput
              style={styles.amountInput}
              value={amount}
              onChangeText={(text) => setAmount(formatAmount(text))}
              placeholder="0"
              keyboardType="numeric"
              maxLength={8}
            />
          </View>

          {amount && (
            <Text style={styles.amountPreview}>
              {formatCurrency(amount)}
            </Text>
          )}

          {/* Quick Amount Buttons */}
          <View style={styles.quickAmountsContainer}>
            <Text style={styles.quickAmountsTitle}>Quick Amounts</Text>
            <View style={styles.quickAmountsGrid}>
              {quickAmounts.map((quickAmount) => (
                <TouchableOpacity
                  key={quickAmount}
                  style={[
                    styles.quickAmountButton,
                    amount === quickAmount.toString() && styles.selectedQuickAmount
                  ]}
                  onPress={() => handleQuickAmount(quickAmount)}
                >
                  <Text style={[
                    styles.quickAmountText,
                    amount === quickAmount.toString() && styles.selectedQuickAmountText
                  ]}>
                    â‚¦{quickAmount.toLocaleString()}
                  </Text>
                </TouchableOpacity>
              ))}
            </View>
          </View>
        </View>

        {/* Payment Methods Section */}
        <View style={styles.section}>
          <View style={styles.sectionHeader}>
            <Text style={styles.sectionTitle}>Payment Method</Text>
            <TouchableOpacity 
              onPress={() => navigation.navigate('AddPaymentMethod' as never)}
            >
              <Text style={styles.addMethodButton}>+ Add New</Text>
            </TouchableOpacity>
          </View>

          {loadingMethods ? (
            <View style={styles.loadingContainer}>
              <ActivityIndicator color="#007bff" />
              <Text style={styles.loadingText}>Loading payment methods...</Text>
            </View>
          ) : paymentMethods.length === 0 ? (
            <View style={styles.emptyContainer}>
              <Text style={styles.emptyText}>No payment methods found</Text>
              <TouchableOpacity 
                style={styles.addFirstMethodButton}
                onPress={() => navigation.navigate('AddPaymentMethod' as never)}
              >
                <Text style={styles.addFirstMethodText}>Add Payment Method</Text>
              </TouchableOpacity>
            </View>
          ) : (
            <View style={styles.paymentMethodsList}>
              {paymentMethods.map(renderPaymentMethod)}
            </View>
          )}
        </View>

        {/* Funding Limits */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Funding Limits</Text>
          <View style={styles.limitsContainer}>
            <View style={styles.limitItem}>
              <Text style={styles.limitLabel}>Minimum</Text>
              <Text style={styles.limitValue}>â‚¦100</Text>
            </View>
            <View style={styles.limitItem}>
              <Text style={styles.limitLabel}>Maximum (per transaction)</Text>
              <Text style={styles.limitValue}>â‚¦500,000</Text>
            </View>
            <View style={styles.limitItem}>
              <Text style={styles.limitLabel}>Daily Limit</Text>
              <Text style={styles.limitValue}>â‚¦2,000,000</Text>
            </View>
          </View>
        </View>

        {/* Fund Button */}
        <TouchableOpacity
          style={[
            styles.fundButton,
            (loading || !amount || !selectedPaymentMethod) && styles.fundButtonDisabled
          ]}
          onPress={handleFundWallet}
          disabled={loading || !amount || !selectedPaymentMethod}
        >
          {loading ? (
            <ActivityIndicator color="white" />
          ) : (
            <Text style={styles.fundButtonText}>
              Fund Wallet {amount && `with ${formatCurrency(amount)}`}
            </Text>
          )}
        </TouchableOpacity>

        {/* Security Notice */}
        <View style={styles.securityNotice}>
          <Text style={styles.securityTitle}>ğŸ”’ Secure Transaction</Text>
          <Text style={styles.securityText}>
            All transactions are encrypted and processed securely through our payment partners.
          </Text>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

