  biometricAvailable: boolean;
}

export const initializeApp = async (): Promise<AppInitializationResult> => {
  try {
    console.log('ğŸš€ Initializing BrillPrime Mobile App...');

    // Check if this is the first launch
    const hasLaunched = await AsyncStorage.getItem(STORAGE_KEYS.ONBOARDING_COMPLETED);
    const isFirstLaunch = !hasLaunched;

    // Check network connectivity
    const networkState = await NetInfo.fetch();
    console.log('ğŸŒ Network Status:', networkState);

    // Check for existing user session
    let hasValidSession = false;
    try {
      const sessionData = await AsyncStorage.getItem(STORAGE_KEYS.USER_SESSION);
      if (sessionData) {
        const session = JSON.parse(sessionData);
        if (session.token) {
          // Validate session with server
          const response = await apiService.getCurrentUser();
          hasValidSession = response.success;

          if (!hasValidSession) {
            // Clear invalid session
            await AsyncStorage.removeItem(STORAGE_KEYS.USER_SESSION);
          }
        }
      }
    } catch (error) {
      console.error('âŒ Session validation error:', error);
      await AsyncStorage.removeItem(STORAGE_KEYS.USER_SESSION);
    }

    // Check biometric availability
    let biometricAvailable = false;
    try {
      if (Platform.OS === 'ios' || Platform.OS === 'android') {
        // This would normally check react-native-biometrics
        // For now, we'll simulate the check
        biometricAvailable = true;
      }
    } catch (error) {
      console.error('âŒ Biometric check error:', error);
    }

    // Initialize push notifications (placeholder)
    try {
      // await initializePushNotifications();
      console.log('ğŸ”” Push notifications initialized');
    } catch (error) {
      console.error('âŒ Push notification initialization failed:', error);
    }

    // Set up error tracking (placeholder)
    try {
      // await initializeErrorTracking();
      console.log('ğŸ“Š Error tracking initialized');
    } catch (error) {
      console.error('âŒ Error tracking initialization failed:', error);
    }

    // Set up analytics (placeholder)
    try {
      // await initializeAnalytics();
      console.log('ğŸ“ˆ Analytics initialized');
    } catch (error) {
      console.error('âŒ Analytics initialization failed:', error);
    }

    // Health check with backend
    try {
      await apiService.healthCheck();
      console.log('âœ… Backend health check passed');
    } catch (error) {
      console.warn('âš ï¸ Backend health check failed:', error);
    }

    const result: AppInitializationResult = {
      isFirstLaunch,
      hasValidSession,
      networkStatus: networkState,
      biometricAvailable,
    };

    console.log('âœ… App initialization completed:', result);
    return result;

  } catch (error) {
    console.error('ğŸ’¥ App initialization failed:', error);
    throw error;
  }
};

export const clearAppData = async (): Promise<void> => {
  try {
    await AsyncStorage.multiRemove([
      STORAGE_KEYS.USER_SESSION,
      STORAGE_KEYS.BIOMETRIC_ENABLED,
      STORAGE_KEYS.PUSH_TOKEN,
    ]);
    console.log('ğŸ§¹ App data cleared');
  } catch (error) {
    console.error('âŒ Failed to clear app data:', error);
  }
};

export const resetOnboarding = async (): Promise<void> => {
  try {
    await AsyncStorage.removeItem(STORAGE_KEYS.ONBOARDING_COMPLETED);
    console.log('ğŸ”„ Onboarding reset');
  } catch (error) {
