import jwt from 'jsonwebtoken';
import jwksClient from 'jwks-client';
import { Request, Response, NextFunction } from 'express';

interface JWTPayload {
  sub: string;
  email?: string;
  name?: string;
  aud?: string;
  iss?: string;
  exp: number;
  iat: number;
  [key: string]: any;
}

interface AuthenticatedRequest extends Request {
  jwtUser?: JWTPayload;
}

class JWTAuthProvider {
  private client: jwksClient.JwksClient | null = null;
  private jwksUri: string | null = null;
  private audience: string | null = null;

  constructor() {
    this.initialize();
  }

  private initialize() {
    this.jwksUri = process.env.JWKS_URI || null;
    this.audience = process.env.JWT_AUDIENCE || null;

    if (this.jwksUri) {
      this.client = jwksClient({
        jwksUri: this.jwksUri,
        requestHeaders: {}, // Optional headers
        timeout: 30000, // Defaults to 30s
        cache: true, // Cache JWKS keys
        cacheMaxEntries: 5,
        cacheMaxAge: 600000, // 10 minutes
        rateLimit: true,
        jwksRequestsPerMinute: 5,
      });
      console.log(`ğŸ” JWT Auth Provider initialized with JWKS URI: ${this.jwksUri}`);
    } else {
      console.log('âš ï¸  JWT Auth Provider not configured - set JWKS_URI environment variable');
    }
  }

  private async getSigningKey(kid: string): Promise<string> {
    if (!this.client) {
      throw new Error('JWKS client not initialized');
    }

    return new Promise((resolve, reject) => {
      this.client!.getSigningKey(kid, (err, key) => {
        if (err) {
          reject(err);
        } else {
          resolve(key.getPublicKey());
        }
      });
    });
  }

  async verifyToken(token: string): Promise<JWTPayload> {
    if (!this.client || !this.jwksUri) {
      throw new Error('JWT Auth Provider not configured');
    }

    // Decode the token header to get the key ID
    const decoded = jwt.decode(token, { complete: true });
    if (!decoded || !decoded.header || !decoded.header.kid) {
      throw new Error('Invalid token: missing key ID');
    }

    // Get the signing key
    const signingKey = await this.getSigningKey(decoded.header.kid);

    // Verify the token
    const payload = jwt.verify(token, signingKey, {
      audience: this.audience || undefined,
      algorithms: ['RS256', 'HS256'],
    }) as JWTPayload;

    return payload;
  }

  // Middleware function
  authenticate() {
    return async (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
      try {
        const authHeader = req.headers.authorization;
        if (!authHeader || !authHeader.startsWith('Bearer ')) {
          return res.status(401).json({
            success: false,
            message: 'Missing or invalid authorization header'
          });
        }

        const token = authHeader.substring(7); // Remove 'Bearer ' prefix
        const payload = await this.verifyToken(token);

        req.jwtUser = payload;
        next();
      } catch (error: any) {
        console.error('JWT verification failed:', error.message);
        return res.status(401).json({
          success: false,
          message: 'Invalid or expired token'
        });
      }
    };
  }

  // Optional middleware that doesn't fail if no token provided
  authenticateOptional() {
    return async (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
      try {
        const authHeader = req.headers.authorization;
        if (authHeader && authHeader.startsWith('Bearer ')) {
          const token = authHeader.substring(7);
          const payload = await this.verifyToken(token);
          req.jwtUser = payload;
        }
        next();
      } catch (error: any) {
        console.error('Optional JWT verification failed:', error.message);
        // Don't fail, just continue without user
        next();
      }
    };
  }
}

export const jwtAuthProvider = new JWTAuthProvider();
export type { AuthenticatedRequest, JWTPayload };

