import express from 'express';
import { jwtAuthProvider, AuthenticatedRequest, JWTPayload } from '../middleware/jwt-auth';
import { db } from '../db';
import { users } from '../../shared/schema';
import { eq } from 'drizzle-orm';

const router = express.Router();

// JWT token verification endpoint
router.post('/verify-jwt', jwtAuthProvider.authenticate(), async (req: AuthenticatedRequest, res) => {
  try {
    const jwtPayload = req.jwtUser!;

    // Try to find existing user by email or external ID
    let user = null;
    if (jwtPayload.email) {
      const [existingUser] = await db
        .select()
        .from(users)
        .where(eq(users.email, jwtPayload.email))
        .limit(1);
      user = existingUser;
    }

    // Create user if doesn't exist (auto-registration)
    if (!user && jwtPayload.email) {
      const newUserData = {
        email: jwtPayload.email,
        fullName: jwtPayload.name || jwtPayload.email.split('@')[0],
        role: 'CONSUMER' as any, // Default role
        isVerified: true, // JWT users are pre-verified
        emailVerified: true,
        password: null, // No password for JWT users
      };

      const [newUser] = await db
        .insert(users)
        .values(newUserData)
        .returning();
      user = newUser;
    }

    if (!user) {
      return res.status(400).json({
        success: false,
        message: 'Unable to create or find user account'
      });
    }

    // Create session
    (req.session as any).userId = user.id;
    (req.session as any).user = {
      id: user.id,
      email: user.email,
      fullName: user.fullName,
      role: user.role
    };
    (req.session as any).jwtProvider = true;

    res.json({
      success: true,
      user: {
        id: user.id,
        email: user.email,
        fullName: user.fullName,
        role: user.role
      },
      jwtPayload: {
        sub: jwtPayload.sub,
        iss: jwtPayload.iss,
        aud: jwtPayload.aud,
        exp: jwtPayload.exp
      }
    });
  } catch (error: any) {
    console.error('JWT auth error:', error);
    res.status(500).json({
      success: false,
      message: 'JWT authentication failed'
    });
  }
});

// Get JWT provider info
router.get('/jwt-config', (req, res) => {
  res.json({
    success: true,
    config: {
      jwksUri: process.env.JWKS_URI || null,
      audience: process.env.JWT_AUDIENCE || null,
      configured: !!(process.env.JWKS_URI)
    }
  });
});

// Protected route example using JWT
router.get('/jwt-protected', jwtAuthProvider.authenticate(), (req: AuthenticatedRequest, res) => {
  res.json({
    success: true,
    message: 'Access granted via JWT',
    user: req.jwtUser
  });
});

export default router;
